(3132, 26, NULL, 200, NULL, NULL, 0, 0, NULL, 'كَذٰلِكَ سَلَكْنَاهُ ف۪ي قُلُوبِ الْمُجْرِم۪ينَۜ', 0),
(3133, 26, NULL, 201, NULL, NULL, 0, 0, NULL, 'لَا يُؤْمِنُونَ بِه۪ حَتّٰى يَرَوُا الْعَذَابَ الْاَل۪يمَۙ', 0),
(3134, 26, NULL, 202, NULL, NULL, 0, 0, NULL, 'فَيَأْتِيَهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَۙ', 0),
(3135, 26, NULL, 203, NULL, NULL, 0, 0, NULL, 'فَيَقُولُوا هَلْ نَحْنُ مُنْظَرُونَۜ', 0),
(3136, 26, NULL, 204, NULL, NULL, 0, 0, NULL, 'اَفَبِعَذَابِنَا يَسْتَعْجِلُونَ', 0),
(3137, 26, NULL, 205, NULL, NULL, 0, 0, NULL, 'اَفَرَاَيْتَ اِنْ مَتَّعْنَاهُمْ سِن۪ينَۙ', 0),
(3138, 26, NULL, 206, NULL, NULL, 0, 0, NULL, 'ثُمَّ جَٓاءَهُمْ مَا كَانُوا يُوعَدُونَۙ', 0),
(3139, 26, NULL, 207, 375, 15, 3, 19, NULL, 'مَٓا اَغْنٰى عَنْهُمْ مَا كَانُوا يُمَتَّعُونَۜ', 0),
(3140, 26, NULL, 208, NULL, NULL, 0, 0, NULL, 'وَمَٓا اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا لَهَا مُنْذِرُونَۗۛ', 0),
(3141, 26, NULL, 209, NULL, NULL, 0, 0, NULL, 'ذِكْرٰى۠ۛ وَمَا كُنَّا ظَالِم۪ينَ', 0),
(3142, 26, NULL, 210, NULL, NULL, 0, 0, NULL, 'وَمَا تَنَزَّلَتْ بِهِ الشَّيَاط۪ينُ', 0),
(3143, 26, NULL, 211, NULL, NULL, 0, 0, NULL, 'وَمَا يَنْبَغ۪ي لَهُمْ وَمَا يَسْتَط۪يعُونَۜ', 0),
(3144, 26, NULL, 212, NULL, NULL, 0, 0, NULL, 'اِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُولُونَۜ', 0),
(3145, 26, NULL, 213, NULL, NULL, 0, 0, NULL, 'فَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتَكُونَ مِنَ الْمُعَذَّب۪ينَۚ', 0),
(3146, 26, NULL, 214, NULL, NULL, 0, 0, NULL, 'وَاَنْذِرْ عَش۪يرَتَكَ الْاَقْرَب۪ينَۙ', 0),
(3147, 26, NULL, 215, NULL, NULL, 0, 0, NULL, 'وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِن۪ينَۚ', 0),
(3148, 26, NULL, 216, NULL, NULL, 0, 0, NULL, 'فَاِنْ عَصَوْكَ فَقُلْ اِنّ۪ي بَر۪ٓيءٌ مِمَّا تَعْمَلُونَۚ', 0),
(3149, 26, NULL, 217, NULL, NULL, 0, 0, NULL, 'وَتَوَكَّلْ عَلَى الْعَز۪يزِ الرَّح۪يمِۙ', 0),
(3150, 26, NULL, 218, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ي يَرٰيكَ ح۪ينَ تَقُومُۙ', 0),
(3151, 26, NULL, 219, NULL, NULL, 0, 0, NULL, 'وَتَقَلُّبَكَ فِي السَّاجِد۪ينَ', 0),
(3152, 26, NULL, 220, NULL, NULL, 0, 0, NULL, 'اِنَّهُ هُوَ السَّم۪يعُ الْعَل۪يمُ', 0),
(3153, 26, NULL, 221, NULL, NULL, 0, 0, NULL, 'هَلْ اُنَبِّئُكُمْ عَلٰى مَنْ تَنَزَّلُ الشَّيَاط۪ينُۜ', 0),
(3154, 26, NULL, 222, NULL, NULL, 0, 0, NULL, 'تَنَزَّلُ عَلٰى كُلِّ اَفَّاكٍ اَث۪يمٍۙ', 0),
(3155, 26, NULL, 223, NULL, NULL, 0, 0, NULL, 'يُلْقُونَ السَّمْعَ وَاَكْثَرُهُمْ كَاذِبُونَۜ', 0),
(3156, 26, NULL, 224, NULL, NULL, 0, 0, NULL, 'وَالشُّعَرَٓاءُ يَتَّبِعُهُمُ الْغَاوُ۫نَۜ', 0),
(3157, 26, NULL, 225, NULL, NULL, 0, 0, NULL, 'اَلَمْ تَرَ اَنَّهُمْ ف۪ي كُلِّ وَادٍ يَه۪يمُونَۙ', 0),
(3158, 26, NULL, 226, NULL, NULL, 0, 0, NULL, 'وَاَنَّهُمْ يَقُولُونَ مَا لَا يَفْعَلُونَۙ', 0),
(3159, 26, NULL, 227, NULL, NULL, 0, 0, NULL, 'اِلَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَذَكَرُوا اللّٰهَ كَث۪يرًا وَانْتَصَرُوا مِنْ بَعْدِ مَا ظُلِمُواۜ وَسَيَعْلَمُ الَّذ۪ينَ ظَلَمُٓوا اَيَّ مُنْقَلَبٍ يَنْقَلِبُونَ', 0),
(3160, 27, 27, 1, 376, 16, 4, 19, NULL, 'طٰسٓ۠ تِلْكَ اٰيَاتُ الْقُرْاٰنِ وَكِتَابٍ مُب۪ينٍۙ', 0),
(3161, 27, NULL, 2, NULL, NULL, 0, 0, NULL, 'هُدًى وَبُشْرٰى لِلْمُؤْمِن۪ينَۙ', 0),
(3162, 27, NULL, 3, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ينَ يُق۪يمُونَ الصَّلٰوةَ وَيُؤْتُونَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوقِنُونَ', 0),
(3163, 27, NULL, 4, NULL, NULL, 0, 0, NULL, 'اِنَّ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ زَيَّنَّا لَهُمْ اَعْمَالَهُمْ فَهُمْ يَعْمَهُونَۜ', 0),
(3164, 27, NULL, 5, NULL, NULL, 0, 0, NULL, 'اُو۬لٰٓئِكَ الَّذ۪ينَ لَهُمْ سُٓوءُ الْعَذَابِ وَهُمْ فِي الْاٰخِرَةِ هُمُ الْاَخْسَرُونَ', 0),
(3165, 27, NULL, 6, NULL, NULL, 0, 0, NULL, 'وَاِنَّكَ لَتُلَقَّى الْقُرْاٰنَ مِنْ لَدُنْ حَك۪يمٍ عَل۪يمٍ', 0),
(3166, 27, NULL, 7, NULL, NULL, 0, 0, NULL, 'اِذْ قَالَ مُوسٰى لِاَهْلِه۪ٓ اِنّ۪ٓي اٰنَسْتُ نَارًاۜ سَاٰت۪يكُمْ مِنْهَا بِخَبَرٍ اَوْ اٰت۪يكُمْ بِشِهَابٍ قَبَسٍ لَعَلَّكُمْ تَصْطَلُونَ', 0),
(3167, 27, NULL, 8, NULL, NULL, 0, 0, NULL, 'فَلَمَّا جَٓاءَهَا نُودِيَ اَنْ بُورِكَ مَنْ فِي النَّارِ وَمَنْ حَوْلَهَاۜ وَسُبْحَانَ اللّٰهِ رَبِّ الْعَالَم۪ينَ', 0),
(3168, 27, NULL, 9, NULL, NULL, 0, 0, NULL, 'يَا مُوسٰٓى اِنَّهُٓ اَنَا۬ اللّٰهُ الْعَز۪يزُ الْحَك۪يمُۙ', 0),
(3169, 27, NULL, 10, NULL, NULL, 0, 0, NULL, 'وَاَلْقِ عَصَاكَۜ فَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَٓانٌّ وَلّٰى مُدْبِرًا وَلَمْ يُعَقِّبْۜ يَا مُوسٰى لَا تَخَفْ اِنّ۪ي لَا يَخَافُ لَدَيَّ الْمُرْسَلُونَۗ', 0),
(3170, 27, NULL, 11, NULL, NULL, 0, 0, NULL, 'اِلَّا مَنْ ظَلَمَ ثُمَّ بَدَّلَ حُسْنًا بَعْدَ سُٓوءٍ فَاِنّ۪ي غَفُورٌ رَح۪يمٌ', 0),
(3171, 27, NULL, 12, NULL, NULL, 0, 0, NULL, 'وَاَدْخِلْ يَدَكَ ف۪ي جَيْبِكَ تَخْرُجْ بَيْضَٓاءَ مِنْ غَيْرِ سُٓوءٍ ف۪ي تِسْعِ اٰيَاتٍ اِلٰى فِرْعَوْنَ وَقَوْمِه۪ۜ اِنَّهُمْ كَانُوا قَوْمًا فَاسِق۪ينَ', 0),
(3172, 27, NULL, 13, NULL, NULL, 0, 0, NULL, 'فَلَمَّا جَٓاءَتْهُمْ اٰيَاتُنَا مُبْصِرَةً قَالُوا هٰذَا سِحْرٌ مُب۪ينٌۚ', 0),
(3173, 27, NULL, 14, 377, 17, 4, 19, NULL, 'وَجَحَدُوا بِهَا وَاسْتَيْقَنَتْهَٓا اَنْفُسُهُمْ ظُلْمًا وَعُلُوًّاۜ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِد۪ينَ۟', 0),
(3174, 27, NULL, 15, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ اٰتَيْنَا دَاوُ۫دَ وَسُلَيْمٰنَ عِلْمًاۚ وَقَالَا الْحَمْدُ لِلّٰهِ الَّذ۪ي فَضَّلَنَا عَلٰى كَث۪يرٍ مِنْ عِبَادِهِ الْمُؤْمِن۪ينَ', 0),
(3175, 27, NULL, 16, NULL, NULL, 0, 0, NULL, 'وَوَرِثَ سُلَيْمٰنُ دَاوُ۫دَ وَقَالَ يَٓا اَيُّهَا النَّاسُ عُلِّمْنَا مَنْطِقَ الطَّيْرِ وَاُو۫ت۪ينَا مِنْ كُلِّ شَيْءٍۜ اِنَّ هٰذَا لَهُوَ الْفَضْلُ الْمُب۪ينُ', 0),
(3176, 27, NULL, 17, NULL, NULL, 0, 0, NULL, 'وَحُشِرَ لِسُلَيْمٰنَ جُنُودُهُ مِنَ الْجِنِّ وَالْاِنْسِ وَالطَّيْرِ فَهُمْ يُوزَعُونَ', 0),
(3177, 27, NULL, 18, NULL, NULL, 0, 0, NULL, 'حَتّٰٓى اِذَٓا اَتَوْا عَلٰى وَادِ النَّمْلِۙ قَالَتْ نَمْلَةٌ يَٓا اَيُّهَا النَّمْلُ ادْخُلُوا مَسَاكِنَكُمْۚ لَا يَحْطِمَنَّكُمْ سُلَيْمٰنُ وَجُنُودُهُۙ وَهُمْ لَا يَشْعُرُونَ', 0),
(3178, 27, NULL, 19, NULL, NULL, 0, 0, NULL, 'فَتَبَسَّمَ ضَاحِكًا مِنْ قَوْلِهَا وَقَالَ رَبِّ اَوْزِعْن۪ٓي اَنْ اَشْكُرَ نِعْمَتَكَ الَّت۪ٓي اَنْعَمْتَ عَلَيَّ وَعَلٰى وَالِدَيَّ وَاَنْ اَعْمَلَ صَالِحًا تَرْضٰيهُ وَاَدْخِلْن۪ي بِرَحْمَتِكَ ف۪ي عِبَادِكَ الصَّالِح۪ينَ', 0),
(3179, 27, NULL, 20, NULL, NULL, 0, 0, NULL, 'وَتَفَقَّدَ الطَّيْرَ فَقَالَ مَا لِيَ لَٓا اَرَى الْهُدْهُدَۘ اَمْ كَانَ مِنَ الْغَٓائِب۪ينَ', 0),
(3180, 27, NULL, 21, NULL, NULL, 0, 0, NULL, 'لَاُعَذِّبَنَّهُ عَذَابًا شَد۪يدًا اَوْ لَا۬اَذْبَحَنَّهُٓ اَوْ لَيَأْتِيَنّ۪ي بِسُلْطَانٍ مُب۪ينٍ', 0),
(3181, 27, NULL, 22, NULL, NULL, 0, 0, NULL, 'فَمَكَثَ غَيْرَ بَع۪يدٍ فَقَالَ اَحَطْتُ بِمَا لَمْ تُحِطْ بِه۪ وَجِئْتُكَ مِنْ سَبَاٍ بِنَبَاٍ يَق۪ينٍ', 0),
(3182, 27, NULL, 23, 378, 18, 4, 19, NULL, 'اِنّ۪ي وَجَدْتُ امْرَاَةً تَمْلِكُهُمْ وَاُو۫تِيَتْ مِنْ كُلِّ شَيْءٍ وَلَهَا عَرْشٌ عَظ۪يمٌ', 0),
(3183, 27, NULL, 24, NULL, NULL, 0, 0, NULL, 'وَجَدْتُهَا وَقَوْمَهَا يَسْجُدُونَ لِلشَّمْسِ مِنْ دُونِ اللّٰهِ وَزَيَّنَ لَهُمُ الشَّيْطَانُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّب۪يلِ فَهُمْ لَا يَهْتَدُونَۙ', 0),
(3184, 27, NULL, 25, NULL, NULL, 0, 0, NULL, 'اَلَّا يَسْجُدُوا لِلّٰهِ الَّذ۪ي يُخْرِجُ الْخَبْءَ فِي السَّمٰوَاتِ وَالْاَرْضِ وَيَعْلَمُ مَا تُخْفُونَ وَمَا تُعْلِنُونَ', 1),
(3185, 27, NULL, 26, NULL, NULL, 0, 0, NULL, 'اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَ رَبُّ الْعَرْشِ الْعَظ۪يمِ', 0),
(3186, 27, NULL, 27, NULL, NULL, 0, 0, NULL, 'قَالَ سَنَنْظُرُ اَصَدَقْتَ اَمْ كُنْتَ مِنَ الْكَاذِب۪ينَ', 0),
(3187, 27, NULL, 28, NULL, NULL, 0, 0, NULL, 'اِذْهَبْ بِكِتَاب۪ي هٰذَا فَاَلْقِهْ اِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانْظُرْ مَاذَا يَرْجِعُونَ', 0),
(3188, 27, NULL, 29, NULL, NULL, 0, 0, NULL, 'قَالَتْ يَٓا اَيُّهَا الْمَلَؤُ۬ا اِنّ۪ٓي اُلْقِيَ اِلَيَّ كِتَابٌ كَر۪يمٌ', 0),
(3189, 27, NULL, 30, NULL, NULL, 0, 0, NULL, 'اِنَّهُ مِنْ سُلَيْمٰنَ وَاِنَّهُ بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّح۪يمِۙ', 0),
(3190, 27, NULL, 31, NULL, NULL, 0, 0, NULL, 'اَلَّا تَعْلُوا عَلَيَّ وَأْتُون۪ي مُسْلِم۪ينَ۟', 0),
(3191, 27, NULL, 32, NULL, NULL, 0, 0, NULL, 'قَالَتْ يَٓا اَيُّهَا الْمَلَؤُ۬ا اَفْتُون۪ي ف۪ٓي اَمْر۪يۚ مَا كُنْتُ قَاطِعَةً اَمْرًا حَتّٰى تَشْهَدُونِ', 0);
INSERT INTO `fkl_verse` (`id`, `sura_id`, `new_sura`, `verse_no`, `page`, `hizb_page`, `hizb`, `juz`, `new_juz`, `verse`, `sajdah`) VALUES
(3192, 27, NULL, 33, NULL, NULL, 0, 0, NULL, 'قَالُوا نَحْنُ اُو۬لُوا قُوَّةٍ وَاُو۬لُوا بَأْسٍ شَد۪يدٍ وَالْاَمْرُ اِلَيْكِ فَانْظُر۪ي مَاذَا تَأْمُر۪ينَ', 0),
(3193, 27, NULL, 34, NULL, NULL, 0, 0, NULL, 'قَالَتْ اِنَّ الْمُلُوكَ اِذَا دَخَلُوا قَرْيَةً اَفْسَدُوهَا وَجَعَلُٓوا اَعِزَّةَ اَهْلِهَٓا اَذِلَّةًۚ وَكَذٰلِكَ يَفْعَلُونَ', 0),
(3194, 27, NULL, 35, NULL, NULL, 0, 0, NULL, 'وَاِنّ۪ي مُرْسِلَةٌ اِلَيْهِمْ بِهَدِيَّةٍ فَنَاظِرَةٌ بِمَ يَرْجِعُ الْمُرْسَلُونَ', 0),
(3195, 27, NULL, 36, 379, 19, 4, 19, NULL, 'فَلَمَّا جَٓاءَ سُلَيْمٰنَ قَالَ اَتُمِدُّونَنِ بِمَالٍۘ فَمَٓا اٰتٰينِ‌يَ اللّٰهُ خَيْرٌ مِمَّٓا اٰتٰيكُمْۚ بَلْ اَنْتُمْ بِهَدِيَّتِكُمْ تَفْرَحُونَ', 0),
(3196, 27, NULL, 37, NULL, NULL, 0, 0, NULL, 'اِرْجِعْ اِلَيْهِمْ فَلَنَأْتِيَنَّهُمْ بِجُنُودٍ لَا قِبَلَ لَهُمْ بِهَا وَلَنُخْرِجَنَّهُمْ مِنْهَٓا اَذِلَّةً وَهُمْ صَاغِرُونَ', 0),
(3197, 27, NULL, 38, NULL, NULL, 0, 0, NULL, 'قَالَ يَٓا اَيُّهَا الْمَلَؤُ۬ا اَيُّكُمْ يَأْت۪ين۪ي بِعَرْشِهَا قَبْلَ اَنْ يَأْتُون۪ي مُسْلِم۪ينَ', 0),
(3198, 27, NULL, 39, NULL, NULL, 0, 0, NULL, 'قَالَ عِفْر۪يتٌ مِنَ الْجِنِّ اَنَا۬ اٰت۪يكَ بِه۪ قَبْلَ اَنْ تَقُومَ مِنْ مَقَامِكَۚ وَاِنّ۪ي عَلَيْهِ لَقَوِيٌّ اَم۪ينٌ', 0),
(3199, 27, NULL, 40, NULL, NULL, 0, 0, NULL, 'قَالَ الَّذ۪ي عِنْدَهُ عِلْمٌ مِنَ الْكِتَابِ اَنَا۬ اٰت۪يكَ بِه۪ قَبْلَ اَنْ يَرْتَدَّ اِلَيْكَ طَرْفُكَۜ فَلَمَّا رَاٰهُ مُسْتَقِرًّا عِنْدَهُ قَالَ هٰذَا مِنْ فَضْلِ رَبّ۪ي۠ لِيَبْلُوَن۪ٓي ءَاَشْكُرُ اَمْ اَكْفُرُۜ وَمَنْ شَكَرَ فَاِنَّمَا يَشْكُرُ لِنَفْسِه۪ۚ وَمَنْ كَفَرَ فَاِنَّ رَبّ۪ي غَنِيٌّ كَر۪يمٌ', 0),
(3200, 27, NULL, 41, NULL, NULL, 0, 0, NULL, 'قَالَ نَكِّرُوا لَهَا عَرْشَهَا نَنْظُرْ اَتَهْتَد۪ٓي اَمْ تَكُونُ مِنَ الَّذ۪ينَ لَا يَهْتَدُونَ', 0),
(3201, 27, NULL, 42, NULL, NULL, 0, 0, NULL, 'فَلَمَّا جَٓاءَتْ ق۪يلَ اَهٰكَذَا عَرْشُكِۜ قَالَتْ كَاَنَّهُ هُوَۚ وَاُو۫ت۪ينَا الْعِلْمَ مِنْ قَبْلِهَا وَكُنَّا مُسْلِم۪ينَ', 0),
(3202, 27, NULL, 43, NULL, NULL, 0, 0, NULL, 'وَصَدَّهَا مَا كَانَتْ تَعْبُدُ مِنْ دُونِ اللّٰهِۜ اِنَّهَا كَانَتْ مِنْ قَوْمٍ كَافِر۪ينَ', 0),
(3203, 27, NULL, 44, NULL, NULL, 0, 0, NULL, 'ق۪يلَ لَهَا ادْخُلِي الصَّرْحَۚ فَلَمَّا رَاَتْهُ حَسِبَتْهُ لُجَّةً وَكَشَفَتْ عَنْ سَاقَيْهَاۜ قَالَ اِنَّهُ صَرْحٌ مُمَرَّدٌ مِنْ قَوَار۪يرَۜ قَالَتْ رَبِّ اِنّ۪ي ظَلَمْتُ نَفْس۪ي وَاَسْلَمْتُ مَعَ سُلَيْمٰنَ لِلّٰهِ رَبِّ الْعَالَم۪ينَ۟', 0),
(3204, 27, NULL, 45, 380, 20, 4, 19, NULL, 'وَلَقَدْ اَرْسَلْنَٓا اِلٰى ثَمُودَ اَخَاهُمْ صَالِحًا اَنِ اعْبُدُوا اللّٰهَ فَاِذَا هُمْ فَر۪يقَانِ يَخْتَصِمُونَ', 0),
(3205, 27, NULL, 46, NULL, NULL, 0, 0, NULL, 'قَالَ يَا قَوْمِ لِمَ تَسْتَعْجِلُونَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِۚ لَوْلَا تَسْتَغْفِرُونَ اللّٰهَ لَعَلَّكُمْ تُرْحَمُونَ', 0),
(3206, 27, NULL, 47, NULL, NULL, 0, 0, NULL, 'قَالُوا اطَّيَّرْنَا بِكَ وَبِمَنْ مَعَكَۜ قَالَ طَٓائِرُكُمْ عِنْدَ اللّٰهِ بَلْ اَنْتُمْ قَوْمٌ تُفْتَنُونَ', 0),
(3207, 27, NULL, 48, NULL, NULL, 0, 0, NULL, 'وَكَانَ فِي الْمَد۪ينَةِ تِسْعَةُ رَهْطٍ يُفْسِدُونَ فِي الْاَرْضِ وَلَا يُصْلِحُونَ', 0),
(3208, 27, NULL, 49, NULL, NULL, 0, 0, NULL, 'قَالُوا تَقَاسَمُوا بِاللّٰهِ لَنُبَيِّتَنَّهُ وَاَهْلَهُ ثُمَّ لَنَقُولَنَّ لِوَلِيِّه۪ مَا شَهِدْنَا مَهْلِكَ اَهْلِه۪ وَاِنَّا لَصَادِقُونَ', 0),
(3209, 27, NULL, 50, NULL, NULL, 0, 0, NULL, 'وَمَكَرُوا مَكْرًا وَمَكَرْنَا مَكْرًا وَهُمْ لَا يَشْعُرُونَ', 0),
(3210, 27, NULL, 51, NULL, NULL, 0, 0, NULL, 'فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ مَكْرِهِمْۙ اَنَّا دَمَّرْنَاهُمْ وَقَوْمَهُمْ اَجْمَع۪ينَ', 0),
(3211, 27, NULL, 52, NULL, NULL, 0, 0, NULL, 'فَتِلْكَ بُيُوتُهُمْ خَاوِيَةً بِمَا ظَلَمُواۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَعْلَمُونَ', 0),
(3212, 27, NULL, 53, NULL, NULL, 0, 0, NULL, 'وَاَنْجَيْنَا الَّذ۪ينَ اٰمَنُوا وَكَانُوا يَتَّقُونَ', 0),
(3213, 27, NULL, 54, NULL, NULL, 0, 0, NULL, 'وَلُوطًا اِذْ قَالَ لِقَوْمِه۪ٓ اَتَأْتُونَ الْفَاحِشَةَ وَاَنْتُمْ تُبْصِرُونَ', 0),
(3214, 27, NULL, 55, NULL, NULL, 0, 0, NULL, 'اَئِنَّكُمْ لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِنْ دُونِ النِّسَٓاءِۜ بَلْ اَنْتُمْ قَوْمٌ تَجْهَلُونَ', 0),
(3215, 27, NULL, 56, 381, 1, 1, 20, 20, 'فَمَا كَانَ جَوَابَ قَوْمِه۪ٓ اِلَّٓا اَنْ قَالُٓوا اَخْرِجُٓوا اٰلَ لُوطٍ مِنْ قَرْيَتِكُمْۚ اِنَّهُمْ اُنَاسٌ يَتَطَهَّرُونَ', 0),
(3216, 27, NULL, 57, NULL, NULL, 0, 0, NULL, 'فَاَنْجَيْنَاهُ وَاَهْلَهُٓ اِلَّا امْرَاَتَهُۘ قَدَّرْنَاهَا مِنَ الْغَابِر۪ينَ', 0),
(3217, 27, NULL, 58, NULL, NULL, 0, 0, NULL, 'وَاَمْطَرْنَا عَلَيْهِمْ مَطَرًاۚ فَسَٓاءَ مَطَرُ الْمُنْذَر۪ينَ۟', 0),
(3218, 27, NULL, 59, NULL, NULL, 0, 0, NULL, 'قُلِ الْحَمْدُ لِلّٰهِ وَسَلَامٌ عَلٰى عِبَادِهِ الَّذ۪ينَ اصْطَفٰىۜ آٰللّٰهُ خَيْرٌ اَمَّا يُشْرِكُونَۜ', 0),
(3219, 27, NULL, 60, NULL, NULL, 0, 0, NULL, 'اَمَّنْ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَاَنْزَلَ لَكُمْ مِنَ السَّمَٓاءِ مَٓاءًۚ فَاَنْبَتْنَا بِه۪ حَدَٓائِقَ ذَاتَ بَهْجَةٍۚ مَا كَانَ لَكُمْ اَنْ تُنْبِتُوا شَجَرَهَاۜ ءَاِلٰهٌ مَعَ اللّٰهِۜ بَلْ هُمْ قَوْمٌ يَعْدِلُونَۜ', 0),
(3220, 27, NULL, 61, NULL, NULL, 0, 0, NULL, 'اَمَّنْ جَعَلَ الْاَرْضَ قَرَارًا وَجَعَلَ خِلَالَهَٓا اَنْهَارًا وَجَعَلَ لَهَا رَوَاسِيَ وَجَعَلَ بَيْنَ الْبَحْرَيْنِ حَاجِزًاۜ ءَاِلٰهٌ مَعَ اللّٰهِۜ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَۜ', 0),
(3221, 27, NULL, 62, NULL, NULL, 0, 0, NULL, 'اَمَّنْ يُج۪يبُ الْمُضْطَرَّ اِذَا دَعَاهُ وَيَكْشِفُ السُّٓوءَ وَيَجْعَلُكُمْ خُلَفَٓاءَ الْاَرْضِۜ ءَاِلٰهٌ مَعَ اللّٰهِۜ قَل۪يلًا مَا تَذَكَّرُونَۜ', 0),
(3222, 27, NULL, 63, NULL, NULL, 0, 0, NULL, 'اَمَّنْ يَهْد۪يكُمْ ف۪ي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ وَمَنْ يُرْسِلُ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِه۪ۜ ءَاِلٰهٌ مَعَ اللّٰهِۜ تَعَالَى اللّٰهُ عَمَّا يُشْرِكُونَۜ', 0),
(3223, 27, NULL, 64, 382, 2, 1, 20, NULL, 'اَمَّنْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُ وَمَنْ يَرْزُقُكُمْ مِنَ السَّمَٓاءِ وَالْاَرْضِۜ ءَاِلٰهٌ مَعَ اللّٰهِۜ قُلْ هَاتُوا بُرْهَانَكُمْ اِنْ كُنْتُمْ صَادِق۪ينَ', 0),
(3224, 27, NULL, 65, NULL, NULL, 0, 0, NULL, 'قُلْ لَا يَعْلَمُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِ الْغَيْبَ اِلَّا اللّٰهُۜ وَمَا يَشْعُرُونَ اَيَّانَ يُبْعَثُونَ', 0),
(3225, 27, NULL, 66, NULL, NULL, 0, 0, NULL, 'بَلِ ادَّارَكَ عِلْمُهُمْ فِي الْاٰخِرَةِ۠ بَلْ هُمْ ف۪ي شَكٍّ مِنْهَا۠ بَلْ هُمْ مِنْهَا عَمُونَ۟', 0),
(3226, 27, NULL, 67, NULL, NULL, 0, 0, NULL, 'وَقَالَ الَّذ۪ينَ كَفَرُٓوا ءَاِذَا كُنَّا تُرَابًا وَاٰبَٓاؤُ۬نَٓا اَئِنَّا لَمُخْرَجُونَ', 0),
(3227, 27, NULL, 68, NULL, NULL, 0, 0, NULL, 'لَقَدْ وُعِدْنَا هٰذَا نَحْنُ وَاٰبَٓاؤُ۬نَا مِنْ قَبْلُۙ اِنْ هٰذَٓا اِلَّٓا اَسَاط۪يرُ الْاَوَّل۪ينَ', 0),
(3228, 27, NULL, 69, NULL, NULL, 0, 0, NULL, 'قُلْ س۪يرُوا فِي الْاَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِم۪ينَ', 0),
(3229, 27, NULL, 70, NULL, NULL, 0, 0, NULL, 'وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُنْ ف۪ي ضَيْقٍ مِمَّا يَمْكُرُونَ', 0),
(3230, 27, NULL, 71, NULL, NULL, 0, 0, NULL, 'وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ', 0),
(3231, 27, NULL, 72, NULL, NULL, 0, 0, NULL, 'قُلْ عَسٰٓى اَنْ يَكُونَ رَدِفَ لَكُمْ بَعْضُ الَّذ۪ي تَسْتَعْجِلُونَ', 0),
(3232, 27, NULL, 73, NULL, NULL, 0, 0, NULL, 'وَاِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُونَ', 0),
(3233, 27, NULL, 74, NULL, NULL, 0, 0, NULL, 'وَاِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ', 0),
(3234, 27, NULL, 75, NULL, NULL, 0, 0, NULL, 'وَمَا مِنْ غَٓائِبَةٍ فِي السَّمَٓاءِ وَالْاَرْضِ اِلَّا ف۪ي كِتَابٍ مُب۪ينٍ', 0),
(3235, 27, NULL, 76, NULL, NULL, 0, 0, NULL, 'اِنَّ هٰذَا الْقُرْاٰنَ يَقُصُّ عَلٰى بَن۪ٓي اِسْرَٓاء۪يلَ اَكْثَرَ الَّذ۪ي هُمْ ف۪يهِ يَخْتَلِفُونَ', 0),
(3236, 27, NULL, 77, 383, 3, 1, 20, NULL, 'وَاِنَّهُ لَهُدًى وَرَحْمَةٌ لِلْمُؤْمِن۪ينَ', 0),
(3237, 27, NULL, 78, NULL, NULL, 0, 0, NULL, 'اِنَّ رَبَّكَ يَقْض۪ي بَيْنَهُمْ بِحُكْمِه۪ۚ وَهُوَ الْعَز۪يزُ الْعَل۪يمُۚ', 0),
(3238, 27, NULL, 79, NULL, NULL, 0, 0, NULL, 'فَتَوَكَّلْ عَلَى اللّٰهِۜ اِنَّكَ عَلَى الْحَقِّ الْمُب۪ينِ', 0),
(3239, 27, NULL, 80, NULL, NULL, 0, 0, NULL, 'اِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَٓاءَ اِذَا وَلَّوْا مُدْبِر۪ينَ', 0),
(3240, 27, NULL, 81, NULL, NULL, 0, 0, NULL, 'وَمَٓا اَنْتَ بِهَادِي الْعُمْيِ عَنْ ضَلَالَتِهِمْۜ اِنْ تُسْمِعُ اِلَّا مَنْ يُؤْمِنُ بِاٰيَاتِنَا فَهُمْ مُسْلِمُونَ', 0),
(3241, 27, NULL, 82, NULL, NULL, 0, 0, NULL, 'وَاِذَا وَقَعَ الْقَوْلُ عَلَيْهِمْ اَخْرَجْنَا لَهُمْ دَٓابَّةً مِنَ الْاَرْضِ تُكَلِّمُهُمْۙ اَنَّ النَّاسَ كَانُوا بِاٰيَاتِنَا لَا يُوقِنُونَ۟', 0),
(3242, 27, NULL, 83, NULL, NULL, 0, 0, NULL, 'وَيَوْمَ نَحْشُرُ مِنْ كُلِّ اُمَّةٍ فَوْجًا مِمَّنْ يُكَذِّبُ بِاٰيَاتِنَا فَهُمْ يُوزَعُونَ', 0),
(3243, 27, NULL, 84, NULL, NULL, 0, 0, NULL, 'حَتّٰٓى اِذَا جَٓاؤُ۫ قَالَ اَكَذَّبْتُمْ بِاٰيَات۪ي وَلَمْ تُح۪يطُوا بِهَا عِلْمًا اَمَّاذَا كُنْتُمْ تَعْمَلُونَ', 0),
(3244, 27, NULL, 85, NULL, NULL, 0, 0, NULL, 'وَوَقَعَ الْقَوْلُ عَلَيْهِمْ بِمَا ظَلَمُوا فَهُمْ لَا يَنْطِقُونَ', 0),
(3245, 27, NULL, 86, NULL, NULL, 0, 0, NULL, 'اَلَمْ يَرَوْا اَنَّا جَعَلْنَا الَّيْلَ لِيَسْكُنُوا ف۪يهِ وَالنَّهَارَ مُبْصِرًاۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 0),
(3246, 27, NULL, 87, NULL, NULL, 0, 0, NULL, 'وَيَوْمَ يُنْفَخُ فِي الصُّورِ فَفَزِعَ مَنْ فِي السَّمٰوَاتِ وَمَنْ فِي الْاَرْضِ اِلَّا مَنْ شَٓاءَ اللّٰهُۜ وَكُلٌّ اَتَوْهُ دَاخِر۪ينَ', 0),
(3247, 27, NULL, 88, NULL, NULL, 0, 0, NULL, 'وَتَرَى الْجِبَالَ تَحْسَبُهَا جَامِدَةً وَهِيَ تَمُرُّ مَرَّ السَّحَابِۜ صُنْعَ اللّٰهِ الَّذ۪ٓي اَتْقَنَ كُلَّ شَيْءٍۜ اِنَّهُ خَب۪يرٌ بِمَا تَفْعَلُونَ', 0),
(3248, 27, NULL, 89, 384, 4, 1, 20, NULL, 'مَنْ جَٓاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِنْهَاۚ وَهُمْ مِنْ فَزَعٍ يَوْمَئِذٍ اٰمِنُونَ', 0),
(3249, 27, NULL, 90, NULL, NULL, 0, 0, NULL, 'وَمَنْ جَٓاءَ بِالسَّيِّئَةِ فَكُبَّتْ وُجُوهُهُمْ فِي النَّارِۜ هَلْ تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُونَ', 0),
(3250, 27, NULL, 91, NULL, NULL, 0, 0, NULL, 'اِنَّمَٓا اُمِرْتُ اَنْ اَعْبُدَ رَبَّ هٰذِهِ الْبَلْدَةِ الَّذ۪ي حَرَّمَهَا وَلَهُ كُلُّ شَيْءٍۘ وَاُمِرْتُ اَنْ اَكُونَ مِنَ الْمُسْلِم۪ينَۙ', 0),
(3251, 27, NULL, 92, NULL, NULL, 0, 0, NULL, 'وَاَنْ اَتْلُوَ۬ا الْقُرْاٰنَۚ فَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَد۪ي لِنَفْسِه۪ۚ وَمَنْ ضَلَّ فَقُلْ اِنَّمَٓا اَنَا۬ مِنَ الْمُنْذِر۪ينَ', 0),
(3252, 27, NULL, 93, NULL, NULL, 0, 0, NULL, 'وَقُلِ الْحَمْدُ لِلّٰهِ سَيُر۪يكُمْ اٰيَاتِه۪ فَتَعْرِفُونَهَاۜ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 0),
(3253, 28, 28, 1, NULL, NULL, 0, 0, NULL, 'طٰسٓمٓۜ', 0),
(3254, 28, NULL, 2, NULL, NULL, 0, 0, NULL, 'تِلْكَ اٰيَاتُ الْكِتَابِ الْمُب۪ينِ', 0),
(3255, 28, NULL, 3, NULL, NULL, 0, 0, NULL, 'نَتْلُوا عَلَيْكَ مِنْ نَبَاِ مُوسٰى وَفِرْعَوْنَ بِالْحَقِّ لِقَوْمٍ يُؤْمِنُونَ', 0),
(3256, 28, NULL, 4, NULL, NULL, 0, 0, NULL, 'اِنَّ فِرْعَوْنَ عَلَا فِي الْاَرْضِ وَجَعَلَ اَهْلَهَا شِيَعًا يَسْتَضْعِفُ طَٓائِفَةً مِنْهُمْ يُذَبِّحُ اَبْنَٓاءَهُمْ وَيَسْتَحْي۪ نِسَٓاءَهُمْۜ اِنَّهُ كَانَ مِنَ الْمُفْسِد۪ينَ', 0),
(3257, 28, NULL, 5, NULL, NULL, 0, 0, NULL, 'وَنُر۪يدُ اَنْ نَمُنَّ عَلَى الَّذ۪ينَ اسْتُضْعِفُوا فِي الْاَرْضِ وَنَجْعَلَهُمْ اَئِمَّةً وَنَجْعَلَهُمُ الْوَارِث۪ينَۙ', 0),
(3258, 28, NULL, 6, 385, 5, 2, 20, NULL, 'وَنُمَكِّنَ لَهُمْ فِي الْاَرْضِ وَنُرِيَ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا مِنْهُمْ مَا كَانُوا يَحْذَرُونَ', 0),
(3259, 28, NULL, 7, NULL, NULL, 0, 0, NULL, 'وَاَوْحَيْنَٓا اِلٰٓى اُمِّ مُوسٰٓى اَنْ اَرْضِع۪يهِۚ فَاِذَا خِفْتِ عَلَيْهِ فَاَلْق۪يهِ فِي الْيَمِّ وَلَا تَخَاف۪ي وَلَا تَحْزَن۪يۚ اِنَّا رَٓادُّوهُ اِلَيْكِ وَجَاعِلُوهُ مِنَ الْمُرْسَل۪ينَ', 0),
(3260, 28, NULL, 8, NULL, NULL, 0, 0, NULL, 'فَالْتَقَطَهُٓ اٰلُ فِرْعَوْنَ لِيَكُونَ لَهُمْ عَدُوًّا وَحَزَنًاۜ اِنَّ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا كَانُوا خَاطِـ۪ٔينَ', 0),
(3261, 28, NULL, 9, NULL, NULL, 0, 0, NULL, 'وَقَالَتِ امْرَاَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ ل۪ي وَلَكَۜ لَا تَقْتُلُوهُۗ عَسٰٓى اَنْ يَنْفَعَنَٓا اَوْ نَتَّخِذَهُ وَلَدًا وَهُمْ لَا يَشْعُرُونَ', 0),
(3262, 28, NULL, 10, NULL, NULL, 0, 0, NULL, 'وَاَصْبَحَ فُؤٰادُ اُمِّ مُوسٰى فَارِغًاۜ اِنْ كَادَتْ لَتُبْد۪ي بِه۪ لَوْلَٓا اَنْ رَبَطْنَا عَلٰى قَلْبِهَا لِتَكُونَ مِنَ الْمُؤْمِن۪ينَ', 0),
(3263, 28, NULL, 11, NULL, NULL, 0, 0, NULL, 'وَقَالَتْ لِاُخْتِه۪ قُصّ۪يهِۘ فَبَصُرَتْ بِه۪ عَنْ جُنُبٍ وَهُمْ لَا يَشْعُرُونَۙ', 0),
(3264, 28, NULL, 12, NULL, NULL, 0, 0, NULL, 'وَحَرَّمْنَا عَلَيْهِ الْمَرَاضِعَ مِنْ قَبْلُ فَقَالَتْ هَلْ اَدُلُّكُمْ عَلٰٓى اَهْلِ بَيْتٍ يَكْفُلُونَهُ لَكُمْ وَهُمْ لَهُ نَاصِحُونَ', 0),
(3265, 28, NULL, 13, NULL, NULL, 0, 0, NULL, 'فَرَدَدْنَاهُ اِلٰٓى اُمِّه۪ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ وَلِتَعْلَمَ اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ۟', 0),
(3266, 28, NULL, 14, 386, 6, 2, 20, NULL, 'وَلَمَّا بَلَغَ اَشُدَّهُ وَاسْتَوٰٓى اٰتَيْنَاهُ حُكْمًا وَعِلْمًاۜ وَكَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَ', 0),
(3267, 28, NULL, 15, NULL, NULL, 0, 0, NULL, 'وَدَخَلَ الْمَد۪ينَةَ عَلٰى ح۪ينِ غَفْلَةٍ مِنْ اَهْلِهَا فَوَجَدَ ف۪يهَا رَجُلَيْنِ يَقْتَتِلَانِۘ هٰذَا مِنْ ش۪يعَتِه۪ وَهٰذَا مِنْ عَدُوِّه۪ۚ فَاسْتَغَاثَهُ الَّذ۪ي مِنْ ش۪يعَتِه۪ عَلَى الَّذ۪ي مِنْ عَدُوِّه۪ۙ فَوَكَزَهُ مُوسٰى فَقَضٰى عَلَيْهِۘ قَالَ هٰذَا مِنْ عَمَلِ الشَّيْطَانِۜ اِنَّهُ عَدُوٌّ مُضِلٌّ مُب۪ينٌ', 0),
(3268, 28, NULL, 16, NULL, NULL, 0, 0, NULL, 'قَالَ رَبِّ اِنّ۪ي ظَلَمْتُ نَفْس۪ي فَاغْفِرْ ل۪ي فَغَفَرَ لَهُۜ اِنَّهُ هُوَ الْغَفُورُ الرَّح۪يمُ', 0),
(3269, 28, NULL, 17, NULL, NULL, 0, 0, NULL, 'قَالَ رَبِّ بِمَٓا اَنْعَمْتَ عَلَيَّ فَلَنْ اَكُونَ ظَه۪يرًا لِلْمُجْرِم۪ينَ', 0),
(3270, 28, NULL, 18, NULL, NULL, 0, 0, NULL, 'فَاَصْبَحَ فِي الْمَد۪ينَةِ خَٓائِفًا يَتَرَقَّبُ فَاِذَا الَّذِي اسْتَنْصَرَهُ بِالْاَمْسِ يَسْتَصْرِخُهُۜ قَالَ لَهُ مُوسٰٓى اِنَّكَ لَغَوِيٌّ مُب۪ينٌ', 0),
(3271, 28, NULL, 19, NULL, NULL, 0, 0, NULL, 'فَلَمَّٓا اَنْ اَرَادَ اَنْ يَبْطِشَ بِالَّذ۪ي هُوَ عَدُوٌّ لَهُمَاۙ قَالَ يَا مُوسٰٓى اَتُر۪يدُ اَنْ تَقْتُلَن۪ي كَمَا قَتَلْتَ نَفْسًا بِالْاَمْسِۗ اِنْ تُر۪يدُ اِلَّٓا اَنْ تَكُونَ جَبَّارًا فِي الْاَرْضِ وَمَا تُر۪يدُ اَنْ تَكُونَ مِنَ الْمُصْلِح۪ينَ', 0),
(3272, 28, NULL, 20, NULL, NULL, 0, 0, NULL, 'وَجَٓاءَ رَجُلٌ مِنْ اَقْصَا الْمَد۪ينَةِ يَسْعٰىۘ قَالَ يَا مُوسٰٓى اِنَّ الْمَلَاَ يَأْتَمِرُونَ بِكَ لِيَقْتُلُوكَ فَاخْرُجْ اِنّ۪ي لَكَ مِنَ النَّاصِح۪ينَ', 0),
(3273, 28, NULL, 21, NULL, NULL, 0, 0, NULL, 'فَخَرَجَ مِنْهَا خَٓائِفًا يَتَرَقَّبُۘ قَالَ رَبِّ نَجِّن۪ي مِنَ الْقَوْمِ الظَّالِم۪ينَ۟', 0),
(3274, 28, NULL, 22, 387, 7, 2, 20, NULL, 'وَلَمَّا تَوَجَّهَ تِلْقَٓاءَ مَدْيَنَ قَالَ عَسٰى رَبّ۪ٓي اَنْ يَهْدِيَن۪ي سَوَٓاءَ السَّب۪يلِ', 0),
(3275, 28, NULL, 23, NULL, NULL, 0, 0, NULL, 'وَلَمَّا وَرَدَ مَٓاءَ مَدْيَنَ وَجَدَ عَلَيْهِ اُمَّةً مِنَ النَّاسِ يَسْقُونَۘ وَوَجَدَ مِنْ دُونِهِمُ امْرَاَتَيْنِ تَذُودَانِۚ قَالَ مَا خَطْبُكُمَاۜ قَالَتَا لَا نَسْق۪ي حَتّٰى يُصْدِرَ الرِّعَٓاءُ وَاَبُونَا شَيْخٌ كَب۪يرٌ', 0),
(3276, 28, NULL, 24, NULL, NULL, 0, 0, NULL, 'فَسَقٰى لَهُمَا ثُمَّ تَوَلّٰٓى اِلَى الظِّلِّ فَقَالَ رَبِّ اِنّ۪ي لِمَٓا اَنْزَلْتَ اِلَيَّ مِنْ خَيْرٍ فَق۪يرٌ', 0),
(3277, 28, NULL, 25, NULL, NULL, 0, 0, NULL, 'فَجَٓاءَتْهُ اِحْدٰيهُمَا تَمْش۪ي عَلَى اسْتِحْيَٓاءٍۘ قَالَتْ اِنَّ اَب۪ي يَدْعُوكَ لِيَجْزِيَكَ اَجْرَ مَا سَقَيْتَ لَنَاۜ فَلَمَّا جَٓاءَهُ وَقَصَّ عَلَيْهِ الْقَصَصَۙ قَالَ لَا تَخَفْ۠ نَجَوْتَ مِنَ الْقَوْمِ الظَّالِم۪ينَ', 0),
(3278, 28, NULL, 26, NULL, NULL, 0, 0, NULL, 'قَالَتْ اِحْدٰيهُمَا يَٓا اَبَتِ اسْتَأْجِرْهُۘ اِنَّ خَيْرَ مَنِ اسْتَأْجَرْتَ الْقَوِيُّ الْاَم۪ينُ', 0),
(3279, 28, NULL, 27, NULL, NULL, 0, 0, NULL, 'قَالَ اِنّ۪ٓي اُر۪يدُ اَنْ اُنْكِحَكَ اِحْدَى ابْنَتَيَّ هَاتَيْنِ عَلٰٓى اَنْ تَأْجُرَن۪ي ثَمَانِيَ حِجَجٍۚ فَاِنْ اَتْمَمْتَ عَشْرًا فَمِنْ عِنْدِكَۚ وَمَٓا اُر۪يدُ اَنْ اَشُقَّ عَلَيْكَۜ سَتَجِدُن۪ٓي اِنْ شَٓاءَ اللّٰهُ مِنَ الصَّالِح۪ينَ', 0),
(3280, 28, NULL, 28, NULL, NULL, 0, 0, NULL, 'قَالَ ذٰلِكَ بَيْن۪ي وَبَيْنَكَۜ اَيَّمَا الْاَجَلَيْنِ قَضَيْتُ فَلَا عُدْوَانَ عَلَيَّۜ وَاللّٰهُ عَلٰى مَا نَقُولُ وَك۪يلٌ۟', 0),
(3281, 28, NULL, 29, 388, 8, 2, 20, NULL, 'فَلَمَّا قَضٰى مُوسَى الْاَجَلَ وَسَارَ بِاَهْلِه۪ٓ اٰنَسَ مِنْ جَانِبِ الطُّورِ نَارًاۚ قَالَ لِاَهْلِهِ امْكُثُٓوا اِنّ۪ٓي اٰنَسْتُ نَارًا لَعَلّ۪ٓي اٰت۪يكُمْ مِنْهَا بِخَبَرٍ اَوْ جَذْوَةٍ مِنَ النَّارِ لَعَلَّكُمْ تَصْطَلُونَ', 0),
(3282, 28, NULL, 30, NULL, NULL, 0, 0, NULL, 'فَلَمَّٓا اَتٰيهَا نُودِيَ مِنْ شَاطِئِ الْوَادِ الْاَيْمَنِ فِي الْبُقْعَةِ الْمُبَارَكَةِ مِنَ الشَّجَرَةِ اَنْ يَا مُوسٰٓى اِنّ۪ٓي اَنَا۬ اللّٰهُ رَبُّ الْعَالَم۪ينَۙ', 0),
(3283, 28, NULL, 31, NULL, NULL, 0, 0, NULL, 'وَاَنْ اَلْقِ عَصَاكَۜ فَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَٓانٌّ وَلّٰى مُدْبِرًا وَلَمْ يُعَقِّبْۜ يَا مُوسٰٓى اَقْبِلْ وَلَا تَخَفْ۠ اِنَّكَ مِنَ الْاٰمِن۪ينَ', 0),
(3284, 28, NULL, 32, NULL, NULL, 0, 0, NULL, 'اُسْلُكْ يَدَكَ ف۪ي جَيْبِكَ تَخْرُجْ بَيْضَٓاءَ مِنْ غَيْرِ سُٓوءٍۘ وَاضْمُمْ اِلَيْكَ جَنَاحَكَ مِنَ الرَّهْبِ فَذَانِكَ بُرْهَانَانِ مِنْ رَبِّكَ اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ۜ اِنَّهُمْ كَانُوا قَوْمًا فَاسِق۪ينَ', 0),
(3285, 28, NULL, 33, NULL, NULL, 0, 0, NULL, 'قَالَ رَبِّ اِنّ۪ي قَتَلْتُ مِنْهُمْ نَفْسًا فَاَخَافُ اَنْ يَقْتُلُونِ', 0),
(3286, 28, NULL, 34, NULL, NULL, 0, 0, NULL, 'وَاَخ۪ي هٰرُونُ هُوَ اَفْصَحُ مِنّ۪ي لِسَانًا فَاَرْسِلْهُ مَعِيَ رِدْءًا يُصَدِّقُن۪يۘ اِنّ۪ٓي اَخَافُ اَنْ يُكَذِّبُونِ', 0),
(3287, 28, NULL, 35, NULL, NULL, 0, 0, NULL, 'قَالَ سَنَشُدُّ عَضُدَكَ بِاَخ۪يكَ وَنَجْعَلُ لَكُمَا سُلْطَانًا فَلَا يَصِلُونَ اِلَيْكُمَا بِاٰيَاتِنَاۚ اَنْتُمَا وَمَنِ اتَّبَعَكُمَا الْغَالِبُونَ', 0),
(3288, 28, NULL, 36, 389, 9, 2, 20, NULL, 'فَلَمَّا جَٓاءَهُمْ مُوسٰى بِاٰيَاتِنَا بَيِّنَاتٍ قَالُوا مَا هٰذَٓا اِلَّا سِحْرٌ مُفْتَرًى وَمَا سَمِعْنَا بِهٰذَا ف۪ٓي اٰبَٓائِنَا الْاَوَّل۪ينَ', 0),
(3289, 28, NULL, 37, NULL, NULL, 0, 0, NULL, 'وَقَالَ مُوسٰى رَبّ۪ٓي اَعْلَمُ بِمَنْ جَٓاءَ بِالْهُدٰى مِنْ عِنْدِه۪ وَمَنْ تَكُونُ لَهُ عَاقِبَةُ الدَّارِۜ اِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ', 0),
(3290, 28, NULL, 38, NULL, NULL, 0, 0, NULL, 'وَقَالَ فِرْعَوْنُ يَٓا اَيُّهَا الْمَلَاُ مَا عَلِمْتُ لَكُمْ مِنْ اِلٰهٍ غَيْر۪يۚ فَاَوْقِدْ ل۪ي يَا هَامَانُ عَلَى الطّ۪ينِ فَاجْعَلْ ل۪ي صَرْحًا لَعَلّ۪ٓي اَطَّلِعُ اِلٰٓى اِلٰهِ مُوسٰىۙ وَاِنّ۪ي لَاَظُنُّهُ مِنَ الْكَاذِب۪ينَ', 0),
(3291, 28, NULL, 39, NULL, NULL, 0, 0, NULL, 'وَاسْتَكْبَرَ هُوَ وَجُنُودُهُ فِي الْاَرْضِ بِغَيْرِ الْحَقِّ وَظَنُّٓوا اَنَّهُمْ اِلَيْنَا لَا يُرْجَعُونَ', 0),
(3292, 28, NULL, 40, NULL, NULL, 0, 0, NULL, 'فَاَخَذْنَاهُ وَجُنُودَهُ فَنَبَذْنَاهُمْ فِي الْيَمِّۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظَّالِم۪ينَ', 0),
(3293, 28, NULL, 41, NULL, NULL, 0, 0, NULL, 'وَجَعَلْنَاهُمْ اَئِمَّةً يَدْعُونَ اِلَى النَّارِۚ وَيَوْمَ الْقِيٰمَةِ لَا يُنْصَرُونَ', 0),
(3294, 28, NULL, 42, NULL, NULL, 0, 0, NULL, 'وَاَتْبَعْنَاهُمْ ف۪ي هٰذِهِ الدُّنْيَا لَعْنَةًۚ وَيَوْمَ الْقِيٰمَةِ هُمْ مِنَ الْمَقْبُوح۪ينَ۟', 0),
(3295, 28, NULL, 43, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ مِنْ بَعْدِ مَٓا اَهْلَكْنَا الْقُرُونَ الْاُو۫لٰى بَصَٓائِرَ لِلنَّاسِ وَهُدًى وَرَحْمَةً لَعَلَّهُمْ يَتَذَكَّرُونَ', 0),
(3296, 28, NULL, 44, 390, 10, 2, 20, NULL, 'وَمَا كُنْتَ بِجَانِبِ الْغَرْبِيِّ اِذْ قَضَيْنَٓا اِلٰى مُوسَى الْاَمْرَ وَمَا كُنْتَ مِنَ الشَّاهِد۪ينَۙ', 0),
(3297, 28, NULL, 45, NULL, NULL, 0, 0, NULL, 'وَلٰكِنَّٓا اَنْشَأْنَا قُرُونًا فَتَطَاوَلَ عَلَيْهِمُ الْعُمُرُۚ وَمَا كُنْتَ ثَاوِيًا ف۪ٓي اَهْلِ مَدْيَنَ تَتْلُوا عَلَيْهِمْ اٰيَاتِنَاۙ وَلٰكِنَّا كُنَّا مُرْسِل۪ينَ', 0),
(3298, 28, NULL, 46, NULL, NULL, 0, 0, NULL, 'وَمَا كُنْتَ بِجَانِبِ الطُّورِ اِذْ نَادَيْنَا وَلٰكِنْ رَحْمَةً مِنْ رَبِّكَ لِتُنْذِرَ قَوْمًا مَٓا اَتٰيهُمْ مِنْ نَذ۪يرٍ مِنْ قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ', 0),
(3299, 28, NULL, 47, NULL, NULL, 0, 0, NULL, 'وَلَوْلَٓا اَنْ تُص۪يبَهُمْ مُص۪يبَةٌ بِمَا قَدَّمَتْ اَيْد۪يهِمْ فَيَقُولُوا رَبَّنَا لَوْلَٓا اَرْسَلْتَ اِلَيْنَا رَسُولًا فَنَتَّبِعَ اٰيَاتِكَ وَنَكُونَ مِنَ الْمُؤْمِن۪ينَ', 0),
(3300, 28, NULL, 48, NULL, NULL, 0, 0, NULL, 'فَلَمَّا جَٓاءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوا لَوْلَٓا اُو۫تِيَ مِثْلَ مَٓا اُو۫تِيَ مُوسٰىۜ اَوَلَمْ يَكْفُرُوا بِمَٓا اُو۫تِيَ مُوسٰى مِنْ قَبْلُۚ قَالُوا سِحْرَانِ تَظَاهَرَا۠ وَقَالُٓوا اِنَّا بِكُلٍّ كَافِرُونَ', 0),
(3301, 28, NULL, 49, NULL, NULL, 0, 0, NULL, 'قُلْ فَأْتُوا بِكِتَابٍ مِنْ عِنْدِ اللّٰهِ هُوَ اَهْدٰى مِنْهُمَٓا اَتَّبِعْهُ اِنْ كُنْتُمْ صَادِق۪ينَ', 0),
(3302, 28, NULL, 50, NULL, NULL, 0, 0, NULL, 'فَاِنْ لَمْ يَسْتَج۪يبُوا لَكَ فَاعْلَمْ اَنَّمَا يَتَّبِعُونَ اَهْوَٓاءَهُمْۜ وَمَنْ اَضَلُّ مِمَّنِ اتَّبَعَ هَوٰيهُ بِغَيْرِ هُدًى مِنَ اللّٰهِۜ اِنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ', 0),
(3303, 28, NULL, 51, 391, 11, 3, 20, NULL, 'وَلَقَدْ وَصَّلْنَا لَهُمُ الْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُونَۜ۟', 0),
(3304, 28, NULL, 52, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ مِنْ قَبْلِه۪ هُمْ بِه۪ يُؤْمِنُونَ', 0),
(3305, 28, NULL, 53, NULL, NULL, 0, 0, NULL, 'وَاِذَا يُتْلٰى عَلَيْهِمْ قَالُٓوا اٰمَنَّا بِه۪ٓ اِنَّهُ الْحَقُّ مِنْ رَبِّنَٓا اِنَّا كُنَّا مِنْ قَبْلِه۪ مُسْلِم۪ينَ', 0),
(3306, 28, NULL, 54, NULL, NULL, 0, 0, NULL, 'اُو۬لٰٓئِكَ يُؤْتَوْنَ اَجْرَهُمْ مَرَّتَيْنِ بِمَا صَبَرُوا وَيَدْرَؤُ۫نَ بِالْحَسَنَةِ السَّيِّئَةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ', 0),
(3307, 28, NULL, 55, NULL, NULL, 0, 0, NULL, 'وَاِذَا سَمِعُوا اللَّغْوَ اَعْرَضُوا عَنْهُ وَقَالُوا لَنَٓا اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْۘ سَلَامٌ عَلَيْكُمْۘ لَا نَبْتَغِي الْجَاهِل۪ينَ', 0),
(3308, 28, NULL, 56, NULL, NULL, 0, 0, NULL, 'اِنَّكَ لَا تَهْد۪ي مَنْ اَحْبَبْتَ وَلٰكِنَّ اللّٰهَ يَهْد۪ي مَنْ يَشَٓاءُۚ وَهُوَ اَعْلَمُ بِالْمُهْتَد۪ينَ', 0),
(3309, 28, NULL, 57, NULL, NULL, 0, 0, NULL, 'وَقَالُٓوا اِنْ نَتَّبِعِ الْهُدٰى مَعَكَ نُتَخَطَّفْ مِنْ اَرْضِنَاۜ اَوَلَمْ نُمَكِّنْ لَهُمْ حَرَمًا اٰمِنًا يُجْبٰٓى اِلَيْهِ ثَمَرَاتُ كُلِّ شَيْءٍ رِزْقًا مِنْ لَدُنَّا وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ', 0),
(3310, 28, NULL, 58, NULL, NULL, 0, 0, NULL, 'وَكَمْ اَهْلَكْنَا مِنْ قَرْيَةٍ بَطِرَتْ مَع۪يشَتَهَاۚ فَتِلْكَ مَسَاكِنُهُمْ لَمْ تُسْكَنْ مِنْ بَعْدِهِمْ اِلَّا قَل۪يلًاۜ وَكُنَّا نَحْنُ الْوَارِث۪ينَ', 0),
(3311, 28, NULL, 59, NULL, NULL, 0, 0, NULL, 'وَمَا كَانَ رَبُّكَ مُهْلِكَ الْقُرٰى حَتّٰى يَبْعَثَ ف۪ٓي اُمِّهَا رَسُولًا يَتْلُوا عَلَيْهِمْ اٰيَاتِنَاۚ وَمَا كُنَّا مُهْلِكِي الْقُرٰٓى اِلَّا وَاَهْلُهَا ظَالِمُونَ', 0),
(3312, 28, NULL, 60, 392, 12, 3, 20, NULL, 'وَمَٓا اُو۫ت۪يتُمْ مِنْ شَيْءٍ فَمَتَاعُ الْحَيٰوةِ الدُّنْيَا وَز۪ينَتُهَاۚ وَمَا عِنْدَ اللّٰهِ خَيْرٌ وَاَبْقٰىۜ اَفَلَا تَعْقِلُونَ۟', 0),
(3313, 28, NULL, 61, NULL, NULL, 0, 0, NULL, 'اَفَمَنْ وَعَدْنَاهُ وَعْدًا حَسَنًا فَهُوَ لَاق۪يهِ كَمَنْ مَتَّعْنَاهُ مَتَاعَ الْحَيٰوةِ الدُّنْيَا ثُمَّ هُوَ يَوْمَ الْقِيٰمَةِ مِنَ الْمُحْضَر۪ينَ', 0),
(3314, 28, NULL, 62, NULL, NULL, 0, 0, NULL, 'وَيَوْمَ يُنَاد۪يهِمْ فَيَقُولُ اَيْنَ شُرَكَٓاءِيَ الَّذ۪ينَ كُنْتُمْ تَزْعُمُونَ', 0),
(3315, 28, NULL, 63, NULL, NULL, 0, 0, NULL, 'قَالَ الَّذ۪ينَ حَقَّ عَلَيْهِمُ الْقَوْلُ رَبَّنَا هٰٓؤُ۬لَٓاءِ الَّذ۪ينَ اَغْوَيْنَاۚ اَغْوَيْنَاهُمْ كَمَا غَوَيْنَاۚ تَبَرَّأْنَٓا اِلَيْكَۘ مَا كَانُٓوا اِيَّانَا يَعْبُدُونَ', 0),
(3316, 28, NULL, 64, NULL, NULL, 0, 0, NULL, 'وَق۪يلَ ادْعُوا شُرَكَٓاءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَج۪يبُوا لَهُمْ وَرَاَوُا الْعَذَابَۚ لَوْ اَنَّهُمْ كَانُوا يَهْتَدُونَ', 0),
(3317, 28, NULL, 65, NULL, NULL, 0, 0, NULL, 'وَيَوْمَ يُنَاد۪يهِمْ فَيَقُولُ مَاذَٓا اَجَبْتُمُ الْمُرْسَل۪ينَ', 0),
(3318, 28, NULL, 66, NULL, NULL, 0, 0, NULL, 'فَعَمِيَتْ عَلَيْهِمُ الْاَنْبَٓاءُ يَوْمَئِذٍ فَهُمْ لَا يَتَسَٓاءَلُونَ', 0),
(3319, 28, NULL, 67, NULL, NULL, 0, 0, NULL, 'فَاَمَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا فَعَسٰٓى اَنْ يَكُونَ مِنَ الْمُفْلِح۪ينَ', 0),
(3320, 28, NULL, 68, NULL, NULL, 0, 0, NULL, 'وَرَبُّكَ يَخْلُقُ مَا يَشَٓاءُ وَيَخْتَارُۜ مَا كَانَ لَهُمُ الْخِيَرَةُۜ سُبْحَانَ اللّٰهِ وَتَعَالٰى عَمَّا يُشْرِكُونَ', 0),
(3321, 28, NULL, 69, NULL, NULL, 0, 0, NULL, 'وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ', 0),
(3322, 28, NULL, 70, NULL, NULL, 0, 0, NULL, 'وَهُوَ اللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۜ لَهُ الْحَمْدُ فِي الْاُو۫لٰى وَالْاٰخِرَةِۘ وَلَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُونَ', 0),
(3323, 28, NULL, 71, 393, 13, 3, 20, NULL, 'قُلْ اَرَاَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ الَّيْلَ سَرْمَدًا اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْت۪يكُمْ بِضِيَٓاءٍۜ اَفَلَا تَسْمَعُونَ', 0),
(3324, 28, NULL, 72, NULL, NULL, 0, 0, NULL, 'قُلْ اَرَاَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ النَّهَارَ سَرْمَدًا اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْت۪يكُمْ بِلَيْلٍ تَسْكُنُونَ ف۪يهِۜ اَفَلَا تُبْصِرُونَ', 0),
(3325, 28, NULL, 73, NULL, NULL, 0, 0, NULL, 'وَمِنْ رَحْمَتِه۪ جَعَلَ لَكُمُ الَّيْلَ وَالنَّهَارَ لِتَسْكُنُوا ف۪يهِ وَلِتَبْتَغُوا مِنْ فَضْلِه۪ وَلَعَلَّكُمْ تَشْكُرُونَ', 0),
(3326, 28, NULL, 74, NULL, NULL, 0, 0, NULL, 'وَيَوْمَ يُنَاد۪يهِمْ فَيَقُولُ اَيْنَ شُرَكَٓاءِيَ الَّذ۪ينَ كُنْتُمْ تَزْعُمُونَ', 0),
(3327, 28, NULL, 75, NULL, NULL, 0, 0, NULL, 'وَنَزَعْنَا مِنْ كُلِّ اُمَّةٍ شَه۪يدًا فَقُلْنَا هَاتُوا بُرْهَانَكُمْ فَعَلِمُٓوا اَنَّ الْحَقَّ لِلّٰهِ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ۟', 0),
(3328, 28, NULL, 76, NULL, NULL, 0, 0, NULL, 'اِنَّ قَارُونَ كَانَ مِنْ قَوْمِ مُوسٰى فَبَغٰى عَلَيْهِمْۖ وَاٰتَيْنَاهُ مِنَ الْكُنُوزِ مَٓا اِنَّ مَفَاتِحَهُ لَتَنُٓواُ بِالْعُصْبَةِ اُ۬ولِي الْقُوَّةِۗ اِذْ قَالَ لَهُ قَوْمُهُ لَا تَفْرَحْ اِنَّ اللّٰهَ لَا يُحِبُّ الْفَرِح۪ينَ', 0),
(3329, 28, NULL, 77, NULL, NULL, 0, 0, NULL, 'وَابْتَغِ ف۪يمَٓا اٰتٰيكَ اللّٰهُ الدَّارَ الْاٰخِرَةَ وَلَا تَنْسَ نَص۪يبَكَ مِنَ الدُّنْيَا وَاَحْسِنْ كَمَٓا اَحْسَنَ اللّٰهُ اِلَيْكَ وَلَا تَبْغِ الْفَسَادَ فِي الْاَرْضِۜ اِنَّ اللّٰهَ لَا يُحِبُّ الْمُفْسِد۪ينَ', 0),
(3330, 28, NULL, 78, 394, 14, 3, 20, NULL, 'قَالَ اِنَّمَٓا اُو۫ت۪يتُهُ عَلٰى عِلْمٍ عِنْد۪يۜ اَوَلَمْ يَعْلَمْ اَنَّ اللّٰهَ قَدْ اَهْلَكَ مِنْ قَبْلِه۪ مِنَ الْقُرُونِ مَنْ هُوَ اَشَدُّ مِنْهُ قُوَّةً وَاَكْثَرُ جَمْعًاۜ وَلَا يُسْـَٔلُ عَنْ ذُنُوبِهِمُ الْمُجْرِمُونَ', 0),
(3331, 28, NULL, 79, NULL, NULL, 0, 0, NULL, 'فَخَرَجَ عَلٰى قَوْمِه۪ ف۪ي ز۪ينَتِه۪ۜ قَالَ الَّذ۪ينَ يُر۪يدُونَ الْحَيٰوةَ الدُّنْيَا يَا لَيْتَ لَنَا مِثْلَ مَٓا اُو۫تِيَ قَارُونُۙ اِنَّهُ لَذُو حَظٍّ عَظ۪يمٍ', 0),
(3332, 28, NULL, 80, NULL, NULL, 0, 0, NULL, 'وَقَالَ الَّذ۪ينَ اُو۫تُوا الْعِلْمَ وَيْلَكُمْ ثَوَابُ اللّٰهِ خَيْرٌ لِمَنْ اٰمَنَ وَعَمِلَ صَالِحًاۚ وَلَا يُلَقّٰيهَٓا اِلَّا الصَّابِرُونَ', 0),
(3333, 28, NULL, 81, NULL, NULL, 0, 0, NULL, 'فَخَسَفْنَا بِه۪ وَبِدَارِهِ الْاَرْضَ فَمَا كَانَ لَهُ مِنْ فِئَةٍ يَنْصُرُونَهُ مِنْ دُونِ اللّٰهِۗ وَمَا كَانَ مِنَ الْمُنْتَصِر۪ينَ', 0),
(3334, 28, NULL, 82, NULL, NULL, 0, 0, NULL, 'وَاَصْبَحَ الَّذ۪ينَ تَمَنَّوْا مَكَانَهُ بِالْاَمْسِ يَقُولُونَ وَيْكَاَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ مِنْ عِبَادِه۪ وَيَقْدِرُۚ لَوْلَٓا اَنْ مَنَّ اللّٰهُ عَلَيْنَا لَخَسَفَ بِنَاۜ وَيْكَاَنَّهُ لَا يُفْلِحُ الْكَافِرُونَ۟', 0),
(3335, 28, NULL, 83, NULL, NULL, 0, 0, NULL, 'تِلْكَ الدَّارُ الْاٰخِرَةُ نَجْعَلُهَا لِلَّذ۪ينَ لَا يُر۪يدُونَ عُلُوًّا فِي الْاَرْضِ وَلَا فَسَادًاۜ وَالْعَاقِبَةُ لِلْمُتَّق۪ينَ', 0),
(3336, 28, NULL, 84, NULL, NULL, 0, 0, NULL, 'مَنْ جَٓاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِنْهَاۚ وَمَنْ جَٓاءَ بِالسَّيِّئَةِ فَلَا يُجْزَى الَّذ۪ينَ عَمِلُوا السَّيِّـَٔاتِ اِلَّا مَا كَانُوا يَعْمَلُونَ', 0),
(3337, 28, NULL, 85, 395, 15, 4, 20, NULL, 'اِنَّ الَّذ۪ي فَرَضَ عَلَيْكَ الْقُرْاٰنَ لَرَٓادُّكَ اِلٰى مَعَادٍۜ قُلْ رَبّ۪ٓي اَعْلَمُ مَنْ جَٓاءَ بِالْهُدٰى وَمَنْ هُوَ ف۪ي ضَلَالٍ مُب۪ينٍ', 0),
(3338, 28, NULL, 86, NULL, NULL, 0, 0, NULL, 'وَمَا كُنْتَ تَرْجُٓوا اَنْ يُلْقٰٓى اِلَيْكَ الْكِتَابُ اِلَّا رَحْمَةً مِنْ رَبِّكَ فَلَا تَكُونَنَّ ظَه۪يرًا لِلْكَافِر۪ينَۘ', 0),
(3339, 28, NULL, 87, NULL, NULL, 0, 0, NULL, 'وَلَا يَصُدُّنَّكَ عَنْ اٰيَاتِ اللّٰهِ بَعْدَ اِذْ اُنْزِلَتْ اِلَيْكَ وَادْعُ اِلٰى رَبِّكَ وَلَا تَكُونَنَّ مِنَ الْمُشْرِك۪ينَۚ', 0),
(3340, 28, NULL, 88, NULL, NULL, 0, 0, NULL, 'وَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَۢ لَٓا اِلٰهَ اِلَّا هُوَ۠ كُلُّ شَيْءٍ هَالِكٌ اِلَّا وَجْهَهُۜ لَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُونَ', 0),
(3341, 29, 29, 1, NULL, NULL, 0, 0, NULL, 'الٓمٓ۠', 0),
(3342, 29, NULL, 2, NULL, NULL, 0, 0, NULL, 'اَحَسِبَ النَّاسُ اَنْ يُتْرَكُٓوا اَنْ يَقُولُٓوا اٰمَنَّا وَهُمْ لَا يُفْتَنُونَ', 0),
(3343, 29, NULL, 3, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ فَتَنَّا الَّذ۪ينَ مِنْ قَبْلِهِمْ فَلَيَعْلَمَنَّ اللّٰهُ الَّذ۪ينَ صَدَقُوا وَلَيَعْلَمَنَّ الْكَاذِب۪ينَ', 0),
(3344, 29, NULL, 4, NULL, NULL, 0, 0, NULL, 'اَمْ حَسِبَ الَّذ۪ينَ يَعْمَلُونَ السَّيِّـَٔاتِ اَنْ يَسْبِقُونَاۜ سَٓاءَ مَا يَحْكُمُونَ', 0),
(3345, 29, NULL, 5, NULL, NULL, 0, 0, NULL, 'مَنْ كَانَ يَرْجُوا لِقَٓاءَ اللّٰهِ فَاِنَّ اَجَلَ اللّٰهِ لَاٰتٍۜ وَهُوَ السَّم۪يعُ الْعَل۪يمُ', 0),
(3346, 29, NULL, 6, NULL, NULL, 0, 0, NULL, 'وَمَنْ جَاهَدَ فَاِنَّمَا يُجَاهِدُ لِنَفْسِه۪ۜ اِنَّ اللّٰهَ لَغَنِيٌّ عَنِ الْعَالَم۪ينَ', 0),
(3347, 29, NULL, 7, 396, 16, 4, 20, NULL, 'وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّـَٔاتِهِمْ وَلَنَجْزِيَنَّهُمْ اَحْسَنَ الَّذ۪ي كَانُوا يَعْمَلُونَ', 0),
(3348, 29, NULL, 8, NULL, NULL, 0, 0, NULL, 'وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِ حُسْنًاۜ وَاِنْ جَاهَدَاكَ لِتُشْرِكَ ب۪ي مَا لَيْسَ لَكَ بِه۪ عِلْمٌ فَلَا تُطِعْهُمَاۜ اِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 0),
(3349, 29, NULL, 9, NULL, NULL, 0, 0, NULL, 'وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُدْخِلَنَّهُمْ فِي الصَّالِح۪ينَ', 0),
(3350, 29, NULL, 10, NULL, NULL, 0, 0, NULL, 'وَمِنَ النَّاسِ مَنْ يَقُولُ اٰمَنَّا بِاللّٰهِ فَاِذَٓا اُو۫ذِيَ فِي اللّٰهِ جَعَلَ فِتْنَةَ النَّاسِ كَعَذَابِ اللّٰهِۜ وَلَئِنْ جَٓاءَ نَصْرٌ مِنْ رَبِّكَ لَيَقُولُنَّ اِنَّا كُنَّا مَعَكُمْۜ اَوَلَيْسَ اللّٰهُ بِاَعْلَمَ بِمَا ف۪ي صُدُورِ الْعَالَم۪ينَ', 0),
(3351, 29, NULL, 11, NULL, NULL, 0, 0, NULL, 'وَلَيَعْلَمَنَّ اللّٰهُ الَّذ۪ينَ اٰمَنُوا وَلَيَعْلَمَنَّ الْمُنَافِق۪ينَ', 0),
(3352, 29, NULL, 12, NULL, NULL, 0, 0, NULL, 'وَقَالَ الَّذ۪ينَ كَفَرُوا لِلَّذ۪ينَ اٰمَنُوا اتَّبِعُوا سَب۪يلَنَا وَلْنَحْمِلْ خَطَايَاكُمْۜ وَمَا هُمْ بِحَامِل۪ينَ مِنْ خَطَايَاهُمْ مِنْ شَيْءٍۜ اِنَّهُمْ لَكَاذِبُونَ', 0),
(3353, 29, NULL, 13, NULL, NULL, 0, 0, NULL, 'وَلَيَحْمِلُنَّ اَثْقَالَهُمْ وَاَثْقَالًا مَعَ اَثْقَالِهِمْۘ وَلَيُسْـَٔلُنَّ يَوْمَ الْقِيٰمَةِ عَمَّا كَانُوا يَفْتَرُونَ۟', 0),
(3354, 29, NULL, 14, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ اَرْسَلْنَا نُوحًا اِلٰى قَوْمِه۪ فَلَبِثَ ف۪يهِمْ اَلْفَ سَنَةٍ اِلَّا خَمْس۪ينَ عَامًاۜ فَاَخَذَهُمُ الطُّوفَانُ وَهُمْ ظَالِمُونَ', 0),
(3355, 29, NULL, 15, 397, 17, 4, 20, NULL, 'فَاَنْجَيْنَاهُ وَاَصْحَابَ السَّف۪ينَةِ وَجَعَلْنَاهَٓا اٰيَةً لِلْعَالَم۪ينَ', 0),
(3356, 29, NULL, 16, NULL, NULL, 0, 0, NULL, 'وَاِبْرٰه۪يمَ اِذْ قَالَ لِقَوْمِهِ اعْبُدُوا اللّٰهَ وَاتَّقُوهُۜ ذٰلِكُمْ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ', 0),
(3357, 29, NULL, 17, NULL, NULL, 0, 0, NULL, 'اِنَّمَا تَعْبُدُونَ مِنْ دُونِ اللّٰهِ اَوْثَانًا وَتَخْلُقُونَ اِفْكًاۜ اِنَّ الَّذ۪ينَ تَعْبُدُونَ مِنْ دُونِ اللّٰهِ لَا يَمْلِكُونَ لَكُمْ رِزْقًا فَابْتَغُوا عِنْدَ اللّٰهِ الرِّزْقَ وَاعْبُدُوهُ وَاشْكُرُوا لَهُۜ اِلَيْهِ تُرْجَعُونَ', 0),
(3358, 29, NULL, 18, NULL, NULL, 0, 0, NULL, 'وَاِنْ تُكَذِّبُوا فَقَدْ كَذَّبَ اُمَمٌ مِنْ قَبْلِكُمْۜ وَمَا عَلَى الرَّسُولِ اِلَّا الْبَلَاغُ الْمُب۪ينُ', 0),
(3359, 29, NULL, 19, NULL, NULL, 0, 0, NULL, 'اَوَلَمْ يَرَوْا كَيْفَ يُبْدِئُ اللّٰهُ الْخَلْقَ ثُمَّ يُع۪يدُهُۜ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَس۪يرٌ', 0),
(3360, 29, NULL, 20, NULL, NULL, 0, 0, NULL, 'قُلْ س۪يرُوا فِي الْاَرْضِ فَانْظُرُوا كَيْفَ بَدَاَ الْخَلْقَ ثُمَّ اللّٰهُ يُنْشِئُ النَّشْاَةَ الْاٰخِرَةَۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌۚ', 0),
(3361, 29, NULL, 21, NULL, NULL, 0, 0, NULL, 'يُعَذِّبُ مَنْ يَشَٓاءُ وَيَرْحَمُ مَنْ يَشَٓاءُۚ وَاِلَيْهِ تُقْلَبُونَ', 0),
(3362, 29, NULL, 22, NULL, NULL, 0, 0, NULL, 'وَمَٓا اَنْتُمْ بِمُعْجِز۪ينَ فِي الْاَرْضِ وَلَا فِي السَّمَٓاءِۘ وَمَا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ۟', 0),
(3363, 29, NULL, 23, NULL, NULL, 0, 0, NULL, 'وَالَّذ۪ينَ كَفَرُوا بِاٰيَاتِ اللّٰهِ وَلِقَٓائِه۪ٓ اُو۬لٰٓئِكَ يَئِسُوا مِنْ رَحْمَت۪ي وَاُو۬لٰٓئِكَ لَهُمْ عَذَابٌ اَل۪يمٌ', 0),
(3364, 29, NULL, 24, 398, 18, 4, 20, NULL, 'فَمَا كَانَ جَوَابَ قَوْمِه۪ٓ اِلَّٓا اَنْ قَالُوا اقْتُلُوهُ اَوْ حَرِّقُوهُ فَاَنْجٰيهُ اللّٰهُ مِنَ النَّارِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 0),
(3365, 29, NULL, 25, NULL, NULL, 0, 0, NULL, 'وَقَالَ اِنَّمَا اتَّخَذْتُمْ مِنْ دُونِ اللّٰهِ اَوْثَانًاۙ مَوَدَّةَ بَيْنِكُمْ فِي الْحَيٰوةِ الدُّنْيَاۚ ثُمَّ يَوْمَ الْقِيٰمَةِ يَكْفُرُ بَعْضُكُمْ بِبَعْضٍ وَيَلْعَنُ بَعْضُكُمْ بَعْضًاۘ وَمَأْوٰيكُمُ النَّارُ وَمَا لَكُمْ مِنْ نَاصِر۪ينَۗ', 0),
(3366, 29, NULL, 26, NULL, NULL, 0, 0, NULL, 'فَاٰمَنَ لَهُ لُوطٌۢ وَقَالَ اِنّ۪ي مُهَاجِرٌ اِلٰى رَبّ۪يۜ اِنَّهُ هُوَ الْعَز۪يزُ الْحَك۪يمُ', 0),
(3367, 29, NULL, 27, NULL, NULL, 0, 0, NULL, 'وَوَهَبْنَا لَهُٓ اِسْحٰقَ وَيَعْقُوبَ وَجَعَلْنَا ف۪ي ذُرِّيَّتِهِ النُّبُوَّةَ وَالْكِتَابَ وَاٰتَيْنَاهُ اَجْرَهُ فِي الدُّنْيَاۚ وَاِنَّهُ فِي الْاٰخِرَةِ لَمِنَ الصَّالِح۪ينَ', 0),
(3368, 29, NULL, 28, NULL, NULL, 0, 0, NULL, 'وَلُوطًا اِذْ قَالَ لِقَوْمِه۪ٓ اِنَّكُمْ لَتَأْتُونَ الْفَاحِشَةَۘ مَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِنَ الْعَالَم۪ينَ', 0),
(3369, 29, NULL, 29, NULL, NULL, 0, 0, NULL, 'اَئِنَّكُمْ لَتَأْتُونَ الرِّجَالَ وَتَقْطَعُونَ السَّب۪يلَ وَتَأْتُونَ ف۪ي نَاد۪يكُمُ الْمُنْكَرَۜ فَمَا كَانَ جَوَابَ قَوْمِه۪ٓ اِلَّٓا اَنْ قَالُوا ائْتِنَا بِعَذَابِ اللّٰهِ اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ', 0),
(3370, 29, NULL, 30, NULL, NULL, 0, 0, NULL, 'قَالَ رَبِّ انْصُرْن۪ي عَلَى الْقَوْمِ الْمُفْسِد۪ينَ۟', 0),
(3371, 29, NULL, 31, 399, 19, 4, 20, NULL, 'وَلَمَّا جَٓاءَتْ رُسُلُنَٓا اِبْرٰه۪يمَ بِالْبُشْرٰىۙ قَالُٓوا اِنَّا مُهْلِكُٓوا اَهْلِ هٰذِهِ الْقَرْيَةِۚ اِنَّ اَهْلَهَا كَانُوا ظَالِم۪ينَۚ', 0),
(3372, 29, NULL, 32, NULL, NULL, 0, 0, NULL, 'قَالَ اِنَّ ف۪يهَا لُوطًاۜ قَالُوا نَحْنُ اَعْلَمُ بِمَنْ ف۪يهَاۘ لَنُنَجِّيَنَّهُ وَاَهْلَهُٓ اِلَّا امْرَاَتَهُۘ كَانَتْ مِنَ الْغَابِر۪ينَ', 0),
(3373, 29, NULL, 33, NULL, NULL, 0, 0, NULL, 'وَلَمَّٓا اَنْ جَٓاءَتْ رُسُلُنَا لُوطًا س۪ٓيءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَقَالُوا لَا تَخَفْ وَلَا تَحْزَنْ۠ اِنَّا مُنَجُّوكَ وَاَهْلَكَ اِلَّا امْرَاَتَكَ كَانَتْ مِنَ الْغَابِر۪ينَ', 0),
(3374, 29, NULL, 34, NULL, NULL, 0, 0, NULL, 'اِنَّا مُنْزِلُونَ عَلٰٓى اَهْلِ هٰذِهِ الْقَرْيَةِ رِجْزًا مِنَ السَّمَٓاءِ بِمَا كَانُوا يَفْسُقُونَ', 0),
(3375, 29, NULL, 35, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ تَرَكْنَا مِنْهَٓا اٰيَةً بَيِّنَةً لِقَوْمٍ يَعْقِلُونَ', 0),
(3376, 29, NULL, 36, NULL, NULL, 0, 0, NULL, 'وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًاۙ فَقَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ وَارْجُوا الْيَوْمَ الْاٰخِرَ وَلَا تَعْثَوْا فِي الْاَرْضِ مُفْسِد۪ينَ', 0),
(3377, 29, NULL, 37, NULL, NULL, 0, 0, NULL, 'فَكَذَّبُوهُ فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوا ف۪ي دَارِهِمْ جَاثِم۪ينَۘ', 0),
(3378, 29, NULL, 38, NULL, NULL, 0, 0, NULL, 'وَعَادًا وَثَمُودَا۬ وَقَدْ تَبَيَّنَ لَكُمْ مِنْ مَسَاكِنِهِمْ۠ وَزَيَّنَ لَهُمُ الشَّيْطَانُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّب۪يلِ وَكَانُوا مُسْتَبْصِر۪ينَۙ', 0),
(3379, 29, NULL, 39, 400, 20, 4, 20, NULL, 'وَقَارُونَ وَفِرْعَوْنَ وَهَامَانَ وَلَقَدْ جَٓاءَهُمْ مُوسٰى بِالْبَيِّنَاتِ فَاسْتَكْبَرُوا فِي الْاَرْضِ وَمَا كَانُوا سَابِق۪ينَۚ', 0),
(3380, 29, NULL, 40, NULL, NULL, 0, 0, NULL, 'فَكُلًّا اَخَذْنَا بِذَنْبِه۪ۚ فَمِنْهُمْ مَنْ اَرْسَلْنَا عَلَيْهِ حَاصِبًاۚ وَمِنْهُمْ مَنْ اَخَذَتْهُ الصَّيْحَةُۚ وَمِنْهُمْ مَنْ خَسَفْنَا بِهِ الْاَرْضَۚ وَمِنْهُمْ مَنْ اَغْرَقْنَاۚ وَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ', 0),
(3381, 29, NULL, 41, NULL, NULL, 0, 0, NULL, 'مَثَلُ الَّذ۪ينَ اتَّخَذُوا مِنْ دُونِ اللّٰهِ اَوْلِيَٓاءَ كَمَثَلِ الْعَنْكَبُوتِۚ اِتَّخَذَتْ بَيْتًاۜ وَاِنَّ اَوْهَنَ الْبُيُوتِ لَبَيْتُ الْعَنْكَبُوتِۢ لَوْ كَانُوا يَعْلَمُونَ', 0),
(3382, 29, NULL, 42, NULL, NULL, 0, 0, NULL, 'اِنَّ اللّٰهَ يَعْلَمُ مَا يَدْعُونَ مِنْ دُونِه۪ مِنْ شَيْءٍۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ', 0),
(3383, 29, NULL, 43, NULL, NULL, 0, 0, NULL, 'وَتِلْكَ الْاَمْثَالُ نَضْرِبُهَا لِلنَّاسِۚ وَمَا يَعْقِلُهَٓا اِلَّا الْعَالِمُونَ', 0),
(3384, 29, NULL, 44, NULL, NULL, 0, 0, NULL, 'خَلَقَ اللّٰهُ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِلْمُؤْمِن۪ينَ۟', 0),
(3385, 29, NULL, 45, NULL, NULL, 0, 0, NULL, 'اُتْلُ مَٓا اُو۫حِيَ اِلَيْكَ مِنَ الْكِتَابِ وَاَقِمِ الصَّلٰوةَۜ اِنَّ الصَّلٰوةَ تَنْهٰى عَنِ الْفَحْشَٓاءِ وَالْمُنْكَرِۜ وَلَذِكْرُ اللّٰهِ اَكْبَرُۜ وَاللّٰهُ يَعْلَمُ مَا تَصْنَعُونَ', 0),
(3386, 29, NULL, 46, 401, 1, 1, 21, 21, 'وَلَا تُجَادِلُٓوا اَهْلَ الْكِتَابِ اِلَّا بِالَّت۪ي هِيَ اَحْسَنُۗ اِلَّا الَّذ۪ينَ ظَلَمُوا مِنْهُمْ وَقُولُٓوا اٰمَنَّا بِالَّذ۪ٓي اُنْزِلَ اِلَيْنَا وَاُنْزِلَ اِلَيْكُمْ وَاِلٰهُنَا وَاِلٰهُكُمْ وَاحِدٌ وَنَحْنُ لَهُ مُسْلِمُونَ', 0),
(3387, 29, NULL, 47, NULL, NULL, 0, 0, NULL, 'وَكَذٰلِكَ اَنْزَلْنَٓا اِلَيْكَ الْكِتَابَۜ فَالَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يُؤْمِنُونَ بِه۪ۚ وَمِنْ هٰٓؤُ۬لَٓاءِ مَنْ يُؤْمِنُ بِه۪ۜ وَمَا يَجْحَدُ بِاٰيَاتِنَٓا اِلَّا الْكَافِرُونَ', 0),
(3388, 29, NULL, 48, NULL, NULL, 0, 0, NULL, 'وَمَا كُنْتَ تَتْلُوا مِنْ قَبْلِه۪ مِنْ كِتَابٍ وَلَا تَخُطُّهُ بِيَم۪ينِكَ اِذًا لَارْتَابَ الْمُبْطِلُونَ', 0),
(3389, 29, NULL, 49, NULL, NULL, 0, 0, NULL, 'بَلْ هُوَ اٰيَاتٌ بَيِّنَاتٌ ف۪ي صُدُورِ الَّذ۪ينَ اُو۫تُوا الْعِلْمَۜ وَمَا يَجْحَدُ بِاٰيَاتِنَٓا اِلَّا الظَّالِمُونَ', 0),
(3390, 29, NULL, 50, NULL, NULL, 0, 0, NULL, 'وَقَالُوا لَوْلَٓا اُنْزِلَ عَلَيْهِ اٰيَاتٌ مِنْ رَبِّه۪ۜ قُلْ اِنَّمَا الْاٰيَاتُ عِنْدَ اللّٰهِۜ وَاِنَّمَٓا اَنَا۬ نَذ۪يرٌ مُب۪ينٌ', 0),
(3391, 29, NULL, 51, NULL, NULL, 0, 0, NULL, 'اَوَلَمْ يَكْفِهِمْ اَنَّٓا اَنْزَلْنَا عَلَيْكَ الْكِتَابَ يُتْلٰى عَلَيْهِمْۜ اِنَّ ف۪ي ذٰلِكَ لَرَحْمَةً وَذِكْرٰى لِقَوْمٍ يُؤْمِنُونَ۟', 0),
(3392, 29, NULL, 52, NULL, NULL, 0, 0, NULL, 'قُلْ كَفٰى بِاللّٰهِ بَيْن۪ي وَبَيْنَكُمْ شَه۪يدًاۚ يَعْلَمُ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ وَالَّذ۪ينَ اٰمَنُوا بِالْبَاطِلِ وَكَفَرُوا بِاللّٰهِۙ اُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ', 0),
(3393, 29, NULL, 53, 402, 2, 1, 21, NULL, 'وَيَسْتَعْجِلُونَكَ بِالْعَذَابِۜ وَلَوْلَٓا اَجَلٌ مُسَمًّى لَجَٓاءَهُمُ الْعَذَابُۜ وَلَيَأْتِيَنَّهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 0),
(3394, 29, NULL, 54, NULL, NULL, 0, 0, NULL, 'يَسْتَعْجِلُونَكَ بِالْعَذَابِۜ وَاِنَّ جَهَنَّمَ لَمُح۪يطَةٌ بِالْكَافِر۪ينَۙ', 0),
(3395, 29, NULL, 55, NULL, NULL, 0, 0, NULL, 'يَوْمَ يَغْشٰيهُمُ الْعَذَابُ مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْ وَيَقُولُ ذُوقُوا مَا كُنْتُمْ تَعْمَلُونَ', 0),
(3396, 29, NULL, 56, NULL, NULL, 0, 0, NULL, 'يَا عِبَادِيَ الَّذ۪ينَ اٰمَنُٓوا اِنَّ اَرْض۪ي وَاسِعَةٌ فَاِيَّايَ فَاعْبُدُونِ', 0),
(3397, 29, NULL, 57, NULL, NULL, 0, 0, NULL, 'كُلُّ نَفْسٍ ذَٓائِقَةُ الْمَوْتِ ثُمَّ اِلَيْنَا تُرْجَعُونَ', 0),
(3398, 29, NULL, 58, NULL, NULL, 0, 0, NULL, 'وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُبَوِّئَنَّهُمْ مِنَ الْجَنَّةِ غُرَفًا تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ نِعْمَ اَجْرُ الْعَامِل۪ينَۗ', 0),
(3399, 29, NULL, 59, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ينَ صَبَرُوا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُونَ', 0),
(3400, 29, NULL, 60, NULL, NULL, 0, 0, NULL, 'وَكَاَيِّنْ مِنْ دَٓابَّةٍ لَا تَحْمِلُ رِزْقَهَاۗ اَللّٰهُ يَرْزُقُهَا وَاِيَّاكُمْۘ وَهُوَ السَّم۪يعُ الْعَل۪يمُ', 0),
(3401, 29, NULL, 61, NULL, NULL, 0, 0, NULL, 'وَلَئِنْ سَاَلْتَهُمْ مَنْ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ لَيَقُولُنَّ اللّٰهُۚ فَاَنّٰى يُؤْفَكُونَ', 0),
(3402, 29, NULL, 62, NULL, NULL, 0, 0, NULL, 'اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ مِنْ عِبَادِه۪ وَيَقْدِرُ لَهُۜ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ', 0),
(3403, 29, NULL, 63, NULL, NULL, 0, 0, NULL, 'وَلَئِنْ سَاَلْتَهُمْ مَنْ نَزَّلَ مِنَ السَّمَٓاءِ مَٓاءً فَاَحْيَا بِهِ الْاَرْضَ مِنْ بَعْدِ مَوْتِهَا لَيَقُولُنَّ اللّٰهُۜ قُلِ الْحَمْدُ لِلّٰهِۜ بَلْ اَكْثَرُهُمْ لَا يَعْقِلُونَ۟', 0),
(3404, 29, NULL, 64, 403, 3, 1, 21, NULL, 'وَمَا هٰذِهِ الْحَيٰوةُ الدُّنْيَٓا اِلَّا لَهْوٌ وَلَعِبٌۜ وَاِنَّ الدَّارَ الْاٰخِرَةَ لَهِيَ الْحَيَوَانُۢ لَوْ كَانُوا يَعْلَمُونَ', 0),
(3405, 29, NULL, 65, NULL, NULL, 0, 0, NULL, 'فَاِذَا رَكِبُوا فِي الْفُلْكِ دَعَوُا اللّٰهَ مُخْلِص۪ينَ لَهُ الدّ۪ينَۚ فَلَمَّا نَجّٰيهُمْ اِلَى الْبَرِّ اِذَا هُمْ يُشْرِكُونَۙ', 0),
(3406, 29, NULL, 66, NULL, NULL, 0, 0, NULL, 'لِيَكْفُرُوا بِمَٓا اٰتَيْنَاهُمْۙ وَلِيَتَمَتَّعُوا۠ فَسَوْفَ يَعْلَمُونَ', 0),
(3407, 29, NULL, 67, NULL, NULL, 0, 0, NULL, 'اَوَلَمْ يَرَوْا اَنَّا جَعَلْنَا حَرَمًا اٰمِنًا وَيُتَخَطَّفُ النَّاسُ مِنْ حَوْلِهِمْۜ اَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَةِ اللّٰهِ يَكْفُرُونَ', 0),
(3408, 29, NULL, 68, NULL, NULL, 0, 0, NULL, 'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِالْحَقِّ لَمَّا جَٓاءَهُۜ اَلَيْسَ ف۪ي جَهَنَّمَ مَثْوًى لِلْكَافِر۪ينَ', 0),
(3409, 29, NULL, 69, NULL, NULL, 0, 0, NULL, 'وَالَّذ۪ينَ جَاهَدُوا ف۪ينَا لَنَهْدِيَنَّهُمْ سُبُلَنَاۜ وَاِنَّ اللّٰهَ لَمَعَ الْمُحْسِن۪ينَ', 0),
(3410, 30, 30, 1, NULL, NULL, 0, 0, NULL, 'الٓمٓ۠', 0),
(3411, 30, NULL, 2, NULL, NULL, 0, 0, NULL, 'غُلِبَتِ الرُّومُۙ', 0),
(3412, 30, NULL, 3, NULL, NULL, 0, 0, NULL, 'ف۪ٓي اَدْنَى الْاَرْضِ وَهُمْ مِنْ بَعْدِ غَلَبِهِمْ سَيَغْلِبُونَۙ', 0),
(3413, 30, NULL, 4, NULL, NULL, 0, 0, NULL, 'ف۪ي بِضْعِ سِن۪ينَۜ لِلّٰهِ الْاَمْرُ مِنْ قَبْلُ وَمِنْ بَعْدُۜ وَيَوْمَئِذٍ يَفْرَحُ الْمُؤْمِنُونَۙ', 0),
(3414, 30, NULL, 5, NULL, NULL, 0, 0, NULL, 'بِنَصْرِ اللّٰهِۜ يَنْصُرُ مَنْ يَشَٓاءُۜ وَهُوَ الْعَز۪يزُ الرَّح۪يمُ', 0),
(3415, 30, NULL, 6, 404, 4, 1, 21, NULL, 'وَعْدَ اللّٰهِۜ لَا يُخْلِفُ اللّٰهُ وَعْدَهُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 0),
(3416, 30, NULL, 7, NULL, NULL, 0, 0, NULL, 'يَعْلَمُونَ ظَاهِرًا مِنَ الْحَيٰوةِ الدُّنْيَاۚ وَهُمْ عَنِ الْاٰخِرَةِ هُمْ غَافِلُونَ', 0),
(3417, 30, NULL, 8, NULL, NULL, 0, 0, NULL, 'اَوَلَمْ يَتَفَكَّرُوا ف۪ٓي اَنْفُسِهِمْ۠ مَا خَلَقَ اللّٰهُ السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَٓا اِلَّا بِالْحَقِّ وَاَجَلٍ مُسَمًّىۜ وَاِنَّ كَث۪يرًا مِنَ النَّاسِ بِلِقَٓائِ۬ رَبِّهِمْ لَكَافِرُونَ', 0),
(3418, 30, NULL, 9, NULL, NULL, 0, 0, NULL, 'اَوَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ مِنْ قَبْلِهِمْۜ كَانُٓوا اَشَدَّ مِنْهُمْ قُوَّةً وَاَثَارُوا الْاَرْضَ وَعَمَرُوهَٓا اَكْثَرَ مِمَّا عَمَرُوهَا وَجَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِۜ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَۜ', 0),
(3419, 30, NULL, 10, NULL, NULL, 0, 0, NULL, 'ثُمَّ كَانَ عَاقِبَةَ الَّذ۪ينَ اَسَٓاؤُا السُّٓوآٰى اَنْ كَذَّبُوا بِاٰيَاتِ اللّٰهِ وَكَانُوا بِهَا يَسْتَهْزِؤُ۫نَ۟', 0),
(3420, 30, NULL, 11, NULL, NULL, 0, 0, NULL, 'اَللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُ ثُمَّ اِلَيْهِ تُرْجَعُونَ', 0),
(3421, 30, NULL, 12, NULL, NULL, 0, 0, NULL, 'وَيَوْمَ تَقُومُ السَّاعَةُ يُبْلِسُ الْمُجْرِمُونَ', 0),
(3422, 30, NULL, 13, NULL, NULL, 0, 0, NULL, 'وَلَمْ يَكُنْ لَهُمْ مِنْ شُرَكَٓائِهِمْ شُفَعٰٓؤُ۬ا وَكَانُوا بِشُرَكَٓائِهِمْ كَافِر۪ينَ', 0),
(3423, 30, NULL, 14, NULL, NULL, 0, 0, NULL, 'وَيَوْمَ تَقُومُ السَّاعَةُ يَوْمَئِذٍ يَتَفَرَّقُونَ', 0),
(3424, 30, NULL, 15, NULL, NULL, 0, 0, NULL, 'فَاَمَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَهُمْ ف۪ي رَوْضَةٍ يُحْبَرُونَ', 0),
(3425, 30, NULL, 16, 405, 5, 1, 21, NULL, 'وَاَمَّا الَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَا وَلِقَٓائِ الْاٰخِرَةِ فَاُو۬لٰٓئِكَ فِي الْعَذَابِ مُحْضَرُونَ', 0),
(3426, 30, NULL, 17, NULL, NULL, 0, 0, NULL, 'فَسُبْحَانَ اللّٰهِ ح۪ينَ تُمْسُونَ وَح۪ينَ تُصْبِحُونَ', 0),
(3427, 30, NULL, 18, NULL, NULL, 0, 0, NULL, 'وَلَهُ الْحَمْدُ فِي السَّمٰوَاتِ وَالْاَرْضِ وَعَشِيًّا وَح۪ينَ تُظْهِرُونَ', 0),
(3428, 30, NULL, 19, NULL, NULL, 0, 0, NULL, 'يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَيُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ وَكَذٰلِكَ تُخْرَجُونَ۟', 0),
(3429, 30, NULL, 20, NULL, NULL, 0, 0, NULL, 'وَمِنْ اٰيَاتِه۪ٓ اَنْ خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ اِذَٓا اَنْتُمْ بَشَرٌ تَنْتَشِرُونَ', 0),
(3430, 30, NULL, 21, NULL, NULL, 0, 0, NULL, 'وَمِنْ اٰيَاتِه۪ٓ اَنْ خَلَقَ لَكُمْ مِنْ اَنْفُسِكُمْ اَزْوَاجًا لِتَسْكُنُٓوا اِلَيْهَا وَجَعَلَ بَيْنَكُمْ مَوَدَّةً وَرَحْمَةًۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ', 0),
(3431, 30, NULL, 22, NULL, NULL, 0, 0, NULL, 'وَمِنْ اٰيَاتِه۪ خَلْقُ السَّمٰوَاتِ وَالْاَرْضِ وَاخْتِلَافُ اَلْسِنَتِكُمْ وَاَلْوَانِكُمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِلْعَالِم۪ينَ', 0),
(3432, 30, NULL, 23, NULL, NULL, 0, 0, NULL, 'وَمِنْ اٰيَاتِه۪ مَنَامُكُمْ بِالَّيْلِ وَالنَّهَارِ وَابْتِغَٓاؤُ۬كُمْ مِنْ فَضْلِه۪ۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَسْمَعُونَ', 0),
(3433, 30, NULL, 24, NULL, NULL, 0, 0, NULL, 'وَمِنْ اٰيَاتِه۪ يُر۪يكُمُ الْبَرْقَ خَوْفًا وَطَمَعًا وَيُنَزِّلُ مِنَ السَّمَٓاءِ مَٓاءً فَيُحْي۪ بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَعْقِلُونَ', 0),
(3434, 30, NULL, 25, 406, 6, 2, 21, NULL, 'وَمِنْ اٰيَاتِه۪ٓ اَنْ تَقُومَ السَّمَٓاءُ وَالْاَرْضُ بِاَمْرِه۪ۜ ثُمَّ اِذَا دَعَاكُمْ دَعْوَةً مِنَ الْاَرْضِ اِذَٓا اَنْتُمْ تَخْرُجُونَ', 0),
(3435, 30, NULL, 26, NULL, NULL, 0, 0, NULL, 'وَلَهُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِۜ كُلٌّ لَهُ قَانِتُونَ', 0),
(3436, 30, NULL, 27, NULL, NULL, 0, 0, NULL, 'وَهُوَ الَّذ۪ي يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُ وَهُوَ اَهْوَنُ عَلَيْهِۜ وَلَهُ الْمَثَلُ الْاَعْلٰى فِي السَّمٰوَاتِ وَالْاَرْضِۚ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ۟', 0),
(3437, 30, NULL, 28, NULL, NULL, 0, 0, NULL, 'ضَرَبَ لَكُمْ مَثَلًا مِنْ اَنْفُسِكُمْۜ هَلْ لَكُمْ مِنْ مَا مَلَكَتْ اَيْمَانُكُمْ مِنْ شُرَكَٓاءَ ف۪ي مَا رَزَقْنَاكُمْ فَاَنْتُمْ ف۪يهِ سَوَٓاءٌ تَخَافُونَهُمْ كَخ۪يفَتِكُمْ اَنْفُسَكُمْۜ كَذٰلِكَ نُفَصِّلُ الْاٰيَاتِ لِقَوْمٍ يَعْقِلُونَ', 0),
(3438, 30, NULL, 29, NULL, NULL, 0, 0, NULL, 'بَلِ اتَّبَعَ الَّذ۪ينَ ظَلَمُٓوا اَهْوَٓاءَهُمْ بِغَيْرِ عِلْمٍۚ فَمَنْ يَهْد۪ي مَنْ اَضَلَّ اللّٰهُۜ وَمَا لَهُمْ مِنْ نَاصِر۪ينَ', 0),
(3439, 30, NULL, 30, NULL, NULL, 0, 0, NULL, 'فَاَقِمْ وَجْهَكَ لِلدّ۪ينِ حَن۪يفًاۜ فِطْرَتَ اللّٰهِ الَّت۪ي فَطَرَ النَّاسَ عَلَيْهَاۜ لَا تَبْد۪يلَ لِخَلْقِ اللّٰهِۜ ذٰلِكَ الدّ۪ينُ الْقَيِّمُۗ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَۗ', 0),
(3440, 30, NULL, 31, NULL, NULL, 0, 0, NULL, 'مُن۪يب۪ينَ اِلَيْهِ وَاتَّقُوهُ وَاَق۪يمُوا الصَّلٰوةَ وَلَا تَكُونُوا مِنَ الْمُشْرِك۪ينَۙ', 0),
(3441, 30, NULL, 32, NULL, NULL, 0, 0, NULL, 'مِنَ الَّذ۪ينَ فَرَّقُوا د۪ينَهُمْ وَكَانُوا شِيَعًاۜ كُلُّ حِزْبٍ بِمَا لَدَيْهِمْ فَرِحُونَ', 0),
(3442, 30, NULL, 33, 407, 7, 2, 21, NULL, 'وَاِذَا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُمْ مُن۪يب۪ينَ اِلَيْهِ ثُمَّ اِذَٓا اَذَاقَهُمْ مِنْهُ رَحْمَةً اِذَا فَر۪يقٌ مِنْهُمْ بِرَبِّهِمْ يُشْرِكُونَۙ', 0),
(3443, 30, NULL, 34, NULL, NULL, 0, 0, NULL, 'لِيَكْفُرُوا بِمَٓا اٰتَيْنَاهُمْۜ فَتَمَتَّعُوا۠ فَسَوْفَ تَعْلَمُونَ', 0),
(3444, 30, NULL, 35, NULL, NULL, 0, 0, NULL, 'اَمْ اَنْزَلْنَا عَلَيْهِمْ سُلْطَانًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوا بِه۪ يُشْرِكُونَ', 0),
(3445, 30, NULL, 36, NULL, NULL, 0, 0, NULL, 'وَاِذَٓا اَذَقْنَا النَّاسَ رَحْمَةً فَرِحُوا بِهَاۜ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ بِمَا قَدَّمَتْ اَيْد۪يهِمْ اِذَا هُمْ يَقْنَطُونَ', 0),
(3446, 30, NULL, 37, NULL, NULL, 0, 0, NULL, 'اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ وَيَقْدِرُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 0),
(3447, 30, NULL, 38, NULL, NULL, 0, 0, NULL, 'فَاٰتِ ذَا الْقُرْبٰى حَقَّهُ وَالْمِسْك۪ينَ وَابْنَ السَّب۪يلِۜ ذٰلِكَ خَيْرٌ لِلَّذ۪ينَ يُر۪يدُونَ وَجْهَ اللّٰهِۘ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ', 0),
(3448, 30, NULL, 39, NULL, NULL, 0, 0, NULL, 'وَمَٓا اٰتَيْتُمْ مِنْ رِبًا لِيَرْبُوَ۬ا ف۪ٓي اَمْوَالِ النَّاسِ فَلَا يَرْبُوا عِنْدَ اللّٰهِۚ وَمَٓا اٰتَيْتُمْ مِنْ زَكٰوةٍ تُر۪يدُونَ وَجْهَ اللّٰهِ فَاُو۬لٰٓئِكَ هُمُ الْمُضْعِفُونَ', 0),
(3449, 30, NULL, 40, NULL, NULL, 0, 0, NULL, 'اَللّٰهُ الَّذ۪ي خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُم۪يتُكُمْ ثُمَّ يُحْي۪يكُمْۜ هَلْ مِنْ شُرَكَٓائِكُمْ مَنْ يَفْعَلُ مِنْ ذٰلِكُمْ مِنْ شَيْءٍۜ سُبْحَانَهُ وَتَعَالٰى عَمَّا يُشْرِكُونَ۟', 0),
(3450, 30, NULL, 41, NULL, NULL, 0, 0, NULL, 'ظَهَرَ الْفَسَادُ فِي الْبَرِّ وَالْبَحْرِ بِمَا كَسَبَتْ اَيْدِي النَّاسِ لِيُذ۪يقَهُمْ بَعْضَ الَّذ۪ي عَمِلُوا لَعَلَّهُمْ يَرْجِعُونَ', 0),
(3451, 30, NULL, 42, 408, 8, 2, 21, NULL, 'قُلْ س۪يرُوا فِي الْاَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ مِنْ قَبْلُۜ كَانَ اَكْثَرُهُمْ مُشْرِك۪ينَ', 0),
(3452, 30, NULL, 43, NULL, NULL, 0, 0, NULL, 'فَاَقِمْ وَجْهَكَ لِلدّ۪ينِ الْقَيِّمِ مِنْ قَبْلِ اَنْ يَأْتِيَ يَوْمٌ لَا مَرَدَّ لَهُ مِنَ اللّٰهِ يَوْمَئِذٍ يَصَّدَّعُونَ', 0),
(3453, 30, NULL, 44, NULL, NULL, 0, 0, NULL, 'مَنْ كَفَرَ فَعَلَيْهِ كُفْرُهُۚ وَمَنْ عَمِلَ صَالِحًا فَلِاَنْفُسِهِمْ يَمْهَدُونَۙ', 0),
(3454, 30, NULL, 45, NULL, NULL, 0, 0, NULL, 'لِيَجْزِيَ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنْ فَضْلِه۪ۜ اِنَّهُ لَا يُحِبُّ الْكَافِر۪ينَ', 0),
(3455, 30, NULL, 46, NULL, NULL, 0, 0, NULL, 'وَمِنْ اٰيَاتِه۪ٓ اَنْ يُرْسِلَ الرِّيَاحَ مُبَشِّرَاتٍ وَلِيُذ۪يقَكُمْ مِنْ رَحْمَتِه۪ وَلِتَجْرِيَ الْفُلْكُ بِاَمْرِه۪ وَلِتَبْتَغُوا مِنْ فَضْلِه۪ وَلَعَلَّكُمْ تَشْكُرُونَ', 0),
(3456, 30, NULL, 47, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ رُسُلًا اِلٰى قَوْمِهِمْ فَجَٓاؤُ۫هُمْ بِالْبَيِّنَاتِ فَانْتَقَمْنَا مِنَ الَّذ۪ينَ اَجْرَمُواۜ وَكَانَ حَقًّا عَلَيْنَا نَصْرُ الْمُؤْمِن۪ينَ', 0),
(3457, 30, NULL, 48, NULL, NULL, 0, 0, NULL, 'اَللّٰهُ الَّذ۪ي يُرْسِلُ الرِّيَاحَ فَتُث۪يرُ سَحَابًا فَيَبْسُطُهُ فِي السَّمَٓاءِ كَيْفَ يَشَٓاءُ وَيَجْعَلُهُ كِسَفًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلَالِه۪ۚ فَاِذَٓا اَصَابَ بِه۪ مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ٓ اِذَا هُمْ يَسْتَبْشِرُونَ', 0),
(3458, 30, NULL, 49, NULL, NULL, 0, 0, NULL, 'وَاِنْ كَانُوا مِنْ قَبْلِ اَنْ يُنَزَّلَ عَلَيْهِمْ مِنْ قَبْلِه۪ لَمُبْلِس۪ينَ', 0),
(3459, 30, NULL, 50, NULL, NULL, 0, 0, NULL, 'فَانْظُرْ اِلٰٓى اٰثَارِ رَحْمَتِ اللّٰهِ كَيْفَ يُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ اِنَّ ذٰلِكَ لَمُحْيِ الْمَوْتٰىۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 0),
(3460, 30, NULL, 51, 409, 9, 2, 21, NULL, 'وَلَئِنْ اَرْسَلْنَا ر۪يحًا فَرَاَوْهُ مُصْفَرًّا لَظَلُّوا مِنْ بَعْدِه۪ يَكْفُرُونَ', 0),
(3461, 30, NULL, 52, NULL, NULL, 0, 0, NULL, 'فَاِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَٓاءَ اِذَا وَلَّوْا مُدْبِر۪ينَ', 0),
(3462, 30, NULL, 53, NULL, NULL, 0, 0, NULL, 'وَمَٓا اَنْتَ بِهَادِ الْعُمْيِ عَنْ ضَلَالَتِهِمْۜ اِنْ تُسْمِعُ اِلَّا مَنْ يُؤْمِنُ بِاٰيَاتِنَا فَهُمْ مُسْلِمُونَ۟', 0),
(3463, 30, NULL, 54, NULL, NULL, 0, 0, NULL, 'اَللّٰهُ الَّذ۪ي خَلَقَكُمْ مِنْ ضَعْفٍ ثُمَّ جَعَلَ مِنْ بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِنْ بَعْدِ قُوَّةٍ ضَعْفًا وَشَيْبَةًۜ يَخْلُقُ مَا يَشَٓاءُۚ وَهُوَ الْعَل۪يمُ الْقَد۪يرُ', 0),
(3464, 30, NULL, 55, NULL, NULL, 0, 0, NULL, 'وَيَوْمَ تَقُومُ السَّاعَةُ يُقْسِمُ الْمُجْرِمُونَۙ مَا لَبِثُوا غَيْرَ سَاعَةٍۜ كَذٰلِكَ كَانُوا يُؤْفَكُونَ', 0),
(3465, 30, NULL, 56, NULL, NULL, 0, 0, NULL, 'وَقَالَ الَّذ۪ينَ اُو۫تُوا الْعِلْمَ وَالْا۪يمَانَ لَقَدْ لَبِثْتُمْ ف۪ي كِتَابِ اللّٰهِ اِلٰى يَوْمِ الْبَعْثِۘ فَهٰذَا يَوْمُ الْبَعْثِ وَلٰكِنَّكُمْ كُنْتُمْ لَا تَعْلَمُونَ', 0),
(3466, 30, NULL, 57, NULL, NULL, 0, 0, NULL, 'فَيَوْمَئِذٍ لَا يَنْفَعُ الَّذ۪ينَ ظَلَمُوا مَعْذِرَتُهُمْ وَلَا هُمْ يُسْتَعْتَبُونَ', 0),
(3467, 30, NULL, 58, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ ف۪ي هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۜ وَلَئِنْ جِئْتَهُمْ بِاٰيَةٍ لَيَقُولَنَّ الَّذ۪ينَ كَفَرُٓوا اِنْ اَنْتُمْ اِلَّا مُبْطِلُونَ', 0),
(3468, 30, NULL, 59, NULL, NULL, 0, 0, NULL, 'كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوبِ الَّذ۪ينَ لَا يَعْلَمُونَ', 0),
(3469, 30, NULL, 60, NULL, NULL, 0, 0, NULL, 'فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَلَا يَسْتَخِفَّنَّكَ الَّذ۪ينَ لَا يُوقِنُونَ', 0),
(3470, 31, 31, 1, 410, 10, 2, 21, NULL, 'الٓمٓ۠', 0),
(3471, 31, NULL, 2, NULL, NULL, 0, 0, NULL, 'تِلْكَ اٰيَاتُ الْكِتَابِ الْحَك۪يمِۙ', 0);
INSERT INTO `fkl_verse` (`id`, `sura_id`, `new_sura`, `verse_no`, `page`, `hizb_page`, `hizb`, `juz`, `new_juz`, `verse`, `sajdah`) VALUES
(3472, 31, NULL, 3, NULL, NULL, 0, 0, NULL, 'هُدًى وَرَحْمَةً لِلْمُحْسِن۪ينَۙ', 0),
(3473, 31, NULL, 4, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ينَ يُق۪يمُونَ الصَّلٰوةَ وَيُؤْتُونَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوقِنُونَۜ', 0),
(3474, 31, NULL, 5, NULL, NULL, 0, 0, NULL, 'اُو۬لٰٓئِكَ عَلٰى هُدًى مِنْ رَبِّهِمْ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ', 0),
(3475, 31, NULL, 6, NULL, NULL, 0, 0, NULL, 'وَمِنَ النَّاسِ مَنْ يَشْتَر۪ي لَهْوَ الْحَد۪يثِ لِيُضِلَّ عَنْ سَب۪يلِ اللّٰهِ بِغَيْرِ عِلْمٍۙ وَيَتَّخِذَهَا هُزُوًاۜ اُو۬لٰٓئِكَ لَهُمْ عَذَابٌ مُه۪ينٌ', 0),
(3476, 31, NULL, 7, NULL, NULL, 0, 0, NULL, 'وَاِذَا تُتْلٰى عَلَيْهِ اٰيَاتُنَا وَلّٰى مُسْتَكْبِرًا كَاَنْ لَمْ يَسْمَعْهَا كَاَنَّ ف۪ٓي اُذُنَيْهِ وَقْرًاۚ فَبَشِّرْهُ بِعَذَابٍ اَل۪يمٍ', 0),
(3477, 31, NULL, 8, NULL, NULL, 0, 0, NULL, 'اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتُ النَّع۪يمِۙ', 0),
(3478, 31, NULL, 9, NULL, NULL, 0, 0, NULL, 'خَالِد۪ينَ ف۪يهَاۜ وَعْدَ اللّٰهِ حَقًّاۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ', 0),
(3479, 31, NULL, 10, NULL, NULL, 0, 0, NULL, 'خَلَقَ السَّمٰوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا وَاَلْقٰى فِي الْاَرْضِ رَوَاسِيَ اَنْ تَم۪يدَ بِكُمْ وَبَثَّ ف۪يهَا مِنْ كُلِّ دَٓابَّةٍۜ وَاَنْزَلْنَا مِنَ السَّمَٓاءِ مَٓاءً فَاَنْبَتْنَا ف۪يهَا مِنْ كُلِّ زَوْجٍ كَر۪يمٍ', 0),
(3480, 31, NULL, 11, NULL, NULL, 0, 0, NULL, 'هٰذَا خَلْقُ اللّٰهِ فَاَرُون۪ي مَاذَا خَلَقَ الَّذ۪ينَ مِنْ دُونِه۪ۜ بَلِ الظَّالِمُونَ ف۪ي ضَلَالٍ مُب۪ينٍ۟', 0),
(3481, 31, NULL, 12, 411, 11, 2, 21, NULL, 'وَلَقَدْ اٰتَيْنَا لُقْمٰنَ الْحِكْمَةَ اَنِ اشْكُرْ لِلّٰهِۜ وَمَنْ يَشْكُرْ فَاِنَّمَا يَشْكُرُ لِنَفْسِه۪ۚ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ حَم۪يدٌ', 0),
(3482, 31, NULL, 13, NULL, NULL, 0, 0, NULL, 'وَاِذْ قَالَ لُقْمٰنُ لِابْنِه۪ وَهُوَ يَعِظُهُ يَا بُنَيَّ لَا تُشْرِكْ بِاللّٰهِۜ اِنَّ الشِّرْكَ لَظُلْمٌ عَظ۪يمٌ', 0),
(3483, 31, NULL, 14, NULL, NULL, 0, 0, NULL, 'وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِۚ حَمَلَتْهُ اُمُّهُ وَهْنًا عَلٰى وَهْنٍ وَفِصَالُهُ ف۪ي عَامَيْنِ اَنِ اشْكُرْ ل۪ي وَلِوَالِدَيْكَۜ اِلَيَّ الْمَص۪يرُ', 0),
(3484, 31, NULL, 15, NULL, NULL, 0, 0, NULL, 'وَاِنْ جَاهَدَاكَ عَلٰٓى اَنْ تُشْرِكَ ب۪ي مَا لَيْسَ لَكَ بِه۪ عِلْمٌ فَلَا تُطِعْهُمَا وَصَاحِبْهُمَا فِي الدُّنْيَا مَعْرُوفًاۘ وَاتَّبِعْ سَب۪يلَ مَنْ اَنَابَ اِلَيَّۚ ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 0),
(3485, 31, NULL, 16, NULL, NULL, 0, 0, NULL, 'يَا بُنَيَّ اِنَّهَٓا اِنْ تَكُ مِثْقَالَ حَبَّةٍ مِنْ خَرْدَلٍ فَتَكُنْ ف۪ي صَخْرَةٍ اَوْ فِي السَّمٰوَاتِ اَوْ فِي الْاَرْضِ يَأْتِ بِهَا اللّٰهُۜ اِنَّ اللّٰهَ لَط۪يفٌ خَب۪يرٌ', 0),
(3486, 31, NULL, 17, NULL, NULL, 0, 0, NULL, 'يَا بُنَيَّ اَقِمِ الصَّلٰوةَ وَأْمُرْ بِالْمَعْرُوفِ وَانْهَ عَنِ الْمُنْكَرِ وَاصْبِرْ عَلٰى مَٓا اَصَابَكَۜ اِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُورِۚ', 0),
(3487, 31, NULL, 18, NULL, NULL, 0, 0, NULL, 'وَلَا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلَا تَمْشِ فِي الْاَرْضِ مَرَحًاۜ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍۚ', 0),
(3488, 31, NULL, 19, NULL, NULL, 0, 0, NULL, 'وَاقْصِدْ ف۪ي مَشْيِكَ وَاغْضُضْ مِنْ صَوْتِكَۜ اِنَّ اَنْكَرَ الْاَصْوَاتِ لَصَوْتُ الْحَم۪يرِ۟', 0),
(3489, 31, NULL, 20, 412, 12, 3, 21, NULL, 'اَلَمْ تَرَوْا اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ وَاَسْبَغَ عَلَيْكُمْ نِعَمَهُ ظَاهِرَةً وَبَاطِنَةًۜ وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللّٰهِ بِغَيْرِ عِلْمٍ وَلَا هُدًى وَلَا كِتَابٍ مُن۪يرٍ', 0),
(3490, 31, NULL, 21, NULL, NULL, 0, 0, NULL, 'وَاِذَا ق۪يلَ لَهُمُ اتَّبِعُوا مَٓا اَنْزَلَ اللّٰهُ قَالُوا بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ اٰبَٓاءَنَاۜ اَوَلَوْ كَانَ الشَّيْطَانُ يَدْعُوهُمْ اِلٰى عَذَابِ السَّع۪يرِ', 0),
(3491, 31, NULL, 22, NULL, NULL, 0, 0, NULL, 'وَمَنْ يُسْلِمْ وَجْهَهُٓ اِلَى اللّٰهِ وَهُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰىۜ وَاِلَى اللّٰهِ عَاقِبَةُ الْاُمُورِ', 0),
(3492, 31, NULL, 23, NULL, NULL, 0, 0, NULL, 'وَمَنْ كَفَرَ فَلَا يَحْزُنْكَ كُفْرُهُۜ اِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُمْ بِمَا عَمِلُواۜ اِنَّ اللّٰهَ عَل۪يمٌ بِذَاتِ الصُّدُورِ', 0),
(3493, 31, NULL, 24, NULL, NULL, 0, 0, NULL, 'نُمَتِّعُهُمْ قَل۪يلًا ثُمَّ نَضْطَرُّهُمْ اِلٰى عَذَابٍ غَل۪يظٍ', 0),
(3494, 31, NULL, 25, NULL, NULL, 0, 0, NULL, 'وَلَئِنْ سَاَلْتَهُمْ مَنْ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ لَيَقُولُنَّ اللّٰهُۜ قُلِ الْحَمْدُ لِلّٰهِۜ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَ', 0),
(3495, 31, NULL, 26, NULL, NULL, 0, 0, NULL, 'لِلّٰهِ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ اِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَم۪يدُ', 0),
(3496, 31, NULL, 27, NULL, NULL, 0, 0, NULL, 'وَلَوْ اَنَّ مَا فِي الْاَرْضِ مِنْ شَجَرَةٍ اَقْلَامٌ وَالْبَحْرُ يَمُدُّهُ مِنْ بَعْدِه۪ سَبْعَةُ اَبْحُرٍ مَا نَفِدَتْ كَلِمَاتُ اللّٰهِۜ اِنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ', 0),
(3497, 31, NULL, 28, NULL, NULL, 0, 0, NULL, 'مَا خَلْقُكُمْ وَلَا بَعْثُكُمْ اِلَّا كَنَفْسٍ وَاحِدَةٍۜ اِنَّ اللّٰهَ سَم۪يعٌ بَص۪يرٌ', 0),
(3498, 31, NULL, 29, 413, 13, 3, 21, NULL, 'اَلَمْ تَرَ اَنَّ اللّٰهَ يُولِجُ الَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي الَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۘ كُلٌّ يَجْر۪ٓي اِلٰٓى اَجَلٍ مُسَمًّى وَاَنَّ اللّٰهَ بِمَا تَعْمَلُونَ خَب۪يرٌ', 0),
(3499, 31, NULL, 30, NULL, NULL, 0, 0, NULL, 'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُونَ مِنْ دُونِهِ الْبَاطِلُۙ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَب۪يرُ۟', 0),
(3500, 31, NULL, 31, NULL, NULL, 0, 0, NULL, 'اَلَمْ تَرَ اَنَّ الْفُلْكَ تَجْر۪ي فِي الْبَحْرِ بِنِعْمَتِ اللّٰهِ لِيُرِيَكُمْ مِنْ اٰيَاتِه۪ۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ', 0),
(3501, 31, NULL, 32, NULL, NULL, 0, 0, NULL, 'وَاِذَا غَشِيَهُمْ مَوْجٌ كَالظُّلَلِ دَعَوُا اللّٰهَ مُخْلِص۪ينَ لَهُ الدّ۪ينَۚ فَلَمَّا نَجّٰيهُمْ اِلَى الْبَرِّ فَمِنْهُمْ مُقْتَصِدٌۜ وَمَا يَجْحَدُ بِاٰيَاتِنَٓا اِلَّا كُلُّ خَتَّارٍ كَفُورٍ', 0),
(3502, 31, NULL, 33, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْ وَاخْشَوْا يَوْمًا لَا يَجْز۪ي وَالِدٌ عَنْ وَلَدِه۪ۘ وَلَا مَوْلُودٌ هُوَ جَازٍ عَنْ وَالِدِه۪ شَيْـًٔاۜ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَا۠ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُورُ', 0),
(3503, 31, NULL, 34, NULL, NULL, 0, 0, NULL, 'اِنَّ اللّٰهَ عِنْدَهُ عِلْمُ السَّاعَةِۚ وَيُنَزِّلُ الْغَيْثَۚ وَيَعْلَمُ مَا فِي الْاَرْحَامِۜ وَمَا تَدْر۪ي نَفْسٌ مَاذَا تَكْسِبُ غَدًاۜ وَمَا تَدْر۪ي نَفْسٌ بِاَيِّ اَرْضٍ تَمُوتُۜ اِنَّ اللّٰهَ عَل۪يمٌ خَب۪يرٌ', 0),
(3504, 32, 32, 1, 414, 14, 3, 21, NULL, 'الٓمٓ۠', 0),
(3505, 32, NULL, 2, NULL, NULL, 0, 0, NULL, 'تَنْز۪يلُ الْكِتَابِ لَا رَيْبَ ف۪يهِ مِنْ رَبِّ الْعَالَم۪ينَۜ', 0),
(3506, 32, NULL, 3, NULL, NULL, 0, 0, NULL, 'اَمْ يَقُولُونَ افْتَرٰيهُۚ بَلْ هُوَ الْحَقُّ مِنْ رَبِّكَ لِتُنْذِرَ قَوْمًا مَٓا اَتٰيهُمْ مِنْ نَذ۪يرٍ مِنْ قَبْلِكَ لَعَلَّهُمْ يَهْتَدُونَ', 0),
(3507, 32, NULL, 4, NULL, NULL, 0, 0, NULL, 'اَللّٰهُ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا ف۪ي سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۜ مَا لَكُمْ مِنْ دُونِه۪ مِنْ وَلِيٍّ وَلَا شَف۪يعٍۜ اَفَلَا تَتَذَكَّرُونَ', 0),
(3508, 32, NULL, 5, NULL, NULL, 0, 0, NULL, 'يُدَبِّرُ الْاَمْرَ مِنَ السَّمَٓاءِ اِلَى الْاَرْضِ ثُمَّ يَعْرُجُ اِلَيْهِ ف۪ي يَوْمٍ كَانَ مِقْدَارُهُٓ اَلْفَ سَنَةٍ مِمَّا تَعُدُّونَ', 0),
(3509, 32, NULL, 6, NULL, NULL, 0, 0, NULL, 'ذٰلِكَ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَز۪يزُ الرَّح۪يمُۙ', 0),
(3510, 32, NULL, 7, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ٓي اَحْسَنَ كُلَّ شَيْءٍ خَلَقَهُ وَبَدَاَ خَلْقَ الْاِنْسَانِ مِنْ ط۪ينٍۚ', 0),
(3511, 32, NULL, 8, NULL, NULL, 0, 0, NULL, 'ثُمَّ جَعَلَ نَسْلَهُ مِنْ سُلَالَةٍ مِنْ مَٓاءٍ مَه۪ينٍۚ', 0),
(3512, 32, NULL, 9, NULL, NULL, 0, 0, NULL, 'ثُمَّ سَوّٰيهُ وَنَفَخَ ف۪يهِ مِنْ رُوحِه۪ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٔدَةَۜ قَل۪يلًا مَا تَشْكُرُونَ', 0),
(3513, 32, NULL, 10, NULL, NULL, 0, 0, NULL, 'وَقَالُٓوا ءَاِذَا ضَلَلْنَا فِي الْاَرْضِ ءَاِنَّا لَف۪ي خَلْقٍ جَد۪يدٍۜ بَلْ هُمْ بِلِقَٓاءِ رَبِّهِمْ كَافِرُونَ', 0),
(3514, 32, NULL, 11, NULL, NULL, 0, 0, NULL, 'قُلْ يَتَوَفّٰيكُمْ مَلَكُ الْمَوْتِ الَّذ۪ي وُكِّلَ بِكُمْ ثُمَّ اِلٰى رَبِّكُمْ تُرْجَعُونَ۟', 0),
(3515, 32, NULL, 12, 415, 15, 3, 21, NULL, 'وَلَوْ تَرٰٓى اِذِ الْمُجْرِمُونَ نَاكِسُوا رُؤُ۫سِهِمْ عِنْدَ رَبِّهِمْۜ رَبَّنَٓا اَبْصَرْنَا وَسَمِعْنَا فَارْجِعْنَا نَعْمَلْ صَالِحًا اِنَّا مُوقِنُونَ', 0),
(3516, 32, NULL, 13, NULL, NULL, 0, 0, NULL, 'وَلَوْ شِئْنَا لَاٰتَيْنَا كُلَّ نَفْسٍ هُدٰيهَا وَلٰكِنْ حَقَّ الْقَوْلُ مِنّ۪ي لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَع۪ينَ', 0),
(3517, 32, NULL, 14, NULL, NULL, 0, 0, NULL, 'فَذُوقُوا بِمَا نَس۪يتُمْ لِقَٓاءَ يَوْمِكُمْ هٰذَاۚ اِنَّا نَس۪ينَاكُمْ وَذُوقُوا عَذَابَ الْخُلْدِ بِمَا كُنْتُمْ تَعْمَلُونَ', 0),
(3518, 32, NULL, 15, NULL, NULL, 0, 0, NULL, 'اِنَّمَا يُؤْمِنُ بِاٰيَاتِنَا الَّذ۪ينَ اِذَا ذُكِّرُوا بِهَا خَرُّوا سُجَّدًا وَسَبَّحُوا بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُونَ', 1),
(3519, 32, NULL, 16, NULL, NULL, 0, 0, NULL, 'تَتَجَافٰى جُنُوبُهُمْ عَنِ الْمَضَاجِعِ يَدْعُونَ رَبَّهُمْ خَوْفًا وَطَمَعًاۘ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ', 0),
(3520, 32, NULL, 17, NULL, NULL, 0, 0, NULL, 'فَلَا تَعْلَمُ نَفْسٌ مَٓا اُخْفِيَ لَهُمْ مِنْ قُرَّةِ اَعْيُنٍۚ جَزَٓاءً بِمَا كَانُوا يَعْمَلُونَ', 0),
(3521, 32, NULL, 18, NULL, NULL, 0, 0, NULL, 'اَفَمَنْ كَانَ مُؤْمِنًا كَمَنْ كَانَ فَاسِقًاۜ لَا يَسْتَوُ۫نَ', 0),
(3522, 32, NULL, 19, NULL, NULL, 0, 0, NULL, 'اَمَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ جَنَّاتُ الْمَأْوٰىۘ نُزُلًا بِمَا كَانُوا يَعْمَلُونَ', 0),
(3523, 32, NULL, 20, NULL, NULL, 0, 0, NULL, 'وَاَمَّا الَّذ۪ينَ فَسَقُوا فَمَأْوٰيهُمُ النَّارُۜ كُلَّمَٓا اَرَادُٓوا اَنْ يَخْرُجُوا مِنْهَٓا اُع۪يدُوا ف۪يهَا وَق۪يلَ لَهُمْ ذُوقُوا عَذَابَ النَّارِ الَّذ۪ي كُنْتُمْ بِه۪ تُكَذِّبُونَ', 0),
(3524, 32, NULL, 21, 416, 16, 3, 21, NULL, 'وَلَنُذ۪يقَنَّهُمْ مِنَ الْعَذَابِ الْاَدْنٰى دُونَ الْعَذَابِ الْاَكْبَرِ لَعَلَّهُمْ يَرْجِعُونَ', 0),
(3525, 32, NULL, 22, NULL, NULL, 0, 0, NULL, 'وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيَاتِ رَبِّه۪ ثُمَّ اَعْرَضَ عَنْهَاۜ اِنَّا مِنَ الْمُجْرِم۪ينَ مُنْتَقِمُونَ۟', 0),
(3526, 32, NULL, 23, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ فَلَا تَكُنْ ف۪ي مِرْيَةٍ مِنْ لِقَٓائِه۪ وَجَعَلْنَاهُ هُدًى لِبَن۪ٓي اِسْرَٓاء۪يلَۚ', 0),
(3527, 32, NULL, 24, NULL, NULL, 0, 0, NULL, 'وَجَعَلْنَا مِنْهُمْ اَئِمَّةً يَهْدُونَ بِاَمْرِنَا لَمَّا صَبَرُواۜ وَكَانُوا بِاٰيَاتِنَا يُوقِنُونَ', 0),
(3528, 32, NULL, 25, NULL, NULL, 0, 0, NULL, 'اِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كَانُوا ف۪يهِ يَخْتَلِفُونَ', 0),
(3529, 32, NULL, 26, NULL, NULL, 0, 0, NULL, 'اَوَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِنَ الْقُرُونِ يَمْشُونَ ف۪ي مَسَاكِنِهِمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍۜ اَفَلَا يَسْمَعُونَ', 0),
(3530, 32, NULL, 27, NULL, NULL, 0, 0, NULL, 'اَوَلَمْ يَرَوْا اَنَّا نَسُوقُ الْمَٓاءَ اِلَى الْاَرْضِ الْجُرُزِ فَنُخْرِجُ بِه۪ زَرْعًا تَأْكُلُ مِنْهُ اَنْعَامُهُمْ وَاَنْفُسُهُمْۜ اَفَلَا يُبْصِرُونَ', 0),
(3531, 32, NULL, 28, NULL, NULL, 0, 0, NULL, 'وَيَقُولُونَ مَتٰى هٰذَا الْفَتْحُ اِنْ كُنْتُمْ صَادِق۪ينَ', 0),
(3532, 32, NULL, 29, NULL, NULL, 0, 0, NULL, 'قُلْ يَوْمَ الْفَتْحِ لَا يَنْفَعُ الَّذ۪ينَ كَفَرُٓوا ا۪يمَانُهُمْ وَلَا هُمْ يُنْظَرُونَ', 0),
(3533, 32, NULL, 30, NULL, NULL, 0, 0, NULL, 'فَاَعْرِضْ عَنْهُمْ وَانْتَظِرْ اِنَّهُمْ مُنْتَظِرُونَ', 0),
(3534, 33, 33, 1, 417, 17, 4, 21, NULL, 'يَٓا اَيُّهَا النَّبِيُّ اتَّقِ اللّٰهَ وَلَا تُطِعِ الْكَافِر۪ينَ وَالْمُنَافِق۪ينَۜ اِنَّ اللّٰهَ كَانَ عَل۪يمًا حَك۪يمًاۙ', 0),
(3535, 33, NULL, 2, NULL, NULL, 0, 0, NULL, 'وَاتَّبِعْ مَا يُوحٰٓى اِلَيْكَ مِنْ رَبِّكَۜ اِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُونَ خَب۪يرًاۙ', 0),
(3536, 33, NULL, 3, NULL, NULL, 0, 0, NULL, 'وَتَوَكَّلْ عَلَى اللّٰهِۜ وَكَفٰى بِاللّٰهِ وَك۪يلًا', 0),
(3537, 33, NULL, 4, NULL, NULL, 0, 0, NULL, 'مَا جَعَلَ اللّٰهُ لِرَجُلٍ مِنْ قَلْبَيْنِ ف۪ي جَوْفِه۪ۚ وَمَا جَعَلَ اَزْوَاجَكُمُ الّٰٓـ۪ٔي تُظَاهِرُونَ مِنْهُنَّ اُمَّهَاتِكُمْۚ وَمَا جَعَلَ اَدْعِيَٓاءَكُمْ اَبْنَٓاءَكُمْۜ ذٰلِكُمْ قَوْلُكُمْ بِاَفْوَاهِكُمْۜ وَاللّٰهُ يَقُولُ الْحَقَّ وَهُوَ يَهْدِي السَّب۪يلَ', 0),
(3538, 33, NULL, 5, NULL, NULL, 0, 0, NULL, 'اُدْعُوهُمْ لِاٰبَٓائِهِمْ هُوَ اَقْسَطُ عِنْدَ اللّٰهِۚ فَاِنْ لَمْ تَعْلَمُٓوا اٰبَٓاءَهُمْ فَاِخْوَانُكُمْ فِي الدّ۪ينِ وَمَوَال۪يكُمْۜ وَلَيْسَ عَلَيْكُمْ جُنَاحٌ ف۪يمَٓا اَخْطَأْتُمْ بِه۪ۙ وَلٰكِنْ مَا تَعَمَّدَتْ قُلُوبُكُمْۜ وَكَانَ اللّٰهُ غَفُورًا رَح۪يمًا', 0),
(3539, 33, NULL, 6, NULL, NULL, 0, 0, NULL, 'اَلنَّبِيُّ اَوْلٰى بِالْمُؤْمِن۪ينَ مِنْ اَنْفُسِهِمْ وَاَزْوَاجُهُٓ اُمَّهَاتُهُمْۜ وَاُو۬لُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ ف۪ي كِتَابِ اللّٰهِ مِنَ الْمُؤْمِن۪ينَ وَالْمُهَاجِر۪ينَ اِلَّٓا اَنْ تَفْعَلُٓوا اِلٰٓى اَوْلِيَٓائِكُمْ مَعْرُوفًاۜ كَانَ ذٰلِكَ فِي الْكِتَابِ مَسْطُورًا', 0),
(3540, 33, NULL, 7, 418, 18, 4, 21, NULL, 'وَاِذْ اَخَذْنَا مِنَ النَّبِيّ۪نَ م۪يثَاقَهُمْ وَمِنْكَ وَمِنْ نُوحٍ وَاِبْرٰه۪يمَ وَمُوسٰى وَع۪يسَى ابْنِ مَرْيَمَۖ وَاَخَذْنَا مِنْهُمْ م۪يثَاقًا غَل۪يظًاۙ', 0),
(3541, 33, NULL, 8, NULL, NULL, 0, 0, NULL, 'لِيَسْـَٔلَ الصَّادِق۪ينَ عَنْ صِدْقِهِمْۚ وَاَعَدَّ لِلْكَافِر۪ينَ عَذَابًا اَل۪يمًا۟', 0),
(3542, 33, NULL, 9, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اذْكُرُوا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَٓاءَتْكُمْ جُنُودٌ فَاَرْسَلْنَا عَلَيْهِمْ ر۪يحًا وَجُنُودًا لَمْ تَرَوْهَاۜ وَكَانَ اللّٰهُ بِمَا تَعْمَلُونَ بَص۪يرًاۚ', 0),
(3543, 33, NULL, 10, NULL, NULL, 0, 0, NULL, 'اِذْ جَٓاؤُ۫كُمْ مِنْ فَوْقِكُمْ وَمِنْ اَسْفَلَ مِنْكُمْ وَاِذْ زَاغَتِ الْاَبْصَارُ وَبَلَغَتِ الْقُلُوبُ الْحَنَاجِرَ وَتَظُنُّونَ بِاللّٰهِ الظُّنُونَا', 0),
(3544, 33, NULL, 11, NULL, NULL, 0, 0, NULL, 'هُنَالِكَ ابْتُلِيَ الْمُؤْمِنُونَ وَزُلْزِلُوا زِلْزَالًا شَد۪يدًا', 0),
(3545, 33, NULL, 12, NULL, NULL, 0, 0, NULL, 'وَاِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ مَا وَعَدَنَا اللّٰهُ وَرَسُولُهُٓ اِلَّا غُرُورًا', 0),
(3546, 33, NULL, 13, NULL, NULL, 0, 0, NULL, 'وَاِذْ قَالَتْ طَٓائِفَةٌ مِنْهُمْ يَٓا اَهْلَ يَثْرِبَ لَا مُقَامَ لَكُمْ فَارْجِعُواۚ وَيَسْتَأْذِنُ فَر۪يقٌ مِنْهُمُ النَّبِيَّ يَقُولُونَ اِنَّ بُيُوتَنَا عَوْرَةٌ وَمَا هِيَ بِعَوْرَةٍۜ اِنْ يُر۪يدُونَ اِلَّا فِرَارًا', 0),
(3547, 33, NULL, 14, NULL, NULL, 0, 0, NULL, 'وَلَوْ دُخِلَتْ عَلَيْهِمْ مِنْ اَقْطَارِهَا ثُمَّ سُئِلُوا الْفِتْنَةَ لَاٰتَوْهَا وَمَا تَلَبَّثُوا بِهَٓا اِلَّا يَس۪يرًا', 0),
(3548, 33, NULL, 15, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ كَانُوا عَاهَدُوا اللّٰهَ مِنْ قَبْلُ لَا يُوَلُّونَ الْاَدْبَارَۜ وَكَانَ عَهْدُ اللّٰهِ مَسْؤُ۫لًا', 0),
(3549, 33, NULL, 16, 419, 19, 4, 21, NULL, 'قُلْ لَنْ يَنْفَعَكُمُ الْفِرَارُ اِنْ فَرَرْتُمْ مِنَ الْمَوْتِ اَوِ الْقَتْلِ وَاِذًا لَا تُمَتَّعُونَ اِلَّا قَل۪يلًا', 0),
(3550, 33, NULL, 17, NULL, NULL, 0, 0, NULL, 'قُلْ مَنْ ذَا الَّذ۪ي يَعْصِمُكُمْ مِنَ اللّٰهِ اِنْ اَرَادَ بِكُمْ سُٓوءًا اَوْ اَرَادَ بِكُمْ رَحْمَةًۜ وَلَا يَجِدُونَ لَهُمْ مِنْ دُونِ اللّٰهِ وَلِيًّا وَلَا نَص۪يرًا', 0),
(3551, 33, NULL, 18, NULL, NULL, 0, 0, NULL, 'قَدْ يَعْلَمُ اللّٰهُ الْمُعَوِّق۪ينَ مِنْكُمْ وَالْقَٓائِل۪ينَ لِاِخْوَانِهِمْ هَلُمَّ اِلَيْنَاۚ وَلَا يَأْتُونَ الْبَأْسَ اِلَّا قَل۪يلًاۙ', 0),
(3552, 33, NULL, 19, NULL, NULL, 0, 0, NULL, 'اَشِحَّةً عَلَيْكُمْۚ فَاِذَا جَٓاءَ الْخَوْفُ رَاَيْتَهُمْ يَنْظُرُونَ اِلَيْكَ تَدُورُ اَعْيُنُهُمْ كَالَّذ۪ي يُغْشٰى عَلَيْهِ مِنَ الْمَوْتِۚ فَاِذَا ذَهَبَ الْخَوْفُ سَلَقُوكُمْ بِاَلْسِنَةٍ حِدَادٍ اَشِحَّةً عَلَى الْخَيْرِۜ اُو۬لٰٓئِكَ لَمْ يُؤْمِنُوا فَاَحْبَطَ اللّٰهُ اَعْمَالَهُمْۜ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَس۪يرًا', 0),
(3553, 33, NULL, 20, NULL, NULL, 0, 0, NULL, 'يَحْسَبُونَ الْاَحْزَابَ لَمْ يَذْهَبُواۚ وَاِنْ يَأْتِ الْاَحْزَابُ يَوَدُّوا لَوْ اَنَّهُمْ بَادُونَ فِي الْاَعْرَابِ يَسْـَٔلُونَ عَنْ اَنْبَٓائِكُمْۜ وَلَوْ كَانُوا ف۪يكُمْ مَا قَاتَلُٓوا اِلَّا قَل۪يلًا۟', 0),
(3554, 33, NULL, 21, NULL, NULL, 0, 0, NULL, 'لَقَدْ كَانَ لَكُمْ ف۪ي رَسُولِ اللّٰهِ اُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُوا اللّٰهَ وَالْيَوْمَ الْاٰخِرَ وَذَكَرَ اللّٰهَ كَث۪يرًاۜ', 0),
(3555, 33, NULL, 22, NULL, NULL, 0, 0, NULL, 'وَلَمَّا رَاَ الْمُؤْمِنُونَ الْاَحْزَابَۙ قَالُوا هٰذَا مَا وَعَدَنَا اللّٰهُ وَرَسُولُهُ وَصَدَقَ اللّٰهُ وَرَسُولُهُۘ وَمَا زَادَهُمْ اِلَّٓا ا۪يمَانًا وَتَسْل۪يمًاۜ', 0),
(3556, 33, NULL, 23, 420, 20, 4, 21, NULL, 'مِنَ الْمُؤْمِن۪ينَ رِجَالٌ صَدَقُوا مَا عَاهَدُوا اللّٰهَ عَلَيْهِۚ فَمِنْهُمْ مَنْ قَضٰى نَحْبَهُ وَمِنْهُمْ مَنْ يَنْتَظِرُۘ وَمَا بَدَّلُوا تَبْد۪يلًاۙ', 0),
(3557, 33, NULL, 24, NULL, NULL, 0, 0, NULL, 'لِيَجْزِيَ اللّٰهُ الصَّادِق۪ينَ بِصِدْقِهِمْ وَيُعَذِّبَ الْمُنَافِق۪ينَ اِنْ شَٓاءَ اَوْ يَتُوبَ عَلَيْهِمْۜ اِنَّ اللّٰهَ كَانَ غَفُورًا رَح۪يمًاۚ', 0),
(3558, 33, NULL, 25, NULL, NULL, 0, 0, NULL, 'وَرَدَّ اللّٰهُ الَّذ۪ينَ كَفَرُوا بِغَيْظِهِمْ لَمْ يَنَالُوا خَيْرًاۜ وَكَفَى اللّٰهُ الْمُؤْمِن۪ينَ الْقِتَالَۜ وَكَانَ اللّٰهُ قَوِيًّا عَز۪يزًاۚ', 0),
(3559, 33, NULL, 26, NULL, NULL, 0, 0, NULL, 'وَاَنْزَلَ الَّذ۪ينَ ظَاهَرُوهُمْ مِنْ اَهْلِ الْكِتَابِ مِنْ صَيَاص۪يهِمْ وَقَذَفَ ف۪ي قُلُوبِهِمُ الرُّعْبَ فَر۪يقًا تَقْتُلُونَ وَتَأْسِرُونَ فَر۪يقًاۚ', 0),
(3560, 33, NULL, 27, NULL, NULL, 0, 0, NULL, 'وَاَوْرَثَكُمْ اَرْضَهُمْ وَدِيَارَهُمْ وَاَمْوَالَهُمْ وَاَرْضًا لَمْ تَطَؤُ۫هَاۜ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرًا۟', 0),
(3561, 33, NULL, 28, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا النَّبِيُّ قُلْ لِاَزْوَاجِكَ اِنْ كُنْتُنَّ تُرِدْنَ الْحَيٰوةَ الدُّنْيَا وَز۪ينَتَهَا فَتَعَالَيْنَ اُمَتِّعْكُنَّ وَاُسَرِّحْكُنَّ سَرَاحًا جَم۪يلًا', 0),
(3562, 33, NULL, 29, NULL, NULL, 0, 0, NULL, 'وَاِنْ كُنْتُنَّ تُرِدْنَ اللّٰهَ وَرَسُولَهُ وَالدَّارَ الْاٰخِرَةَ فَاِنَّ اللّٰهَ اَعَدَّ لِلْمُحْسِنَاتِ مِنْكُنَّ اَجْرًا عَظ۪يمًا', 0),
(3563, 33, NULL, 30, NULL, NULL, 0, 0, NULL, 'يَا نِسَٓاءَ النَّبِيِّ مَنْ يَأْتِ مِنْكُنَّ بِفَاحِشَةٍ مُبَيِّنَةٍ يُضَاعَفْ لَهَا الْعَذَابُ ضِعْفَيْنِۜ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَس۪يرًا', 0),
(3564, 33, NULL, 31, 421, 1, 1, 22, 22, 'وَمَنْ يَقْنُتْ مِنْكُنَّ لِلّٰهِ وَرَسُولِه۪ وَتَعْمَلْ صَالِحًا نُؤْتِهَٓا اَجْرَهَا مَرَّتَيْنِۙ وَاَعْتَدْنَا لَهَا رِزْقًا كَر۪يمًا', 0),
(3565, 33, NULL, 32, NULL, NULL, 0, 0, NULL, 'يَا نِسَٓاءَ النَّبِيِّ لَسْتُنَّ كَاَحَدٍ مِنَ النِّسَٓاءِ اِنِ اتَّقَيْتُنَّ فَلَا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذ۪ي ف۪ي قَلْبِه۪ مَرَضٌ وَقُلْنَ قَوْلًا مَعْرُوفًاۚ', 0),
(3566, 33, NULL, 33, NULL, NULL, 0, 0, NULL, 'وَقَرْنَ ف۪ي بُيُوتِكُنَّ وَلَا تَبَرَّجْنَ تَبَرُّجَ الْجَاهِلِيَّةِ الْاُو۫لٰى وَاَقِمْنَ الصَّلٰوةَ وَاٰت۪ينَ الزَّكٰوةَ وَاَطِعْنَ اللّٰهَ وَرَسُولَهُۜ اِنَّمَا يُر۪يدُ اللّٰهُ لِيُذْهِبَ عَنْكُمُ الرِّجْسَ اَهْلَ الْبَيْتِ وَيُطَهِّرَكُمْ تَطْه۪يرًاۚ', 0),
(3567, 33, NULL, 34, NULL, NULL, 0, 0, NULL, 'وَاذْكُرْنَ مَا يُتْلٰى ف۪ي بُيُوتِكُنَّ مِنْ اٰيَاتِ اللّٰهِ وَالْحِكْمَةِۜ اِنَّ اللّٰهَ كَانَ لَط۪يفًا خَب۪يرًا۟', 0),
(3568, 33, NULL, 35, NULL, NULL, 0, 0, NULL, 'اِنَّ الْمُسْلِم۪ينَ وَالْمُسْلِمَاتِ وَالْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِ وَالْقَانِت۪ينَ وَالْقَانِتَاتِ وَالصَّادِق۪ينَ وَالصَّادِقَاتِ وَالصَّابِر۪ينَ وَالصَّابِرَاتِ وَالْخَاشِع۪ينَ وَالْخَاشِعَاتِ وَالْمُتَصَدِّق۪ينَ وَالْمُتَصَدِّقَاتِ وَالصَّٓائِم۪ينَ وَالصَّٓائِمَاتِ وَالْحَافِظ۪ينَ فُرُوجَهُمْ وَالْحَافِظَاتِ وَالذَّاكِر۪ينَ اللّٰهَ كَث۪يرًا وَالذَّاكِرَاتِ اَعَدَّ اللّٰهُ لَهُمْ مَغْفِرَةً وَاَجْرًا عَظ۪يمًا', 0),
(3569, 33, NULL, 36, 422, 2, 1, 22, NULL, 'وَمَا كَانَ لِمُؤْمِنٍ وَلَا مُؤْمِنَةٍ اِذَا قَضَى اللّٰهُ وَرَسُولُهُٓ اَمْرًا اَنْ يَكُونَ لَهُمُ الْخِيَرَةُ مِنْ اَمْرِهِمْۜ وَمَنْ يَعْصِ اللّٰهَ وَرَسُولَهُ فَقَدْ ضَلَّ ضَلَالًا مُب۪ينًا', 0),
(3570, 33, NULL, 37, NULL, NULL, 0, 0, NULL, 'وَاِذْ تَقُولُ لِلَّذ۪ٓي اَنْعَمَ اللّٰهُ عَلَيْهِ وَاَنْعَمْتَ عَلَيْهِ اَمْسِكْ عَلَيْكَ زَوْجَكَ وَاتَّقِ اللّٰهَ وَتُخْف۪ي ف۪ي نَفْسِكَ مَا اللّٰهُ مُبْد۪يهِ وَتَخْشَى النَّاسَۚ وَاللّٰهُ اَحَقُّ اَنْ تَخْشٰيهُۜ فَلَمَّا قَضٰى زَيْدٌ مِنْهَا وَطَرًا زَوَّجْنَاكَهَا لِكَيْ لَا يَكُونَ عَلَى الْمُؤْمِن۪ينَ حَرَجٌ ف۪ٓي اَزْوَاجِ اَدْعِيَٓائِهِمْ اِذَا قَضَوْا مِنْهُنَّ وَطَرًاۜ وَكَانَ اَمْرُ اللّٰهِ مَفْعُولًا', 0),
(3571, 33, NULL, 38, NULL, NULL, 0, 0, NULL, 'مَا كَانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ ف۪يمَا فَرَضَ اللّٰهُ لَهُۜ سُنَّةَ اللّٰهِ فِي الَّذ۪ينَ خَلَوْا مِنْ قَبْلُۜ وَكَانَ اَمْرُ اللّٰهِ قَدَرًا مَقْدُورًاۙ', 0),
(3572, 33, NULL, 39, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ينَ يُبَلِّغُونَ رِسَالَاتِ اللّٰهِ وَيَخْشَوْنَهُ وَلَا يَخْشَوْنَ اَحَدًا اِلَّا اللّٰهَۜ وَكَفٰى بِاللّٰهِ حَس۪يبًا', 0),
(3573, 33, NULL, 40, NULL, NULL, 0, 0, NULL, 'مَا كَانَ مُحَمَّدٌ اَبَٓا اَحَدٍ مِنْ رِجَالِكُمْ وَلٰكِنْ رَسُولَ اللّٰهِ وَخَاتَمَ النَّبِيّ۪نَۜ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمًا۟', 0),
(3574, 33, NULL, 41, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اذْكُرُوا اللّٰهَ ذِكْرًا كَث۪يرًاۙ', 0),
(3575, 33, NULL, 42, NULL, NULL, 0, 0, NULL, 'وَسَبِّحُوهُ بُكْرَةً وَاَص۪يلًا', 0),
(3576, 33, NULL, 43, NULL, NULL, 0, 0, NULL, 'هُوَ الَّذ۪ي يُصَلّ۪ي عَلَيْكُمْ وَمَلٰٓئِكَتُهُ لِيُخْرِجَكُمْ مِنَ الظُّلُمَاتِ اِلَى النُّورِۜ وَكَانَ بِالْمُؤْمِن۪ينَ رَح۪يمًا', 0),
(3577, 33, NULL, 44, 423, 3, 1, 22, NULL, 'تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهُ سَلَامٌۚ وَاَعَدَّ لَهُمْ اَجْرًا كَر۪يمًا', 0),
(3578, 33, NULL, 45, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا النَّبِيُّ اِنَّٓا اَرْسَلْنَاكَ شَاهِدًا وَمُبَشِّرًا وَنَذ۪يرًاۙ', 0),
(3579, 33, NULL, 46, NULL, NULL, 0, 0, NULL, 'وَدَاعِيًا اِلَى اللّٰهِ بِاِذْنِه۪ وَسِرَاجًا مُن۪يرًا', 0),
(3580, 33, NULL, 47, NULL, NULL, 0, 0, NULL, 'وَبَشِّرِ الْمُؤْمِن۪ينَ بِاَنَّ لَهُمْ مِنَ اللّٰهِ فَضْلًا كَب۪يرًا', 0),
(3581, 33, NULL, 48, NULL, NULL, 0, 0, NULL, 'وَلَا تُطِعِ الْكَافِر۪ينَ وَالْمُنَافِق۪ينَ وَدَعْ اَذٰيهُمْ وَتَوَكَّلْ عَلَى اللّٰهِۜ وَكَفٰى بِاللّٰهِ وَك۪يلًا', 0),
(3582, 33, NULL, 49, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا نَكَحْتُمُ الْمُؤْمِنَاتِ ثُمَّ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّونَهَاۚ فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحًا جَم۪يلًا', 0),
(3583, 33, NULL, 50, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا النَّبِيُّ اِنَّٓا اَحْلَلْنَا لَكَ اَزْوَاجَكَ الّٰت۪ٓي اٰتَيْتَ اُجُورَهُنَّ وَمَا مَلَكَتْ يَم۪ينُكَ مِمَّٓا اَفَٓاءَ اللّٰهُ عَلَيْكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّاتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَالَاتِكَ الّٰت۪ي هَاجَرْنَ مَعَكَۘ وَامْرَاَةً مُؤْمِنَةً اِنْ وَهَبَتْ نَفْسَهَا لِلنَّبِيِّ اِنْ اَرَادَ النَّبِيُّ اَنْ يَسْتَنْكِحَهَاۗ خَالِصَةً لَكَ مِنْ دُونِ الْمُؤْمِن۪ينَۜ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ ف۪ٓي اَزْوَاجِهِمْ وَمَا مَلَكَتْ اَيْمَانُهُمْ لِكَيْلَا يَكُونَ عَلَيْكَ حَرَجٌۜ وَكَانَ اللّٰهُ غَفُورًا رَح۪يمًا', 0),
(3584, 33, NULL, 51, 424, 4, 1, 22, NULL, 'تُرْج۪ي مَنْ تَشَٓاءُ مِنْهُنَّ وَتُـْٔو۪ٓي اِلَيْكَ مَنْ تَشَٓاءُۜ وَمَنِ ابْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَۜ ذٰلِكَ اَدْنٰٓى اَنْ تَقَرَّ اَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَٓا اٰتَيْتَهُنَّ كُلُّهُنَّۜ وَاللّٰهُ يَعْلَمُ مَا ف۪ي قُلُوبِكُمْۜ وَكَانَ اللّٰهُ عَل۪يمًا حَل۪يمًا', 0),
(3585, 33, NULL, 52, NULL, NULL, 0, 0, NULL, 'لَا يَحِلُّ لَكَ النِّسَٓاءُ مِنْ بَعْدُ وَلَٓا اَنْ تَبَدَّلَ بِهِنَّ مِنْ اَزْوَاجٍ وَلَوْ اَعْجَبَكَ حُسْنُهُنَّ اِلَّا مَا مَلَكَتْ يَم۪ينُكَۜ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ رَق۪يبًا۟', 0),
(3586, 33, NULL, 53, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَدْخُلُوا بُيُوتَ النَّبِيِّ اِلَّٓا اَنْ يُؤْذَنَ لَكُمْ اِلٰى طَعَامٍ غَيْرَ نَاظِر۪ينَ اِنٰيهُۙ وَلٰكِنْ اِذَا دُع۪يتُمْ فَادْخُلُوا فَاِذَا طَعِمْتُمْ فَانْتَشِرُوا وَلَا مُسْتَأْنِس۪ينَ لِحَد۪يثٍۜ اِنَّ ذٰلِكُمْ كَانَ يُؤْذِي النَّبِيَّ فَيَسْتَحْي۪ مِنْكُمْۘ وَاللّٰهُ لَا يَسْتَحْي۪ مِنَ الْحَقِّۜ وَاِذَا سَاَلْتُمُوهُنَّ مَتَاعًا فَسْـَٔلُوهُنَّ مِنْ وَرَٓاءِ حِجَابٍۜ ذٰلِكُمْ اَطْهَرُ لِقُلُوبِكُمْ وَقُلُوبِهِنَّۜ وَمَا كَانَ لَكُمْ اَنْ تُؤْذُوا رَسُولَ اللّٰهِ وَلَٓا اَنْ تَنْكِحُٓوا اَزْوَاجَهُ مِنْ بَعْدِه۪ٓ اَبَدًاۜ اِنَّ ذٰلِكُمْ كَانَ عِنْدَ اللّٰهِ عَظ۪يمًا', 0),
(3587, 33, NULL, 54, NULL, NULL, 0, 0, NULL, 'اِنْ تُبْدُوا شَيْـًٔا اَوْ تُخْفُوهُ فَاِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَل۪يمًا', 0),
(3588, 33, NULL, 55, 425, 5, 2, 22, NULL, 'لَا جُنَاحَ عَلَيْهِنَّ ف۪ٓي اٰبَٓائِهِنَّ وَلَٓا اَبْنَٓائِهِنَّ وَلَٓا اِخْوَانِهِنَّ وَلَٓا اَبْنَٓاءِ اِخْوَانِهِنَّ وَلَٓا اَبْنَٓاءِ اَخَوَاتِهِنَّ وَلَا نِسَٓائِهِنَّ وَلَا مَا مَلَكَتْ اَيْمَانُهُنَّۚ وَاتَّق۪ينَ اللّٰهَۜ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَه۪يدًا', 0),
(3589, 33, NULL, 56, NULL, NULL, 0, 0, NULL, 'اِنَّ اللّٰهَ وَمَلٰٓئِكَتَهُ يُصَلُّونَ عَلَى النَّبِيِّۜ يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا صَلُّوا عَلَيْهِ وَسَلِّمُوا تَسْل۪يمًا', 0),
(3590, 33, NULL, 57, NULL, NULL, 0, 0, NULL, 'اِنَّ الَّذ۪ينَ يُؤْذُونَ اللّٰهَ وَرَسُولَهُ لَعَنَهُمُ اللّٰهُ فِي الدُّنْيَا وَالْاٰخِرَةِ وَاَعَدَّ لَهُمْ عَذَابًا مُه۪ينًا', 0),
(3591, 33, NULL, 58, NULL, NULL, 0, 0, NULL, 'وَالَّذ۪ينَ يُؤْذُونَ الْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِ بِغَيْرِ مَا اكْتَسَبُوا فَقَدِ احْتَمَلُوا بُهْتَانًا وَاِثْمًا مُب۪ينًا۟', 0),
(3592, 33, NULL, 59, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا النَّبِيُّ قُلْ لِاَزْوَاجِكَ وَبَنَاتِكَ وَنِسَٓاءِ الْمُؤْمِن۪ينَ يُدْن۪ينَ عَلَيْهِنَّ مِنْ جَلَاب۪يبِهِنَّۜ ذٰلِكَ اَدْنٰٓى اَنْ يُعْرَفْنَ فَلَا يُؤْذَيْنَۜ وَكَانَ اللّٰهُ غَفُورًا رَح۪يمًا', 0),
(3593, 33, NULL, 60, NULL, NULL, 0, 0, NULL, 'لَئِنْ لَمْ يَنْتَهِ الْمُنَافِقُونَ وَالَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ وَالْمُرْجِفُونَ فِي الْمَد۪ينَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُونَكَ ف۪يهَٓا اِلَّا قَل۪يلًاۚۛ', 0),
(3594, 33, NULL, 61, NULL, NULL, 0, 0, NULL, 'مَلْعُون۪ينَۚۛ اَيْنَ مَا ثُقِفُٓوا اُخِذُوا وَقُتِّلُوا تَقْت۪يلًا', 0),
(3595, 33, NULL, 62, NULL, NULL, 0, 0, NULL, 'سُنَّةَ اللّٰهِ فِي الَّذ۪ينَ خَلَوْا مِنْ قَبْلُۚ وَلَنْ تَجِدَ لِسُنَّةِ اللّٰهِ تَبْد۪يلًا', 0),
(3596, 33, NULL, 63, 426, 6, 2, 22, NULL, 'يَسْـَٔلُكَ النَّاسُ عَنِ السَّاعَةِۜ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِۜ وَمَا يُدْر۪يكَ لَعَلَّ السَّاعَةَ تَكُونُ قَر۪يبًا', 0),
(3597, 33, NULL, 64, NULL, NULL, 0, 0, NULL, 'اِنَّ اللّٰهَ لَعَنَ الْكَافِر۪ينَ وَاَعَدَّ لَهُمْ سَع۪يرًاۙ', 0),
(3598, 33, NULL, 65, NULL, NULL, 0, 0, NULL, 'خَالِد۪ينَ ف۪يهَٓا اَبَدًاۚ لَا يَجِدُونَ وَلِيًّا وَلَا نَص۪يرًاۚ', 0),
(3599, 33, NULL, 66, NULL, NULL, 0, 0, NULL, 'يَوْمَ تُقَلَّبُ وُجُوهُهُمْ فِي النَّارِ يَقُولُونَ يَا لَيْتَنَٓا اَطَعْنَا اللّٰهَ وَاَطَعْنَا الرَّسُولَا', 0),
(3600, 33, NULL, 67, NULL, NULL, 0, 0, NULL, 'وَقَالُوا رَبَّنَٓا اِنَّٓا اَطَعْنَا سَادَتَنَا وَكُبَرَٓاءَنَا فَاَضَلُّونَا السَّب۪يلَا', 0),
(3601, 33, NULL, 68, NULL, NULL, 0, 0, NULL, 'رَبَّنَٓا اٰتِهِمْ ضِعْفَيْنِ مِنَ الْعَذَابِ وَالْعَنْهُمْ لَعْنًا كَب۪يرًا۟', 0),
(3602, 33, NULL, 69, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَكُونُوا كَالَّذ۪ينَ اٰذَوْا مُوسٰى فَبَرَّاَهُ اللّٰهُ مِمَّا قَالُواۜ وَكَانَ عِنْدَ اللّٰهِ وَج۪يهًا', 0),
(3603, 33, NULL, 70, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ وَقُولُوا قَوْلًا سَد۪يدًاۙ', 0),
(3604, 33, NULL, 71, NULL, NULL, 0, 0, NULL, 'يُصْلِحْ لَكُمْ اَعْمَالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْۜ وَمَنْ يُطِعِ اللّٰهَ وَرَسُولَهُ فَقَدْ فَازَ فَوْزًا عَظ۪يمًا', 0),
(3605, 33, NULL, 72, NULL, NULL, 0, 0, NULL, 'اِنَّا عَرَضْنَا الْاَمَانَةَ عَلَى السَّمٰوَاتِ وَالْاَرْضِ وَالْجِبَالِ فَاَبَيْنَ اَنْ يَحْمِلْنَهَا وَاَشْفَقْنَ مِنْهَا وَحَمَلَهَا الْاِنْسَانُۜ اِنَّهُ كَانَ ظَلُومًا جَهُولًاۙ', 0),
(3606, 33, NULL, 73, NULL, NULL, 0, 0, NULL, 'لِيُعَذِّبَ اللّٰهُ الْمُنَافِق۪ينَ وَالْمُنَافِقَاتِ وَالْمُشْرِك۪ينَ وَالْمُشْرِكَاتِ وَيَتُوبَ اللّٰهُ عَلَى الْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِۜ وَكَانَ اللّٰهُ غَفُورًا رَح۪يمًا', 0),
(3607, 34, 34, 1, 427, 7, 2, 22, NULL, 'اَلْحَمْدُ لِلّٰهِ الَّذ۪ي لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ وَلَهُ الْحَمْدُ فِي الْاٰخِرَةِۜ وَهُوَ الْحَك۪يمُ الْخَب۪يرُ', 0),
(3608, 34, NULL, 2, NULL, NULL, 0, 0, NULL, 'يَعْلَمُ مَا يَلِجُ فِي الْاَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَٓاءِ وَمَا يَعْرُجُ ف۪يهَاۜ وَهُوَ الرَّح۪يمُ الْغَفُورُ', 0),
(3609, 34, NULL, 3, NULL, NULL, 0, 0, NULL, 'وَقَالَ الَّذ۪ينَ كَفَرُوا لَا تَأْت۪ينَا السَّاعَةُۜ قُلْ بَلٰى وَرَبّ۪ي لَتَأْتِيَنَّكُمْ عَالِمِ الْغَيْبِۚ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ فِي السَّمٰوَاتِ وَلَا فِي الْاَرْضِ وَلَٓا اَصْغَرُ مِنْ ذٰلِكَ وَلَٓا اَكْبَرُ اِلَّا ف۪ي كِتَابٍ مُب۪ينٍۙ', 0),
(3610, 34, NULL, 4, NULL, NULL, 0, 0, NULL, 'لِيَجْزِيَ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِۜ اُو۬لٰٓئِكَ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَر۪يمٌ', 0),
(3611, 34, NULL, 5, NULL, NULL, 0, 0, NULL, 'وَالَّذ۪ينَ سَعَوْ ف۪ٓي اٰيَاتِنَا مُعَاجِز۪ينَ اُو۬لٰٓئِكَ لَهُمْ عَذَابٌ مِنْ رِجْزٍ اَل۪يمٌۗ', 0),
(3612, 34, NULL, 6, NULL, NULL, 0, 0, NULL, 'وَيَرَى الَّذ۪ينَ اُو۫تُوا الْعِلْمَ الَّذ۪ٓي اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَ هُوَ الْحَقَّۙ وَيَهْد۪ٓي اِلٰى صِرَاطِ الْعَز۪يزِ الْحَم۪يدِ', 0),
(3613, 34, NULL, 7, NULL, NULL, 0, 0, NULL, 'وَقَالَ الَّذ۪ينَ كَفَرُوا هَلْ نَدُلُّكُمْ عَلٰى رَجُلٍ يُنَبِّئُكُمْ اِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍۙ اِنَّكُمْ لَف۪ي خَلْقٍ جَد۪يدٍۚ', 0),
(3614, 34, NULL, 8, 428, 8, 2, 22, NULL, 'اَفْتَرٰى عَلَى اللّٰهِ كَذِبًا اَمْ بِه۪ جِنَّةٌۜ بَلِ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ فِي الْعَذَابِ وَالضَّلَالِ الْبَع۪يدِ', 0),
(3615, 34, NULL, 9, NULL, NULL, 0, 0, NULL, 'اَفَلَمْ يَرَوْا اِلٰى مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْ مِنَ السَّمَٓاءِ وَالْاَرْضِۜ اِنْ نَشَأْ نَخْسِفْ بِهِمُ الْاَرْضَ اَوْ نُسْقِطْ عَلَيْهِمْ كِسَفًا مِنَ السَّمَٓاءِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِكُلِّ عَبْدٍ مُن۪يبٍ۟', 0),
(3616, 34, NULL, 10, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ اٰتَيْنَا دَاوُ۫دَ مِنَّا فَضْلًاۜ يَا جِبَالُ اَوِّب۪ي مَعَهُ وَالطَّيْرَۚ وَاَلَنَّا لَهُ الْحَد۪يدَۙ', 0),
(3617, 34, NULL, 11, NULL, NULL, 0, 0, NULL, 'اَنِ اعْمَلْ سَابِغَاتٍ وَقَدِّرْ فِي السَّرْدِ وَاعْمَلُوا صَالِحًاۜ اِنّ۪ي بِمَا تَعْمَلُونَ بَص۪يرٌ', 0),
(3618, 34, NULL, 12, NULL, NULL, 0, 0, NULL, 'وَلِسُلَيْمٰنَ الرّ۪يحَ غُدُوُّهَا شَهْرٌ وَرَوَاحُهَا شَهْرٌۚ وَاَسَلْنَا لَهُ عَيْنَ الْقِطْرِۜ وَمِنَ الْجِنِّ مَنْ يَعْمَلُ بَيْنَ يَدَيْهِ بِاِذْنِ رَبِّه۪ۜ وَمَنْ يَزِغْ مِنْهُمْ عَنْ اَمْرِنَا نُذِقْهُ مِنْ عَذَابِ السَّع۪يرِ', 0),
(3619, 34, NULL, 13, NULL, NULL, 0, 0, NULL, 'يَعْمَلُونَ لَهُ مَا يَشَٓاءُ مِنْ مَحَار۪يبَ وَتَمَاث۪يلَ وَجِفَانٍ كَالْجَوَابِ وَقُدُورٍ رَاسِيَاتٍۜ اِعْمَلُٓوا اٰلَ دَاوُ۫دَ شُكْرًاۜ وَقَل۪يلٌ مِنْ عِبَادِيَ الشَّكُورُ', 0),
(3620, 34, NULL, 14, NULL, NULL, 0, 0, NULL, 'فَلَمَّا قَضَيْنَا عَلَيْهِ الْمَوْتَ مَا دَلَّهُمْ عَلٰى مَوْتِه۪ٓ اِلَّا دَٓابَّةُ الْاَرْضِ تَأْكُلُ مِنْسَاَتَهُۚ فَلَمَّا خَرَّ تَبَيَّنَتِ الْجِنُّ اَنْ لَوْ كَانُوا يَعْلَمُونَ الْغَيْبَ مَا لَبِثُوا فِي الْعَذَابِ الْمُه۪ينِ', 0),
(3621, 34, NULL, 15, 429, 9, 2, 22, NULL, 'لَقَدْ كَانَ لِسَبَاٍ ف۪ي مَسْكَنِهِمْ اٰيَةٌۚ جَنَّتَانِ عَنْ يَم۪ينٍ وَشِمَالٍۜ كُلُوا مِنْ رِزْقِ رَبِّكُمْ وَاشْكُرُوا لَهُۜ بَلْدَةٌ طَيِّبَةٌ وَرَبٌّ غَفُورٌ', 0),
(3622, 34, NULL, 16, NULL, NULL, 0, 0, NULL, 'فَاَعْرَضُوا فَاَرْسَلْنَا عَلَيْهِمْ سَيْلَ الْعَرِمِ وَبَدَّلْنَاهُمْ بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَيْ اُكُلٍ خَمْطٍ وَاَثْلٍ وَشَيْءٍ مِنْ سِدْرٍ قَل۪يلٍ', 0),
(3623, 34, NULL, 17, NULL, NULL, 0, 0, NULL, 'ذٰلِكَ جَزَيْنَاهُمْ بِمَا كَفَرُواۜ وَهَلْ نُجَاز۪ٓي اِلَّا الْكَفُورَ', 0),
(3624, 34, NULL, 18, NULL, NULL, 0, 0, NULL, 'وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ الْقُرَى الَّت۪ي بَارَكْنَا ف۪يهَا قُرًى ظَاهِرَةً وَقَدَّرْنَا ف۪يهَا السَّيْرَۜ س۪يرُوا ف۪يهَا لَيَالِيَ وَاَيَّامًا اٰمِن۪ينَ', 0),
(3625, 34, NULL, 19, NULL, NULL, 0, 0, NULL, 'فَقَالُوا رَبَّنَا بَاعِدْ بَيْنَ اَسْفَارِنَا وَظَلَمُٓوا اَنْفُسَهُمْ فَجَعَلْنَاهُمْ اَحَاد۪يثَ وَمَزَّقْنَاهُمْ كُلَّ مُمَزَّقٍۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ', 0),
(3626, 34, NULL, 20, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ صَدَّقَ عَلَيْهِمْ اِبْل۪يسُ ظَنَّهُ فَاتَّبَعُوهُ اِلَّا فَر۪يقًا مِنَ الْمُؤْمِن۪ينَ', 0),
(3627, 34, NULL, 21, NULL, NULL, 0, 0, NULL, 'وَمَا كَانَ لَهُ عَلَيْهِمْ مِنْ سُلْطَانٍ اِلَّا لِنَعْلَمَ مَنْ يُؤْمِنُ بِالْاٰخِرَةِ مِمَّنْ هُوَ مِنْهَا ف۪ي شَكٍّۜ وَرَبُّكَ عَلٰى كُلِّ شَيْءٍ حَف۪يظٌ۟', 0),
(3628, 34, NULL, 22, NULL, NULL, 0, 0, NULL, 'قُلِ ادْعُوا الَّذ۪ينَ زَعَمْتُمْ مِنْ دُونِ اللّٰهِۚ لَا يَمْلِكُونَ مِثْقَالَ ذَرَّةٍ فِي السَّمٰوَاتِ وَلَا فِي الْاَرْضِ وَمَا لَهُمْ ف۪يهِمَا مِنْ شِرْكٍ وَمَا لَهُ مِنْهُمْ مِنْ ظَه۪يرٍ', 0),
(3629, 34, NULL, 23, 430, 10, 3, 22, NULL, 'وَلَا تَنْفَعُ الشَّفَاعَةُ عِنْدَهُٓ اِلَّا لِمَنْ اَذِنَ لَهُۜ حَتّٰٓى اِذَا فُزِّعَ عَنْ قُلُوبِهِمْ قَالُوا مَاذَاۙ قَالَ رَبُّكُمْۜ قَالُوا الْحَقَّۚ وَهُوَ الْعَلِيُّ الْكَب۪يرُ', 0),
(3630, 34, NULL, 24, NULL, NULL, 0, 0, NULL, 'قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّمٰوَاتِ وَالْاَرْضِۜ قُلِ اللّٰهُۙ وَاِنَّٓا اَوْ اِيَّاكُمْ لَعَلٰى هُدًى اَوْ ف۪ي ضَلَالٍ مُب۪ينٍ', 0),
(3631, 34, NULL, 25, NULL, NULL, 0, 0, NULL, 'قُلْ لَا تُسْـَٔلُونَ عَمَّٓا اَجْرَمْنَا وَلَا نُسْـَٔلُ عَمَّا تَعْمَلُونَ', 0),
(3632, 34, NULL, 26, NULL, NULL, 0, 0, NULL, 'قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِالْحَقِّۜ وَهُوَ الْفَتَّاحُ الْعَل۪يمُ', 0),
(3633, 34, NULL, 27, NULL, NULL, 0, 0, NULL, 'قُلْ اَرُونِيَ الَّذ۪ينَ اَلْحَقْتُمْ بِه۪ شُرَكَٓاءَ كَلَّاۜ بَلْ هُوَ اللّٰهُ الْعَز۪يزُ الْحَك۪يمُ', 0),
(3634, 34, NULL, 28, NULL, NULL, 0, 0, NULL, 'وَمَٓا اَرْسَلْنَاكَ اِلَّا كَٓافَّةً لِلنَّاسِ بَش۪يرًا وَنَذ۪يرًا وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 0),
(3635, 34, NULL, 29, NULL, NULL, 0, 0, NULL, 'وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ', 0),
(3636, 34, NULL, 30, NULL, NULL, 0, 0, NULL, 'قُلْ لَكُمْ م۪يعَادُ يَوْمٍ لَا تَسْتَأْخِرُونَ عَنْهُ سَاعَةً وَلَا تَسْتَقْدِمُونَ۟', 0),
(3637, 34, NULL, 31, NULL, NULL, 0, 0, NULL, 'وَقَالَ الَّذ۪ينَ كَفَرُوا لَنْ نُؤْمِنَ بِهٰذَا الْقُرْاٰنِ وَلَا بِالَّذ۪ي بَيْنَ يَدَيْهِۜ وَلَوْ تَرٰٓى اِذِ الظَّالِمُونَ مَوْقُوفُونَ عِنْدَ رَبِّهِمْۚ يَرْجِعُ بَعْضُهُمْ اِلٰى بَعْضٍۨ الْقَوْلَۚ يَقُولُ الَّذ۪ينَ اسْتُضْعِفُوا لِلَّذ۪ينَ اسْتَكْبَرُوا لَوْلَٓا اَنْتُمْ لَكُنَّا مُؤْمِن۪ينَ', 0),
(3638, 34, NULL, 32, 431, 11, 3, 22, NULL, 'قَالَ الَّذ۪ينَ اسْتَكْبَرُوا لِلَّذ۪ينَ اسْتُضْعِفُٓوا اَنَحْنُ صَدَدْنَاكُمْ عَنِ الْهُدٰى بَعْدَ اِذْ جَٓاءَكُمْ بَلْ كُنْتُمْ مُجْرِم۪ينَ', 0),
(3639, 34, NULL, 33, NULL, NULL, 0, 0, NULL, 'وَقَالَ الَّذ۪ينَ اسْتُضْعِفُوا لِلَّذ۪ينَ اسْتَكْبَرُوا بَلْ مَكْرُ الَّيْلِ وَالنَّهَارِ اِذْ تَأْمُرُونَنَٓا اَنْ نَكْفُرَ بِاللّٰهِ وَنَجْعَلَ لَهُٓ اَنْدَادًاۜ وَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۜ وَجَعَلْنَا الْاَغْلَالَ ف۪ٓي اَعْنَاقِ الَّذ۪ينَ كَفَرُواۜ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوا يَعْمَلُونَ', 0),
(3640, 34, NULL, 34, NULL, NULL, 0, 0, NULL, 'وَمَٓا اَرْسَلْنَا ف۪ي قَرْيَةٍ مِنْ نَذ۪يرٍ اِلَّا قَالَ مُتْرَفُوهَٓاۙ اِنَّا بِمَٓا اُرْسِلْتُمْ بِه۪ كَافِرُونَ', 0),
(3641, 34, NULL, 35, NULL, NULL, 0, 0, NULL, 'وَقَالُوا نَحْنُ اَكْثَرُ اَمْوَالًا وَاَوْلَادًاۙ وَمَا نَحْنُ بِمُعَذَّب۪ينَ', 0),
(3642, 34, NULL, 36, NULL, NULL, 0, 0, NULL, 'قُلْ اِنَّ رَبّ۪ي يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ وَيَقْدِرُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ۟', 0),
(3643, 34, NULL, 37, NULL, NULL, 0, 0, NULL, 'وَمَٓا اَمْوَالُكُمْ وَلَٓا اَوْلَادُكُمْ بِالَّت۪ي تُقَرِّبُكُمْ عِنْدَنَا زُلْفٰٓى اِلَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحًاۘ فَاُو۬لٰٓئِكَ لَهُمْ جَزَٓاءُ الضِّعْفِ بِمَا عَمِلُوا وَهُمْ فِي الْغُرُفَاتِ اٰمِنُونَ', 0),
(3644, 34, NULL, 38, NULL, NULL, 0, 0, NULL, 'وَالَّذ۪ينَ يَسْعَوْنَ ف۪ٓي اٰيَاتِنَا مُعَاجِز۪ينَ اُو۬لٰٓئِكَ فِي الْعَذَابِ مُحْضَرُونَ', 0),
(3645, 34, NULL, 39, NULL, NULL, 0, 0, NULL, 'قُلْ اِنَّ رَبّ۪ي يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ مِنْ عِبَادِه۪ وَيَقْدِرُ لَهُۜ وَمَٓا اَنْفَقْتُمْ مِنْ شَيْءٍ فَهُوَ يُخْلِفُهُۚ وَهُوَ خَيْرُ الرَّازِق۪ينَ', 0),
(3646, 34, NULL, 40, 432, 12, 3, 22, NULL, 'وَيَوْمَ يَحْشُرُهُمْ جَم۪يعًا ثُمَّ يَقُولُ لِلْمَلٰٓئِكَةِ اَهٰٓؤُ۬لَٓاءِ اِيَّاكُمْ كَانُوا يَعْبُدُونَ', 0),
(3647, 34, NULL, 41, NULL, NULL, 0, 0, NULL, 'قَالُوا سُبْحَانَكَ اَنْتَ وَلِيُّنَا مِنْ دُونِهِمْۚ بَلْ كَانُوا يَعْبُدُونَ الْجِنَّۚ اَكْثَرُهُمْ بِهِمْ مُؤْمِنُونَ', 0),
(3648, 34, NULL, 42, NULL, NULL, 0, 0, NULL, 'فَالْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَفْعًا وَلَا ضَرًّاۜ وَنَقُولُ لِلَّذ۪ينَ ظَلَمُوا ذُوقُوا عَذَابَ النَّارِ الَّت۪ي كُنْتُمْ بِهَا تُكَذِّبُونَ', 0),
(3649, 34, NULL, 43, NULL, NULL, 0, 0, NULL, 'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنَاتٍ قَالُوا مَا هٰذَٓا اِلَّا رَجُلٌ يُر۪يدُ اَنْ يَصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ اٰبَٓاؤُ۬كُمْۚ وَقَالُوا مَا هٰذَٓا اِلَّٓا اِفْكٌ مُفْتَرًىۜ وَقَالَ الَّذ۪ينَ كَفَرُوا لِلْحَقِّ لَمَّا جَٓاءَهُمْۙ اِنْ هٰذَٓا اِلَّا سِحْرٌ مُب۪ينٌ', 0),
(3650, 34, NULL, 44, NULL, NULL, 0, 0, NULL, 'وَمَٓا اٰتَيْنَاهُمْ مِنْ كُتُبٍ يَدْرُسُونَهَا وَمَٓا اَرْسَلْنَٓا اِلَيْهِمْ قَبْلَكَ مِنْ نَذ۪يرٍۜ', 0),
(3651, 34, NULL, 45, NULL, NULL, 0, 0, NULL, 'وَكَذَّبَ الَّذ۪ينَ مِنْ قَبْلِهِمْۙ وَمَا بَلَغُوا مِعْشَارَ مَٓا اٰتَيْنَاهُمْ فَكَذَّبُوا رُسُل۪ي۠ فَكَيْفَ كَانَ نَك۪يرِ۟', 0),
(3652, 34, NULL, 46, NULL, NULL, 0, 0, NULL, 'قُلْ اِنَّمَٓا اَعِظُكُمْ بِوَاحِدَةٍۚ اَنْ تَقُومُوا لِلّٰهِ مَثْنٰى وَفُرَادٰى ثُمَّ تَتَفَكَّرُوا۠ مَا بِصَاحِبِكُمْ مِنْ جِنَّةٍۜ اِنْ هُوَ اِلَّا نَذ۪يرٌ لَكُمْ بَيْنَ يَدَيْ عَذَابٍ شَد۪يدٍ', 0),
(3653, 34, NULL, 47, NULL, NULL, 0, 0, NULL, 'قُلْ مَا سَاَلْتُكُمْ مِنْ اَجْرٍ فَهُوَ لَكُمْۜ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ شَه۪يدٌ', 0),
(3654, 34, NULL, 48, NULL, NULL, 0, 0, NULL, 'قُلْ اِنَّ رَبّ۪ي يَقْذِفُ بِالْحَقِّۚ عَلَّامُ الْغُيُوبِ', 0),
(3655, 34, NULL, 49, 433, 13, 3, 22, NULL, 'قُلْ جَٓاءَ الْحَقُّ وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُع۪يدُ', 0),
(3656, 34, NULL, 50, NULL, NULL, 0, 0, NULL, 'قُلْ اِنْ ضَلَلْتُ فَاِنَّمَٓا اَضِلُّ عَلٰى نَفْس۪يۚ وَاِنِ اهْتَدَيْتُ فَبِمَا يُوح۪ٓي اِلَيَّ رَبّ۪يۜ اِنَّهُ سَم۪يعٌ قَر۪يبٌ', 0),
(3657, 34, NULL, 51, NULL, NULL, 0, 0, NULL, 'وَلَوْ تَرٰٓى اِذْ فَزِعُوا فَلَا فَوْتَ وَاُخِذُوا مِنْ مَكَانٍ قَر۪يبٍۙ', 0),
(3658, 34, NULL, 52, NULL, NULL, 0, 0, NULL, 'وَقَالُٓوا اٰمَنَّا بِه۪ۚ وَاَنّٰى لَهُمُ التَّنَاوُشُ مِنْ مَكَانٍ بَع۪يدٍۚ', 0),
(3659, 34, NULL, 53, NULL, NULL, 0, 0, NULL, 'وَقَدْ كَفَرُوا بِه۪ مِنْ قَبْلُۚ وَيَقْذِفُونَ بِالْغَيْبِ مِنْ مَكَانٍ بَع۪يدٍ', 0),
(3660, 34, NULL, 54, NULL, NULL, 0, 0, NULL, 'وَح۪يلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُونَ كَمَا فُعِلَ بِاَشْيَاعِهِمْ مِنْ قَبْلُۜ اِنَّهُمْ كَانُوا ف۪ي شَكٍّ مُر۪يبٍ', 0),
(3661, 35, 35, 1, NULL, NULL, 0, 0, NULL, 'اَلْحَمْدُ لِلّٰهِ فَاطِرِ السَّمٰوَاتِ وَالْاَرْضِ جَاعِلِ الْمَلٰٓئِكَةِ رُسُلًا اُو۬ل۪ٓي اَجْنِحَةٍ مَثْنٰى وَثُلٰثَ وَرُبَاعَۜ يَز۪يدُ فِي الْخَلْقِ مَا يَشَٓاءُۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ', 0),
(3662, 35, NULL, 2, NULL, NULL, 0, 0, NULL, 'مَا يَفْتَحِ اللّٰهُ لِلنَّاسِ مِنْ رَحْمَةٍ فَلَا مُمْسِكَ لَهَاۚ وَمَا يُمْسِكْۙ فَلَا مُرْسِلَ لَهُ مِنْ بَعْدِه۪ۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ', 0),
(3663, 35, NULL, 3, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا النَّاسُ اذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْۜ هَلْ مِنْ خَالِقٍ غَيْرُ اللّٰهِ يَرْزُقُكُمْ مِنَ السَّمَٓاءِ وَالْاَرْضِۜ لَٓا اِلٰهَ اِلَّا هُوَۘ فَاَنّٰى تُؤْفَكُونَ', 0),
(3664, 35, NULL, 4, 434, 14, 3, 22, NULL, 'وَاِنْ يُكَذِّبُوكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ', 0),
(3665, 35, NULL, 5, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا النَّاسُ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَا۠ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُورُ', 0),
(3666, 35, NULL, 6, NULL, NULL, 0, 0, NULL, 'اِنَّ الشَّيْطَانَ لَكُمْ عَدُوٌّ فَاتَّخِذُوهُ عَدُوًّاۜ اِنَّمَا يَدْعُوا حِزْبَهُ لِيَكُونُوا مِنْ اَصْحَابِ السَّع۪يرِۜ', 0),
(3667, 35, NULL, 7, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ينَ كَفَرُوا لَهُمْ عَذَابٌ شَد۪يدٌۜ وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مَغْفِرَةٌ وَاَجْرٌ كَب۪يرٌ۟', 0),
(3668, 35, NULL, 8, NULL, NULL, 0, 0, NULL, 'اَفَمَنْ زُيِّنَ لَهُ سُٓوءُ عَمَلِه۪ فَرَاٰهُ حَسَنًاۜ فَاِنَّ اللّٰهَ يُضِلُّ مَنْ يَشَٓاءُ وَيَهْد۪ي مَنْ يَشَٓاءُۘ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرَاتٍۜ اِنَّ اللّٰهَ عَل۪يمٌ بِمَا يَصْنَعُونَ', 0),
(3669, 35, NULL, 9, NULL, NULL, 0, 0, NULL, 'وَاللّٰهُ الَّذ۪ٓي اَرْسَلَ الرِّيَاحَ فَتُث۪يرُ سَحَابًا فَسُقْنَاهُ اِلٰى بَلَدٍ مَيِّتٍ فَاَحْيَيْنَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ كَذٰلِكَ النُّشُورُ', 0),
(3670, 35, NULL, 10, NULL, NULL, 0, 0, NULL, 'مَنْ كَانَ يُر۪يدُ الْعِزَّةَ فَلِلّٰهِ الْعِزَّةُ جَم۪يعًاۜ اِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهُۜ وَالَّذ۪ينَ يَمْكُرُونَ السَّيِّـَٔاتِ لَهُمْ عَذَابٌ شَد۪يدٌۜ وَمَكْرُ اُو۬لٰٓئِكَ هُوَ يَبُورُ', 0),
(3671, 35, NULL, 11, NULL, NULL, 0, 0, NULL, 'وَاللّٰهُ خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ جَعَلَكُمْ اَزْوَاجًاۜ وَمَا تَحْمِلُ مِنْ اُنْثٰى وَلَا تَضَعُ اِلَّا بِعِلْمِه۪ۜ وَمَا يُعَمَّرُ مِنْ مُعَمَّرٍ وَلَا يُنْقَصُ مِنْ عُمُرِه۪ٓ اِلَّا ف۪ي كِتَابٍۜ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَس۪يرٌ', 0),
(3672, 35, NULL, 12, 435, 15, 4, 22, NULL, 'وَمَا يَسْتَوِي الْبَحْرَانِۗ هٰذَا عَذْبٌ فُرَاتٌ سَٓائِغٌ شَرَابُهُ وَهٰذَا مِلْحٌ اُجَاجٌۜ وَمِنْ كُلٍّ تَأْكُلُونَ لَحْمًا طَرِيًّا وَتَسْتَخْرِجُونَ حِلْيَةً تَلْبَسُونَهَاۚ وَتَرَى الْفُلْكَ ف۪يهِ مَوَاخِرَ لِتَبْتَغُوا مِنْ فَضْلِه۪ وَلَعَلَّكُمْ تَشْكُرُونَ', 0),
(3673, 35, NULL, 13, NULL, NULL, 0, 0, NULL, 'يُولِجُ الَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي الَّيْلِۙ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۘ كُلٌّ يَجْر۪ي لِاَجَلٍ مُسَمًّىۜ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لَهُ الْمُلْكُۜ وَالَّذ۪ينَ تَدْعُونَ مِنْ دُونِه۪ مَا يَمْلِكُونَ مِنْ قِطْم۪يرٍۜ', 0),
(3674, 35, NULL, 14, NULL, NULL, 0, 0, NULL, 'اِنْ تَدْعُوهُمْ لَا يَسْمَعُوا دُعَٓاءَكُمْۚ وَلَوْ سَمِعُوا مَا اسْتَجَابُوا لَكُمْۜ وَيَوْمَ الْقِيٰمَةِ يَكْفُرُونَ بِشِرْكِكُمْۜ وَلَا يُنَبِّئُكَ مِثْلُ خَب۪يرٍ۟', 0),
(3675, 35, NULL, 15, NULL, NULL, 0, 0, NULL, 'يَٓا اَيُّهَا النَّاسُ اَنْتُمُ الْفُقَرَٓاءُ اِلَى اللّٰهِۚ وَاللّٰهُ هُوَ الْغَنِيُّ الْحَم۪يدُ', 0),
(3676, 35, NULL, 16, NULL, NULL, 0, 0, NULL, 'اِنْ يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَد۪يدٍۚ', 0),
(3677, 35, NULL, 17, NULL, NULL, 0, 0, NULL, 'وَمَا ذٰلِكَ عَلَى اللّٰهِ بِعَز۪يزٍ', 0),
(3678, 35, NULL, 18, NULL, NULL, 0, 0, NULL, 'وَلَا تَزِرُ وَازِرَةٌ وِزْرَ اُخْرٰىۜ وَاِنْ تَدْعُ مُثْقَلَةٌ اِلٰى حِمْلِهَا لَا يُحْمَلْ مِنْهُ شَيْءٌ وَلَوْ كَانَ ذَا قُرْبٰىۜ اِنَّمَا تُنْذِرُ الَّذ۪ينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَاَقَامُوا الصَّلٰوةَۜ وَمَنْ تَزَكّٰى فَاِنَّمَا يَتَزَكّٰى لِنَفْسِه۪ۜ وَاِلَى اللّٰهِ الْمَص۪يرُ', 0),
(3679, 35, NULL, 19, 436, 16, 4, 22, NULL, 'وَمَا يَسْتَوِي الْاَعْمٰى وَالْبَص۪يرُۙ', 0),
(3680, 35, NULL, 20, NULL, NULL, 0, 0, NULL, 'وَلَا الظُّلُمَاتُ وَلَا النُّورُۙ', 0),
(3681, 35, NULL, 21, NULL, NULL, 0, 0, NULL, 'وَلَا الظِّلُّ وَلَا الْحَرُورُۚ', 0),
(3682, 35, NULL, 22, NULL, NULL, 0, 0, NULL, 'وَمَا يَسْتَوِي الْاَحْيَٓاءُ وَلَا الْاَمْوَاتُۜ اِنَّ اللّٰهَ يُسْمِعُ مَنْ يَشَٓاءُۚ وَمَٓا اَنْتَ بِمُسْمِعٍ مَنْ فِي الْقُبُورِ', 0),
(3683, 35, NULL, 23, NULL, NULL, 0, 0, NULL, 'اِنْ اَنْتَ اِلَّا نَذ۪يرٌ', 0),
(3684, 35, NULL, 24, NULL, NULL, 0, 0, NULL, 'اِنَّٓا اَرْسَلْنَاكَ بِالْحَقِّ بَش۪يرًا وَنَذ۪يرًاۜ وَاِنْ مِنْ اُمَّةٍ اِلَّا خَلَا ف۪يهَا نَذ۪يرٌ', 0),
(3685, 35, NULL, 25, NULL, NULL, 0, 0, NULL, 'وَاِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَ الَّذ۪ينَ مِنْ قَبْلِهِمْۚ جَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ وَبِالزُّبُرِ وَبِالْكِتَابِ الْمُن۪يرِ', 0),
(3686, 35, NULL, 26, NULL, NULL, 0, 0, NULL, 'ثُمَّ اَخَذْتُ الَّذ۪ينَ كَفَرُوا فَكَيْفَ كَانَ نَك۪يرِ۟', 0),
(3687, 35, NULL, 27, NULL, NULL, 0, 0, NULL, 'اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءًۚ فَاَخْرَجْنَا بِه۪ ثَمَرَاتٍ مُخْتَلِفًا اَلْوَانُهَاۜ وَمِنَ الْجِبَالِ جُدَدٌ ب۪يضٌ وَحُمْرٌ مُخْتَلِفٌ اَلْوَانُهَا وَغَرَاب۪يبُ سُودٌ', 0),
(3688, 35, NULL, 28, NULL, NULL, 0, 0, NULL, 'وَمِنَ النَّاسِ وَالدَّوَٓابِّ وَالْاَنْعَامِ مُخْتَلِفٌ اَلْوَانُهُ كَذٰلِكَۜ اِنَّمَا يَخْشَى اللّٰهَ مِنْ عِبَادِهِ الْعُلَمٰٓؤُ۬اۜ اِنَّ اللّٰهَ عَز۪يزٌ غَفُورٌ', 0),
(3689, 35, NULL, 29, NULL, NULL, 0, 0, NULL, 'اِنَّ الَّذ۪ينَ يَتْلُونَ كِتَابَ اللّٰهِ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلَانِيَةً يَرْجُونَ تِجَارَةً لَنْ تَبُورَۙ', 0),
(3690, 35, NULL, 30, NULL, NULL, 0, 0, NULL, 'لِيُوَفِّيَهُمْ اُجُورَهُمْ وَيَز۪يدَهُمْ مِنْ فَضْلِه۪ۜ اِنَّهُ غَفُورٌ شَكُورٌ', 0),
(3691, 35, NULL, 31, 437, 17, 4, 22, NULL, 'وَالَّذ۪ٓي اَوْحَيْنَٓا اِلَيْكَ مِنَ الْكِتَابِ هُوَ الْحَقُّ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِۜ اِنَّ اللّٰهَ بِعِبَادِه۪ لَخَب۪يرٌ بَص۪يرٌ', 0),
(3692, 35, NULL, 32, NULL, NULL, 0, 0, NULL, 'ثُمَّ اَوْرَثْنَا الْكِتَابَ الَّذ۪ينَ اصْطَفَيْنَا مِنْ عِبَادِنَاۚ فَمِنْهُمْ ظَالِمٌ لِنَفْسِه۪ۚ وَمِنْهُمْ مُقْتَصِدٌۚ وَمِنْهُمْ سَابِقٌ بِالْخَيْرَاتِ بِاِذْنِ اللّٰهِۜ ذٰلِكَ هُوَ الْفَضْلُ الْكَب۪يرُۜ', 0),
(3693, 35, NULL, 33, NULL, NULL, 0, 0, NULL, 'جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا يُحَلَّوْنَ ف۪يهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَلُؤْلُؤً۬اۚ وَلِبَاسُهُمْ ف۪يهَا حَر۪يرٌ', 0),
(3694, 35, NULL, 34, NULL, NULL, 0, 0, NULL, 'وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذ۪ٓي اَذْهَبَ عَنَّا الْحَزَنَۜ اِنَّ رَبَّنَا لَغَفُورٌ شَكُورٌۙ', 0),
(3695, 35, NULL, 35, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ٓي اَحَلَّنَا دَارَ الْمُقَامَةِ مِنْ فَضْلِه۪ۚ لَا يَمَسُّنَا ف۪يهَا نَصَبٌ وَلَا يَمَسُّنَا ف۪يهَا لُغُوبٌ', 0),
(3696, 35, NULL, 36, NULL, NULL, 0, 0, NULL, 'وَالَّذ۪ينَ كَفَرُوا لَهُمْ نَارُ جَهَنَّمَۚ لَا يُقْضٰى عَلَيْهِمْ فَيَمُوتُوا وَلَا يُخَفَّفُ عَنْهُمْ مِنْ عَذَابِهَاۜ كَذٰلِكَ نَجْز۪ي كُلَّ كَفُورٍۚ', 0),
(3697, 35, NULL, 37, NULL, NULL, 0, 0, NULL, 'وَهُمْ يَصْطَرِخُونَ ف۪يهَاۚ رَبَّنَٓا اَخْرِجْنَا نَعْمَلْ صَالِحًا غَيْرَ الَّذ۪ي كُنَّا نَعْمَلُۜ اَوَلَمْ نُعَمِّرْكُمْ مَا يَتَذَكَّرُ ف۪يهِ مَنْ تَذَكَّرَ وَجَٓاءَكُمُ النَّذ۪يرُۜ فَذُوقُوا فَمَا لِلظَّالِم۪ينَ مِنْ نَص۪يرٍ۟', 0),
(3698, 35, NULL, 38, NULL, NULL, 0, 0, NULL, 'اِنَّ اللّٰهَ عَالِمُ غَيْبِ السَّمٰوَاتِ وَالْاَرْضِۜ اِنَّهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ', 0),
(3699, 35, NULL, 39, 438, 18, 4, 22, NULL, 'هُوَ الَّذ۪ي جَعَلَكُمْ خَلَٓائِفَ فِي الْاَرْضِۜ فَمَنْ كَفَرَ فَعَلَيْهِ كُفْرُهُۜ وَلَا يَز۪يدُ الْكَافِر۪ينَ كُفْرُهُمْ عِنْدَ رَبِّهِمْ اِلَّا مَقْتًاۚ وَلَا يَز۪يدُ الْكَافِر۪ينَ كُفْرُهُمْ اِلَّا خَسَارًا', 0),
(3700, 35, NULL, 40, NULL, NULL, 0, 0, NULL, 'قُلْ اَرَاَيْتُمْ شُرَكَٓاءَكُمُ الَّذ۪ينَ تَدْعُونَ مِنْ دُونِ اللّٰهِۜ اَرُون۪ي مَاذَا خَلَقُوا مِنَ الْاَرْضِ اَمْ لَهُمْ شِرْكٌ فِي السَّمٰوَاتِۚ اَمْ اٰتَيْنَاهُمْ كِتَابًا فَهُمْ عَلٰى بَيِّنَتٍ مِنْهُۚ بَلْ اِنْ يَعِدُ الظَّالِمُونَ بَعْضُهُمْ بَعْضًا اِلَّا غُرُورًا', 0),
(3701, 35, NULL, 41, NULL, NULL, 0, 0, NULL, 'اِنَّ اللّٰهَ يُمْسِكُ السَّمٰوَاتِ وَالْاَرْضَ اَنْ تَزُولَاۚ وَلَئِنْ زَالَتَٓا اِنْ اَمْسَكَهُمَا مِنْ اَحَدٍ مِنْ بَعْدِه۪ۜ اِنَّهُ كَانَ حَل۪يمًا غَفُورًا', 0),
(3702, 35, NULL, 42, NULL, NULL, 0, 0, NULL, 'وَاَقْسَمُوا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَئِنْ جَٓاءَهُمْ نَذ۪يرٌ لَيَكُونُنَّ اَهْدٰى مِنْ اِحْدَى الْاُمَمِۚ فَلَمَّا جَٓاءَهُمْ نَذ۪يرٌ مَا زَادَهُمْ اِلَّا نُفُورًاۙ', 0),
(3703, 35, NULL, 43, NULL, NULL, 0, 0, NULL, 'اِسْتِكْبَارًا فِي الْاَرْضِ وَمَكْرَ السَّيِّئِۜ وَلَا يَح۪يقُ الْمَكْرُ السَّيِّئُ اِلَّا بِاَهْلِه۪ۜ فَهَلْ يَنْظُرُونَ اِلَّا سُنَّتَ الْاَوَّل۪ينَۚ فَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَبْد۪يلًاۚ وَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَحْو۪يلًا', 0),
(3704, 35, NULL, 44, NULL, NULL, 0, 0, NULL, 'اَوَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ مِنْ قَبْلِهِمْ وَكَانُٓوا اَشَدَّ مِنْهُمْ قُوَّةًۜ وَمَا كَانَ اللّٰهُ لِيُعْجِزَهُ مِنْ شَيْءٍ فِي السَّمٰوَاتِ وَلَا فِي الْاَرْضِۜ اِنَّهُ كَانَ عَل۪يمًا قَد۪يرًا', 0),
(3705, 35, NULL, 45, 439, 19, 4, 22, NULL, 'وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِمَا كَسَبُوا مَا تَرَكَ عَلٰى ظَهْرِهَا مِنْ دَٓابَّةٍ وَلٰكِنْ يُؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُسَمًّىۚ فَاِذَا جَٓاءَ اَجَلُهُمْ فَاِنَّ اللّٰهَ كَانَ بِعِبَادِه۪ بَص۪يرًا', 0),
(3706, 36, 36, 1, NULL, NULL, 0, 0, NULL, 'يٰسٓۜ', 0),
(3707, 36, NULL, 2, NULL, NULL, 0, 0, NULL, 'وَالْقُرْاٰنِ الْحَك۪يمِۙ', 0),
(3708, 36, NULL, 3, NULL, NULL, 0, 0, NULL, 'اِنَّكَ لَمِنَ الْمُرْسَل۪ينَۙ', 0),
(3709, 36, NULL, 4, NULL, NULL, 0, 0, NULL, 'عَلٰى صِرَاطٍ مُسْتَق۪يمٍۜ', 0),
(3710, 36, NULL, 5, NULL, NULL, 0, 0, NULL, 'تَنْز۪يلَ الْعَز۪يزِ الرَّح۪يمِۙ', 0),
(3711, 36, NULL, 6, NULL, NULL, 0, 0, NULL, 'لِتُنْذِرَ قَوْمًا مَٓا اُنْذِرَ اٰبَٓاؤُ۬هُمْ فَهُمْ غَافِلُونَ', 0),
(3712, 36, NULL, 7, NULL, NULL, 0, 0, NULL, 'لَقَدْ حَقَّ الْقَوْلُ عَلٰٓى اَكْثَرِهِمْ فَهُمْ لَا يُؤْمِنُونَ', 0),
(3713, 36, NULL, 8, NULL, NULL, 0, 0, NULL, 'اِنَّا جَعَلْنَا ف۪ٓي اَعْنَاقِهِمْ اَغْلَالًا فَهِيَ اِلَى الْاَذْقَانِ فَهُمْ مُقْمَحُونَ', 0),
(3714, 36, NULL, 9, NULL, NULL, 0, 0, NULL, 'وَجَعَلْنَا مِنْ بَيْنِ اَيْد۪يهِمْ سَدًّا وَمِنْ خَلْفِهِمْ سَدًّا فَاَغْشَيْنَاهُمْ فَهُمْ لَا يُبْصِرُونَ', 0),
(3715, 36, NULL, 10, NULL, NULL, 0, 0, NULL, 'وَسَوَٓاءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُونَ', 0),
(3716, 36, NULL, 11, NULL, NULL, 0, 0, NULL, 'اِنَّمَا تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمٰنَ بِالْغَيْبِۚ فَبَشِّرْهُ بِمَغْفِرَةٍ وَاَجْرٍ كَر۪يمٍ', 0),
(3717, 36, NULL, 12, NULL, NULL, 0, 0, NULL, 'اِنَّا نَحْنُ نُحْيِ الْمَوْتٰى وَنَكْتُبُ مَا قَدَّمُوا وَاٰثَارَهُمْۜ وَكُلَّ شَيْءٍ اَحْصَيْنَاهُ ف۪ٓي اِمَامٍ مُب۪ينٍ۟', 0),
(3718, 36, NULL, 13, 440, 20, 4, 22, NULL, 'وَاضْرِبْ لَهُمْ مَثَلًا اَصْحَابَ الْقَرْيَةِۢ اِذْ جَٓاءَهَا الْمُرْسَلُونَۚ', 0),
(3719, 36, NULL, 14, NULL, NULL, 0, 0, NULL, 'اِذْ اَرْسَلْنَٓا اِلَيْهِمُ اثْنَيْنِ فَكَذَّبُوهُمَا فَعَزَّزْنَا بِثَالِثٍ فَقَالُٓوا اِنَّٓا اِلَيْكُمْ مُرْسَلُونَ', 0),
(3720, 36, NULL, 15, NULL, NULL, 0, 0, NULL, 'قَالُوا مَٓا اَنْتُمْ اِلَّا بَشَرٌ مِثْلُنَاۙ وَمَٓا اَنْزَلَ الرَّحْمٰنُ مِنْ شَيْءٍۙ اِنْ اَنْتُمْ اِلَّا تَكْذِبُونَ', 0),
(3721, 36, NULL, 16, NULL, NULL, 0, 0, NULL, 'قَالُوا رَبُّنَا يَعْلَمُ اِنَّٓا اِلَيْكُمْ لَمُرْسَلُونَ', 0),
(3722, 36, NULL, 17, NULL, NULL, 0, 0, NULL, 'وَمَا عَلَيْنَٓا اِلَّا الْبَلَاغُ الْمُب۪ينُ', 0),
(3723, 36, NULL, 18, NULL, NULL, 0, 0, NULL, 'قَالُٓوا اِنَّا تَطَيَّرْنَا بِكُمْۚ لَئِنْ لَمْ تَنْتَهُوا لَنَرْجُمَنَّكُمْ وَلَيَمَسَّنَّكُمْ مِنَّا عَذَابٌ اَل۪يمٌ', 0),
(3724, 36, NULL, 19, NULL, NULL, 0, 0, NULL, 'قَالُوا طَٓائِرُكُمْ مَعَكُمْۜ اَئِنْ ذُكِّرْتُمْۜ بَلْ اَنْتُمْ قَوْمٌ مُسْرِفُونَ', 0),
(3725, 36, NULL, 20, NULL, NULL, 0, 0, NULL, 'وَجَٓاءَ مِنْ اَقْصَا الْمَد۪ينَةِ رَجُلٌ يَسْعٰى قَالَ يَا قَوْمِ اتَّبِعُوا الْمُرْسَل۪ينَۙ', 0),
(3726, 36, NULL, 21, NULL, NULL, 0, 0, NULL, 'اِتَّبِعُوا مَنْ لَا يَسْـَٔلُكُمْ اَجْرًا وَهُمْ مُهْتَدُونَ', 0),
(3727, 36, NULL, 22, NULL, NULL, 0, 0, NULL, 'وَمَا لِيَ لَٓا اَعْبُدُ الَّذ۪ي فَطَرَن۪ي وَاِلَيْهِ تُرْجَعُونَ', 0),
(3728, 36, NULL, 23, NULL, NULL, 0, 0, NULL, 'ءَاَتَّخِذُ مِنْ دُونِه۪ٓ اٰلِهَةً اِنْ يُرِدْنِ الرَّحْمٰنُ بِضُرٍّ لَا تُغْنِ عَنّ۪ي شَفَاعَتُهُمْ شَيْـًٔا وَلَا يُنْقِذُونِۚ', 0),
(3729, 36, NULL, 24, NULL, NULL, 0, 0, NULL, 'اِنّ۪ٓي اِذًا لَف۪ي ضَلَالٍ مُب۪ينٍ', 0),
(3730, 36, NULL, 25, NULL, NULL, 0, 0, NULL, 'اِنّ۪ٓي اٰمَنْتُ بِرَبِّكُمْ فَاسْمَعُونِۜ', 0),
(3731, 36, NULL, 26, NULL, NULL, 0, 0, NULL, 'ق۪يلَ ادْخُلِ الْجَنَّةَۜ قَالَ يَا لَيْتَ قَوْم۪ي يَعْلَمُونَۙ', 0),
(3732, 36, NULL, 27, NULL, NULL, 0, 0, NULL, 'بِمَا غَفَرَ ل۪ي رَبّ۪ي وَجَعَلَن۪ي مِنَ الْمُكْرَم۪ينَ', 0),
(3733, 36, NULL, 28, 441, 1, 1, 23, 23, 'وَمَٓا اَنْزَلْنَا عَلٰى قَوْمِه۪ مِنْ بَعْدِه۪ مِنْ جُنْدٍ مِنَ السَّمَٓاءِ وَمَا كُنَّا مُنْزِل۪ينَ', 0),
(3734, 36, NULL, 29, NULL, NULL, 0, 0, NULL, 'اِنْ كَانَتْ اِلَّا صَيْحَةً وَاحِدَةً فَاِذَا هُمْ خَامِدُونَ', 0),
(3735, 36, NULL, 30, NULL, NULL, 0, 0, NULL, 'يَا حَسْرَةً عَلَى الْعِبَادِۚ مَا يَأْت۪يهِمْ مِنْ رَسُولٍ اِلَّا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ', 0);
INSERT INTO `fkl_verse` (`id`, `sura_id`, `new_sura`, `verse_no`, `page`, `hizb_page`, `hizb`, `juz`, `new_juz`, `verse`, `sajdah`) VALUES
(3736, 36, NULL, 31, NULL, NULL, 0, 0, NULL, 'اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا قَبْلَهُمْ مِنَ الْقُرُونِ اَنَّهُمْ اِلَيْهِمْ لَا يَرْجِعُونَ', 0),
(3737, 36, NULL, 32, NULL, NULL, 0, 0, NULL, 'وَاِنْ كُلٌّ لَمَّا جَم۪يعٌ لَدَيْنَا مُحْضَرُونَ۟', 0),
(3738, 36, NULL, 33, NULL, NULL, 0, 0, NULL, 'وَاٰيَةٌ لَهُمُ الْاَرْضُ الْمَيْتَةُۚ اَحْيَيْنَاهَا وَاَخْرَجْنَا مِنْهَا حَبًّا فَمِنْهُ يَأْكُلُونَ', 0),
(3739, 36, NULL, 34, NULL, NULL, 0, 0, NULL, 'وَجَعَلْنَا ف۪يهَا جَنَّاتٍ مِنْ نَخ۪يلٍ وَاَعْنَابٍ وَفَجَّرْنَا ف۪يهَا مِنَ الْعُيُونِۙ', 0),
(3740, 36, NULL, 35, NULL, NULL, 0, 0, NULL, 'لِيَأْكُلُوا مِنْ ثَمَرِه۪ۙ وَمَا عَمِلَتْهُ اَيْد۪يهِمْۜ اَفَلَا يَشْكُرُونَ', 0),
(3741, 36, NULL, 36, NULL, NULL, 0, 0, NULL, 'سُبْحَانَ الَّذ۪ي خَلَقَ الْاَزْوَاجَ كُلَّهَا مِمَّا تُنْبِتُ الْاَرْضُ وَمِنْ اَنْفُسِهِمْ وَمِمَّا لَا يَعْلَمُونَ', 0),
(3742, 36, NULL, 37, NULL, NULL, 0, 0, NULL, 'وَاٰيَةٌ لَهُمُ الَّيْلُۚ نَسْلَخُ مِنْهُ النَّهَارَ فَاِذَا هُمْ مُظْلِمُونَۙ', 0),
(3743, 36, NULL, 38, NULL, NULL, 0, 0, NULL, 'وَالشَّمْسُ تَجْر۪ي لِمُسْتَقَرٍّ لَهَاۜ ذٰلِكَ تَقْد۪يرُ الْعَز۪يزِ الْعَل۪يمِۜ', 0),
(3744, 36, NULL, 39, NULL, NULL, 0, 0, NULL, 'وَالْقَمَرَ قَدَّرْنَاهُ مَنَازِلَ حَتّٰى عَادَ كَالْعُرْجُونِ الْقَد۪يمِ', 0),
(3745, 36, NULL, 40, NULL, NULL, 0, 0, NULL, 'لَا الشَّمْسُ يَنْبَغ۪ي لَهَٓا اَنْ تُدْرِكَ الْقَمَرَ وَلَا الَّيْلُ سَابِقُ النَّهَارِۜ وَكُلٌّ ف۪ي فَلَكٍ يَسْبَحُونَ', 0),
(3746, 36, NULL, 41, 442, 2, 1, 23, NULL, 'وَاٰيَةٌ لَهُمْ اَنَّا حَمَلْنَا ذُرِّيَّتَهُمْ فِي الْفُلْكِ الْمَشْحُونِۙ', 0),
(3747, 36, NULL, 42, NULL, NULL, 0, 0, NULL, 'وَخَلَقْنَا لَهُمْ مِنْ مِثْلِه۪ مَا يَرْكَبُونَ', 0),
(3748, 36, NULL, 43, NULL, NULL, 0, 0, NULL, 'وَاِنْ نَشَأْ نُغْرِقْهُمْ فَلَا صَر۪يخَ لَهُمْ وَلَا هُمْ يُنْقَذُونَۙ', 0),
(3749, 36, NULL, 44, NULL, NULL, 0, 0, NULL, 'اِلَّا رَحْمَةً مِنَّا وَمَتَاعًا اِلٰى ح۪ينٍ', 0),
(3750, 36, NULL, 45, NULL, NULL, 0, 0, NULL, 'وَاِذَا ق۪يلَ لَهُمُ اتَّقُوا مَا بَيْنَ اَيْد۪يكُمْ وَمَا خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُونَ', 0),
(3751, 36, NULL, 46, NULL, NULL, 0, 0, NULL, 'وَمَا تَأْت۪يهِمْ مِنْ اٰيَةٍ مِنْ اٰيَاتِ رَبِّهِمْ اِلَّا كَانُوا عَنْهَا مُعْرِض۪ينَ', 0),
(3752, 36, NULL, 47, NULL, NULL, 0, 0, NULL, 'وَاِذَا ق۪يلَ لَهُمْ اَنْفِقُوا مِمَّا رَزَقَكُمُ اللّٰهُۙ قَالَ الَّذ۪ينَ كَفَرُوا لِلَّذ۪ينَ اٰمَنُٓوا اَنُطْعِمُ مَنْ لَوْ يَشَٓاءُ اللّٰهُ اَطْعَمَهُۗ اِنْ اَنْتُمْ اِلَّا ف۪ي ضَلَالٍ مُب۪ينٍ', 0),
(3753, 36, NULL, 48, NULL, NULL, 0, 0, NULL, 'وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ', 0),
(3754, 36, NULL, 49, NULL, NULL, 0, 0, NULL, 'مَا يَنْظُرُونَ اِلَّا صَيْحَةً وَاحِدَةً تَأْخُذُهُمْ وَهُمْ يَخِصِّمُونَ', 0),
(3755, 36, NULL, 50, NULL, NULL, 0, 0, NULL, 'فَلَا يَسْتَط۪يعُونَ تَوْصِيَةً وَلَٓا اِلٰٓى اَهْلِهِمْ يَرْجِعُونَ۟', 0),
(3756, 36, NULL, 51, NULL, NULL, 0, 0, NULL, 'وَنُفِخَ فِي الصُّورِ فَاِذَا هُمْ مِنَ الْاَجْدَاثِ اِلٰى رَبِّهِمْ يَنْسِلُونَ', 0),
(3757, 36, NULL, 52, NULL, NULL, 0, 0, NULL, 'قَالُوا يَا وَيْلَنَا مَنْ بَعَثَنَا مِنْ مَرْقَدِنَاۢ هٰذَا مَا وَعَدَ الرَّحْمٰنُ وَصَدَقَ الْمُرْسَلُونَ', 0),
(3758, 36, NULL, 53, NULL, NULL, 0, 0, NULL, 'اِنْ كَانَتْ اِلَّا صَيْحَةً وَاحِدَةً فَاِذَا هُمْ جَم۪يعٌ لَدَيْنَا مُحْضَرُونَ', 0),
(3759, 36, NULL, 54, NULL, NULL, 0, 0, NULL, 'فَالْيَوْمَ لَا تُظْلَمُ نَفْسٌ شَيْـًٔا وَلَا تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُونَ', 0),
(3760, 36, NULL, 55, 443, 3, 1, 23, NULL, 'اِنَّ اَصْحَابَ الْجَنَّةِ الْيَوْمَ ف۪ي شُغُلٍ فَاكِهُونَۚ', 0),
(3761, 36, NULL, 56, NULL, NULL, 0, 0, NULL, 'هُمْ وَاَزْوَاجُهُمْ ف۪ي ظِلَالٍ عَلَى الْاَرَٓائِكِ مُتَّكِؤُ۫نَ', 0),
(3762, 36, NULL, 57, NULL, NULL, 0, 0, NULL, 'لَهُمْ ف۪يهَا فَاكِهَةٌ وَلَهُمْ مَا يَدَّعُونَۚ', 0),
(3763, 36, NULL, 58, NULL, NULL, 0, 0, NULL, 'سَلَامٌ قَوْلًا مِنْ رَبٍّ رَح۪يمٍ', 0),
(3764, 36, NULL, 59, NULL, NULL, 0, 0, NULL, 'وَامْتَازُوا الْيَوْمَ اَيُّهَا الْمُجْرِمُونَ', 0),
(3765, 36, NULL, 60, NULL, NULL, 0, 0, NULL, 'اَلَمْ اَعْهَدْ اِلَيْكُمْ يَا بَن۪ٓي اٰدَمَ اَنْ لَا تَعْبُدُوا الشَّيْطَانَۚ اِنَّهُ لَكُمْ عَدُوٌّ مُب۪ينٌۙ', 0),
(3766, 36, NULL, 61, NULL, NULL, 0, 0, NULL, 'وَاَنِ اعْبُدُون۪يۜ هٰذَا صِرَاطٌ مُسْتَق۪يمٌ', 0),
(3767, 36, NULL, 62, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ اَضَلَّ مِنْكُمْ جِبِلًّا كَث۪يرًاۜ اَفَلَمْ تَكُونُوا تَعْقِلُونَ', 0),
(3768, 36, NULL, 63, NULL, NULL, 0, 0, NULL, 'هٰذِه۪ جَهَنَّمُ الَّت۪ي كُنْتُمْ تُوعَدُونَ', 0),
(3769, 36, NULL, 64, NULL, NULL, 0, 0, NULL, 'اِصْلَوْهَا الْيَوْمَ بِمَا كُنْتُمْ تَكْفُرُونَ', 0),
(3770, 36, NULL, 65, NULL, NULL, 0, 0, NULL, 'اَلْيَوْمَ نَخْتِمُ عَلٰٓى اَفْوَاهِهِمْ وَتُكَلِّمُنَٓا اَيْد۪يهِمْ وَتَشْهَدُ اَرْجُلُهُمْ بِمَا كَانُوا يَكْسِبُونَ', 0),
(3771, 36, NULL, 66, NULL, NULL, 0, 0, NULL, 'وَلَوْ نَشَٓاءُ لَطَمَسْنَا عَلٰٓى اَعْيُنِهِمْ فَاسْتَبَقُوا الصِّرَاطَ فَاَنّٰى يُبْصِرُونَ', 0),
(3772, 36, NULL, 67, NULL, NULL, 0, 0, NULL, 'وَلَوْ نَشَٓاءُ لَمَسَخْنَاهُمْ عَلٰى مَكَانَتِهِمْ فَمَا اسْتَطَاعُوا مُضِيًّا وَلَا يَرْجِعُونَ۟', 0),
(3773, 36, NULL, 68, NULL, NULL, 0, 0, NULL, 'وَمَنْ نُعَمِّرْهُ نُنَكِّسْهُ فِي الْخَلْقِۜ اَفَلَا يَعْقِلُونَ', 0),
(3774, 36, NULL, 69, NULL, NULL, 0, 0, NULL, 'وَمَا عَلَّمْنَاهُ الشِّعْرَ وَمَا يَنْبَغ۪ي لَهُۜ اِنْ هُوَ اِلَّا ذِكْرٌ وَقُرْاٰنٌ مُب۪ينٌۙ', 0),
(3775, 36, NULL, 70, NULL, NULL, 0, 0, NULL, 'لِيُنْذِرَ مَنْ كَانَ حَيًّا وَيَحِقَّ الْقَوْلُ عَلَى الْكَافِر۪ينَ', 0),
(3776, 36, NULL, 71, 444, 4, 1, 23, NULL, 'اَوَلَمْ يَرَوْا اَنَّا خَلَقْنَا لَهُمْ مِمَّا عَمِلَتْ اَيْد۪ينَٓا اَنْعَامًا فَهُمْ لَهَا مَالِكُونَ', 0),
(3777, 36, NULL, 72, NULL, NULL, 0, 0, NULL, 'وَذَلَّلْنَاهَا لَهُمْ فَمِنْهَا رَكُوبُهُمْ وَمِنْهَا يَأْكُلُونَ', 0),
(3778, 36, NULL, 73, NULL, NULL, 0, 0, NULL, 'وَلَهُمْ ف۪يهَا مَنَافِعُ وَمَشَارِبُۜ اَفَلَا يَشْكُرُونَ', 0),
(3779, 36, NULL, 74, NULL, NULL, 0, 0, NULL, 'وَاتَّخَذُوا مِنْ دُونِ اللّٰهِ اٰلِهَةً لَعَلَّهُمْ يُنْصَرُونَۜ', 0),
(3780, 36, NULL, 75, NULL, NULL, 0, 0, NULL, 'لَا يَسْتَط۪يعُونَ نَصْرَهُمْۙ وَهُمْ لَهُمْ جُنْدٌ مُحْضَرُونَ', 0),
(3781, 36, NULL, 76, NULL, NULL, 0, 0, NULL, 'فَلَا يَحْزُنْكَ قَوْلُهُمْۢ اِنَّا نَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ', 0),
(3782, 36, NULL, 77, NULL, NULL, 0, 0, NULL, 'اَوَلَمْ يَرَ الْاِنْسَانُ اَنَّا خَلَقْنَاهُ مِنْ نُطْفَةٍ فَاِذَا هُوَ خَص۪يمٌ مُب۪ينٌ', 0),
(3783, 36, NULL, 78, NULL, NULL, 0, 0, NULL, 'وَضَرَبَ لَنَا مَثَلًا وَنَسِيَ خَلْقَهُۜ قَالَ مَنْ يُحْيِ الْعِظَامَ وَهِيَ رَم۪يمٌ', 0),
(3784, 36, NULL, 79, NULL, NULL, 0, 0, NULL, 'قُلْ يُحْي۪يهَا الَّذ۪ٓي اَنْشَاَهَٓا اَوَّلَ مَرَّةٍۜ وَهُوَ بِكُلِّ خَلْقٍ عَل۪يمٌۙ', 0),
(3785, 36, NULL, 80, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ي جَعَلَ لَكُمْ مِنَ الشَّجَرِ الْاَخْضَرِ نَارًا فَاِذَٓا اَنْتُمْ مِنْهُ تُوقِدُونَ', 0),
(3786, 36, NULL, 81, NULL, NULL, 0, 0, NULL, 'اَوَلَيْسَ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ بِقَادِرٍ عَلٰٓى اَنْ يَخْلُقَ مِثْلَهُمْۜ بَلٰى وَهُوَ الْخَلَّاقُ الْعَل۪يمُ', 0),
(3787, 36, NULL, 82, NULL, NULL, 0, 0, NULL, 'اِنَّمَٓا اَمْرُهُٓ اِذَٓا اَرَادَ شَيْـًٔا اَنْ يَقُولَ لَهُ كُنْ فَيَكُونُ', 0),
(3788, 36, NULL, 83, NULL, NULL, 0, 0, NULL, 'فَسُبْحَانَ الَّذ۪ي بِيَدِه۪ مَلَكُوتُ كُلِّ شَيْءٍ وَاِلَيْهِ تُرْجَعُونَ', 0),
(3789, 37, 37, 1, 445, 5, 2, 23, NULL, 'وَالصَّٓافَّاتِ صَفًّاۙ', 0),
(3790, 37, NULL, 2, NULL, NULL, 0, 0, NULL, 'فَالزَّاجِرَاتِ زَجْرًاۙ', 0),
(3791, 37, NULL, 3, NULL, NULL, 0, 0, NULL, 'فَالتَّالِيَاتِ ذِكْرًاۙ', 0),
(3792, 37, NULL, 4, NULL, NULL, 0, 0, NULL, 'اِنَّ اِلٰهَكُمْ لَوَاحِدٌۜ', 0),
(3793, 37, NULL, 5, NULL, NULL, 0, 0, NULL, 'رَبُّ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا وَرَبُّ الْمَشَارِقِۜ', 0),
(3794, 37, NULL, 6, NULL, NULL, 0, 0, NULL, 'اِنَّا زَيَّنَّا السَّمَٓاءَ الدُّنْيَا بِز۪ينَةٍۨ الْكَوَاكِبِۙ', 0),
(3795, 37, NULL, 7, NULL, NULL, 0, 0, NULL, 'وَحِفْظًا مِنْ كُلِّ شَيْطَانٍ مَارِدٍۚ', 0),
(3796, 37, NULL, 8, NULL, NULL, 0, 0, NULL, 'لَا يَسَّمَّعُونَ اِلَى الْمَلَاِ الْاَعْلٰى وَيُقْذَفُونَ مِنْ كُلِّ جَانِبٍۗ', 0),
(3797, 37, NULL, 9, NULL, NULL, 0, 0, NULL, 'دُحُورًا وَلَهُمْ عَذَابٌ وَاصِبٌۙ', 0),
(3798, 37, NULL, 10, NULL, NULL, 0, 0, NULL, 'اِلَّا مَنْ خَطِفَ الْخَطْفَةَ فَاَتْبَعَهُ شِهَابٌ ثَاقِبٌ', 0),
(3799, 37, NULL, 11, NULL, NULL, 0, 0, NULL, 'فَاسْتَفْتِهِمْ اَهُمْ اَشَدُّ خَلْقًا اَمْ مَنْ خَلَقْنَاۜ اِنَّا خَلَقْنَاهُمْ مِنْ ط۪ينٍ لَازِبٍ', 0),
(3800, 37, NULL, 12, NULL, NULL, 0, 0, NULL, 'بَلْ عَجِبْتَ وَيَسْخَرُونَۖ', 0),
