(4718, 51, NULL, 43, NULL, NULL, 0, 0, NULL, 'وَف۪ي ثَمُودَ اِذْ ق۪يلَ لَهُمْ تَمَتَّعُوا حَتّٰى ح۪ينٍ', 0),
(4719, 51, NULL, 44, NULL, NULL, 0, 0, NULL, 'فَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ فَاَخَذَتْهُمُ الصَّاعِقَةُ وَهُمْ يَنْظُرُونَ', 0),
(4720, 51, NULL, 45, NULL, NULL, 0, 0, NULL, 'فَمَا اسْتَطَاعُوا مِنْ قِيَامٍ وَمَا كَانُوا مُنْتَصِر۪ينَۙ', 0),
(4721, 51, NULL, 46, NULL, NULL, 0, 0, NULL, 'وَقَوْمَ نُوحٍ مِنْ قَبْلُۜ اِنَّهُمْ كَانُوا قَوْمًا فَاسِق۪ينَ۟', 0),
(4722, 51, NULL, 47, NULL, NULL, 0, 0, NULL, 'وَالسَّمَٓاءَ بَنَيْنَاهَا بِاَيْدٍ وَاِنَّا لَمُوسِعُونَ', 0),
(4723, 51, NULL, 48, NULL, NULL, 0, 0, NULL, 'وَالْاَرْضَ فَرَشْنَاهَا فَنِعْمَ الْمَاهِدُونَ', 0),
(4724, 51, NULL, 49, NULL, NULL, 0, 0, NULL, 'وَمِنْ كُلِّ شَيْءٍ خَلَقْنَا زَوْجَيْنِ لَعَلَّكُمْ تَذَكَّرُونَ', 0),
(4725, 51, NULL, 50, NULL, NULL, 0, 0, NULL, 'فَفِرُّٓوا اِلَى اللّٰهِۜ اِنّ۪ي لَكُمْ مِنْهُ نَذ۪يرٌ مُب۪ينٌۚ', 0),
(4726, 51, NULL, 51, NULL, NULL, 0, 0, NULL, 'وَلَا تَجْعَلُوا مَعَ اللّٰهِ اِلٰهًا اٰخَرَۜ اِنّ۪ي لَكُمْ مِنْهُ نَذ۪يرٌ مُب۪ينٌ', 0),
(4727, 51, NULL, 52, 522, 2, 1, 27, NULL, 'كَذٰلِكَ مَٓا اَتَى الَّذ۪ينَ مِنْ قَبْلِهِمْ مِنْ رَسُولٍ اِلَّا قَالُوا سَاحِرٌ اَوْ مَجْنُونٌ', 0),
(4728, 51, NULL, 53, NULL, NULL, 0, 0, NULL, 'اَتَوَاصَوْا بِه۪ۚ بَلْ هُمْ قَوْمٌ طَاغُونَ', 0),
(4729, 51, NULL, 54, NULL, NULL, 0, 0, NULL, 'فَتَوَلَّ عَنْهُمْ فَمَٓا اَنْتَ بِمَلُومٍۘ', 0),
(4730, 51, NULL, 55, NULL, NULL, 0, 0, NULL, 'وَذَكِّرْ فَاِنَّ الذِّكْرٰى تَنْفَعُ الْمُؤْمِن۪ينَ', 0),
(4731, 51, NULL, 56, NULL, NULL, 0, 0, NULL, 'وَمَا خَلَقْتُ الْجِنَّ وَالْاِنْسَ اِلَّا لِيَعْبُدُونِ', 0),
(4732, 51, NULL, 57, NULL, NULL, 0, 0, NULL, 'مَٓا اُر۪يدُ مِنْهُمْ مِنْ رِزْقٍ وَمَٓا اُر۪يدُ اَنْ يُطْعِمُونِ', 0),
(4733, 51, NULL, 58, NULL, NULL, 0, 0, NULL, 'اِنَّ اللّٰهَ هُوَ الرَّزَّاقُ ذُو الْقُوَّةِ الْمَت۪ينُ', 0),
(4734, 51, NULL, 59, NULL, NULL, 0, 0, NULL, 'فَاِنَّ لِلَّذ۪ينَ ظَلَمُوا ذَنُوبًا مِثْلَ ذَنُوبِ اَصْحَابِهِمْ فَلَا يَسْتَعْجِلُونِ', 0),
(4735, 51, NULL, 60, NULL, NULL, 0, 0, NULL, 'فَوَيْلٌ لِلَّذ۪ينَ كَفَرُوا مِنْ يَوْمِهِمُ الَّذ۪ي يُوعَدُونَ', 0),
(4736, 52, 52, 1, NULL, NULL, 0, 0, NULL, 'وَالطُّورِۙ', 0),
(4737, 52, NULL, 2, NULL, NULL, 0, 0, NULL, 'وَكِتَابٍ مَسْطُورٍۙ', 0),
(4738, 52, NULL, 3, NULL, NULL, 0, 0, NULL, 'ف۪ي رَقٍّ مَنْشُورٍۙ', 0),
(4739, 52, NULL, 4, NULL, NULL, 0, 0, NULL, 'وَالْبَيْتِ الْمَعْمُورِۙ', 0),
(4740, 52, NULL, 5, NULL, NULL, 0, 0, NULL, 'وَالسَّقْفِ الْمَرْفُوعِۙ', 0),
(4741, 52, NULL, 6, NULL, NULL, 0, 0, NULL, 'وَالْبَحْرِ الْمَسْجُورِۙ', 0),
(4742, 52, NULL, 7, NULL, NULL, 0, 0, NULL, 'اِنَّ عَذَابَ رَبِّكَ لَوَاقِعٌۙ', 0),
(4743, 52, NULL, 8, NULL, NULL, 0, 0, NULL, 'مَا لَهُ مِنْ دَافِعٍۙ', 0),
(4744, 52, NULL, 9, NULL, NULL, 0, 0, NULL, 'يَوْمَ تَمُورُ السَّمَٓاءُ مَوْرًاۙ', 0),
(4745, 52, NULL, 10, NULL, NULL, 0, 0, NULL, 'وَتَس۪يرُ الْجِبَالُ سَيْرًاۜ', 0),
(4746, 52, NULL, 11, NULL, NULL, 0, 0, NULL, 'فَوَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَۙ', 0),
(4747, 52, NULL, 12, NULL, NULL, 0, 0, NULL, 'اَلَّذ۪ينَ هُمْ ف۪ي خَوْضٍ يَلْعَبُونَۢ', 0),
(4748, 52, NULL, 13, NULL, NULL, 0, 0, NULL, 'يَوْمَ يُدَعُّونَ اِلٰى نَارِ جَهَنَّمَ دَعًّاۜ', 0),
(4749, 52, NULL, 14, NULL, NULL, 0, 0, NULL, 'هٰذِهِ النَّارُ الَّت۪ي كُنْتُمْ بِهَا تُكَذِّبُونَ', 0),
(4750, 52, NULL, 15, 523, 3, 1, 27, NULL, 'اَفَسِحْرٌ هٰذَٓا اَمْ اَنْتُمْ لَا تُبْصِرُونَ', 0),
(4751, 52, NULL, 16, NULL, NULL, 0, 0, NULL, 'اِصْلَوْهَا فَاصْبِرُٓوا اَوْ لَا تَصْبِرُواۚ سَوَٓاءٌ عَلَيْكُمْۜ اِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُونَ', 0),
(4752, 52, NULL, 17, NULL, NULL, 0, 0, NULL, 'اِنَّ الْمُتَّق۪ينَ ف۪ي جَنَّاتٍ وَنَع۪يمٍۙ', 0),
(4753, 52, NULL, 18, NULL, NULL, 0, 0, NULL, 'فَاكِه۪ينَ بِمَٓا اٰتٰيهُمْ رَبُّهُمْۚ وَوَقٰيهُمْ رَبُّهُمْ عَذَابَ الْجَح۪يمِ', 0),
(4754, 52, NULL, 19, NULL, NULL, 0, 0, NULL, 'كُلُوا وَاشْرَبُوا هَن۪ٓيـًٔا بِمَا كُنْتُمْ تَعْمَلُونَۙ', 0),
(4755, 52, NULL, 20, NULL, NULL, 0, 0, NULL, 'مُتَّكِـ۪ٔينَ عَلٰى سُرُرٍ مَصْفُوفَةٍۚ وَزَوَّجْنَاهُمْ بِحُورٍ ع۪ينٍ', 0),
(4756, 52, NULL, 21, NULL, NULL, 0, 0, NULL, 'وَالَّذ۪ينَ اٰمَنُوا وَاتَّبَعَتْهُمْ ذُرِّيَّتُهُمْ بِا۪يمَانٍ اَلْحَقْنَا بِهِمْ ذُرِّيَّتَهُمْ وَمَٓا اَلَتْنَاهُمْ مِنْ عَمَلِهِمْ مِنْ شَيْءٍۜ كُلُّ امْرِئٍ بِمَا كَسَبَ رَه۪ينٌ', 0),
(4757, 52, NULL, 22, NULL, NULL, 0, 0, NULL, 'وَاَمْدَدْنَاهُمْ بِفَاكِهَةٍ وَلَحْمٍ مِمَّا يَشْتَهُونَ', 0),
(4758, 52, NULL, 23, NULL, NULL, 0, 0, NULL, 'يَتَنَازَعُونَ ف۪يهَا كَأْسًا لَا لَغْوٌ ف۪يهَا وَلَا تَأْث۪يمٌ', 0),
(4759, 52, NULL, 24, NULL, NULL, 0, 0, NULL, 'وَيَطُوفُ عَلَيْهِمْ غِلْمَانٌ لَهُمْ كَاَنَّهُمْ لُؤْلُؤٌ۬ مَكْنُونٌ', 0),
(4760, 52, NULL, 25, NULL, NULL, 0, 0, NULL, 'وَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَتَسَٓاءَلُونَ', 0),
(4761, 52, NULL, 26, NULL, NULL, 0, 0, NULL, 'قَالُٓوا اِنَّا كُنَّا قَبْلُ ف۪ٓي اَهْلِنَا مُشْفِق۪ينَ', 0),
(4762, 52, NULL, 27, NULL, NULL, 0, 0, NULL, 'فَمَنَّ اللّٰهُ عَلَيْنَا وَوَقٰينَا عَذَابَ السَّمُومِ', 0),
(4763, 52, NULL, 28, NULL, NULL, 0, 0, NULL, 'اِنَّا كُنَّا مِنْ قَبْلُ نَدْعُوهُۜ اِنَّهُ هُوَ الْبَرُّ الرَّح۪يمُ۟', 0),
(4764, 52, NULL, 29, NULL, NULL, 0, 0, NULL, 'فَذَكِّرْ فَمَٓا اَنْتَ بِنِعْمَتِ رَبِّكَ بِكَاهِنٍ وَلَا مَجْنُونٍۜ', 0),
(4765, 52, NULL, 30, NULL, NULL, 0, 0, NULL, 'اَمْ يَقُولُونَ شَاعِرٌ نَتَرَبَّصُ بِه۪ رَيْبَ الْمَنُونِ', 0),
(4766, 52, NULL, 31, NULL, NULL, 0, 0, NULL, 'قُلْ تَرَبَّصُوا فَاِنّ۪ي مَعَكُمْ مِنَ الْمُتَرَبِّص۪ينَۜ', 0),
(4767, 52, NULL, 32, 524, 4, 1, 27, NULL, 'اَمْ تَأْمُرُهُمْ اَحْلَامُهُمْ بِهٰذَٓا اَمْ هُمْ قَوْمٌ طَاغُونَۚ', 0),
(4768, 52, NULL, 33, NULL, NULL, 0, 0, NULL, 'اَمْ يَقُولُونَ تَقَوَّلَهُۚ بَلْ لَا يُؤْمِنُونَۚ', 0),
(4769, 52, NULL, 34, NULL, NULL, 0, 0, NULL, 'فَلْيَأْتُوا بِحَد۪يثٍ مِثْلِه۪ٓ اِنْ كَانُوا صَادِق۪ينَۜ', 0),
(4770, 52, NULL, 35, NULL, NULL, 0, 0, NULL, 'اَمْ خُلِقُوا مِنْ غَيْرِ شَيْءٍ اَمْ هُمُ الْخَالِقُونَۜ', 0),
(4771, 52, NULL, 36, NULL, NULL, 0, 0, NULL, 'اَمْ خَلَقُوا السَّمٰوَاتِ وَالْاَرْضَۚ بَلْ لَا يُوقِنُونَۜ', 0),
(4772, 52, NULL, 37, NULL, NULL, 0, 0, NULL, 'اَمْ عِنْدَهُمْ خَزَٓائِنُ رَبِّكَ اَمْ هُمُ الْمُصَيْطِرُونَۜ', 0);
INSERT INTO `fkl_verse` (`id`, `sura_id`, `new_sura`, `verse_no`, `page`, `hizb_page`, `hizb`, `juz`, `new_juz`, `verse`, `sajdah`) VALUES
(4773, 52, NULL, 38, NULL, NULL, 0, 0, NULL, 'اَمْ لَهُمْ سُلَّمٌ يَسْتَمِعُونَ ف۪يهِۚ فَلْيَأْتِ مُسْتَمِعُهُمْ بِسُلْطَانٍ مُب۪ينٍۜ', 0),
(4774, 52, NULL, 39, NULL, NULL, 0, 0, NULL, 'اَمْ لَهُ الْبَنَاتُ وَلَكُمُ الْبَنُونَۜ', 0),
(4775, 52, NULL, 40, NULL, NULL, 0, 0, NULL, 'اَمْ تَسْـَٔلُهُمْ اَجْرًا فَهُمْ مِنْ مَغْرَمٍ مُثْقَلُونَۜ', 0),
(4776, 52, NULL, 41, NULL, NULL, 0, 0, NULL, 'اَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَۜ', 0),
(4777, 52, NULL, 42, NULL, NULL, 0, 0, NULL, 'اَمْ يُر۪يدُونَ كَيْدًاۜ فَالَّذ۪ينَ كَفَرُوا هُمُ الْمَك۪يدُونَۜ', 0),
(4778, 52, NULL, 43, NULL, NULL, 0, 0, NULL, 'اَمْ لَهُمْ اِلٰهٌ غَيْرُ اللّٰهِۜ سُبْحَانَ اللّٰهِ عَمَّا يُشْرِكُونَ', 0),
(4779, 52, NULL, 44, NULL, NULL, 0, 0, NULL, 'وَاِنْ يَرَوْا كِسْفًا مِنَ السَّمَٓاءِ سَاقِطًا يَقُولُوا سَحَابٌ مَرْكُومٌ', 0),
(4780, 52, NULL, 45, NULL, NULL, 0, 0, NULL, 'فَذَرْهُمْ حَتّٰى يُلَاقُوا يَوْمَهُمُ الَّذ۪ي ف۪يهِ يُصْعَقُونَۙ', 0),
(4781, 52, NULL, 46, NULL, NULL, 0, 0, NULL, 'يَوْمَ لَا يُغْن۪ي عَنْهُمْ كَيْدُهُمْ شَيْـًٔا وَلَا هُمْ يُنْصَرُونَۜ', 0),
(4782, 52, NULL, 47, NULL, NULL, 0, 0, NULL, 'وَاِنَّ لِلَّذ۪ينَ ظَلَمُوا عَذَابًا دُونَ ذٰلِكَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ', 0),
(4783, 52, NULL, 48, NULL, NULL, 0, 0, NULL, 'وَاصْبِرْ لِحُكْمِ رَبِّكَ فَاِنَّكَ بِاَعْيُنِنَا وَسَبِّحْ بِحَمْدِ رَبِّكَ ح۪ينَ تَقُومُۙ', 0),
(4784, 52, NULL, 49, NULL, NULL, 0, 0, NULL, 'وَمِنَ الَّيْلِ فَسَبِّحْهُ وَاِدْبَارَ النُّجُومِ', 0),
(4785, 53, 53, 1, 525, 5, 1, 27, NULL, 'وَالنَّجْمِ اِذَا هَوٰىۙ', 0),
(4786, 53, NULL, 2, NULL, NULL, 0, 0, NULL, 'مَا ضَلَّ صَاحِبُكُمْ وَمَا غَوٰىۚ', 0),
(4787, 53, NULL, 3, NULL, NULL, 0, 0, NULL, 'وَمَا يَنْطِقُ عَنِ الْهَوٰىۜ', 0),
(4788, 53, NULL, 4, NULL, NULL, 0, 0, NULL, 'اِنْ هُوَ اِلَّا وَحْيٌ يُوحٰىۙ', 0),
(4789, 53, NULL, 5, NULL, NULL, 0, 0, NULL, 'عَلَّمَهُ شَد۪يدُ الْقُوٰىۙ', 0),
(4790, 53, NULL, 6, NULL, NULL, 0, 0, NULL, 'ذُو مِرَّةٍۜ فَاسْتَوٰىۙ', 0),
(4791, 53, NULL, 7, NULL, NULL, 0, 0, NULL, 'وَهُوَ بِالْاُفُقِ الْاَعْلٰىۜ', 0),
(4792, 53, NULL, 8, NULL, NULL, 0, 0, NULL, 'ثُمَّ دَنَا فَتَدَلّٰىۙ', 0),
(4793, 53, NULL, 9, NULL, NULL, 0, 0, NULL, 'فَكَانَ قَابَ قَوْسَيْنِ اَوْ اَدْنٰىۚ', 0),
(4794, 53, NULL, 10, NULL, NULL, 0, 0, NULL, 'فَاَوْحٰٓى اِلٰى عَبْدِه۪ مَٓا اَوْحٰىۜ', 0),
(4795, 53, NULL, 11, NULL, NULL, 0, 0, NULL, 'مَا كَذَبَ الْفُؤٰادُ مَا رَاٰى', 0),
(4796, 53, NULL, 12, NULL, NULL, 0, 0, NULL, 'اَفَتُمَارُونَهُ عَلٰى مَا يَرٰى', 0),
(4797, 53, NULL, 13, NULL, NULL, 0, 0, NULL, 'وَلَقَدْ رَاٰهُ نَزْلَةً اُخْرٰىۙ', 0),
(4798, 53, NULL, 14, NULL, NULL, 0, 0, NULL, 'عِنْدَ سِدْرَةِ الْمُنْتَهٰى', 0),
(4799, 53, NULL, 15, NULL, NULL, 0, 0, NULL, 'عِنْدَهَا جَنَّةُ الْمَأْوٰىۜ', 0),
(4800, 53, NULL, 16, NULL, NULL, 0, 0, NULL, 'اِذْ يَغْشَى السِّدْرَةَ مَا يَغْشٰىۙ', 0),
(4801, 53, NULL, 17, NULL, NULL, 0, 0, NULL, 'مَا زَاغَ الْبَصَرُ وَمَا طَغٰى', 0),
(4802, 53, NULL, 18, NULL, NULL, 0, 0, NULL, 'لَقَدْ رَاٰى مِنْ اٰيَاتِ رَبِّهِ الْكُبْرٰى', 0),
(4803, 53, NULL, 19, NULL, NULL, 0, 0, NULL, 'اَفَرَاَيْتُمُ اللَّاتَ وَالْعُزّٰىۙ', 0),
(4804, 53, NULL, 20, NULL, NULL, 0, 0, NULL, 'وَمَنٰوةَ الثَّالِثَةَ الْاُخْرٰى', 0),
(4805, 53, NULL, 21, NULL, NULL, 0, 0, NULL, 'اَلَكُمُ الذَّكَرُ وَلَهُ الْاُنْثٰى', 0),
(4806, 53, NULL, 22, NULL, NULL, 0, 0, NULL, 'تِلْكَ اِذًا قِسْمَةٌ ض۪يزٰى', 0),
(4807, 53, NULL, 23, NULL, NULL, 0, 0, NULL, 'اِنْ هِيَ اِلَّٓا اَسْمَٓاءٌ سَمَّيْتُمُوهَٓا اَنْتُمْ وَاٰبَٓاؤُ۬كُمْ مَٓا اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطَانٍۜ اِنْ يَتَّبِعُونَ اِلَّا الظَّنَّ وَمَا تَهْوَى الْاَنْفُسُۚ وَلَقَدْ جَٓاءَهُمْ مِنْ رَبِّهِمُ الْهُدٰىۜ', 0),
