(5552, 75, 75, 1, NULL, NULL, 0, 0, NULL, 'لَٓا اُقْسِمُ بِيَوْمِ الْقِيٰمَةِۙ', 0),
(5553, 75, NULL, 2, NULL, NULL, 0, 0, NULL, 'وَلَٓا اُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ', 0),
(5554, 75, NULL, 3, NULL, NULL, 0, 0, NULL, 'اَيَحْسَبُ الْاِنْسَانُ اَلَّنْ نَجْمَعَ عِظَامَهُۜ', 0),
(5555, 75, NULL, 4, NULL, NULL, 0, 0, NULL, 'بَلٰى قَادِر۪ينَ عَلٰٓى اَنْ نُسَوِّيَ بَنَانَهُ', 0),
