(5560, 75, NULL, 9, NULL, NULL, 0, 0, NULL, 'وَجُمِعَ الشَّمْسُ وَالْقَمَرُۙ', 0),
(5561, 75, NULL, 10, NULL, NULL, 0, 0, NULL, 'يَقُولُ الْاِنْسَانُ يَوْمَئِذٍ اَيْنَ الْمَفَرُّۚ', 0),
(5562, 75, NULL, 11, NULL, NULL, 0, 0, NULL, 'كَلَّا لَا وَزَرَۚ', 0),
(5563, 75, NULL, 12, NULL, NULL, 0, 0, NULL, 'اِلٰى رَبِّكَ يَوْمَئِذٍۨ الْمُسْتَقَرُّۜ', 0),
(5564, 75, NULL, 13, NULL, NULL, 0, 0, NULL, 'يُنَبَّؤُا الْاِنْسَانُ يَوْمَئِذٍ بِمَا قَدَّمَ وَاَخَّرَۜ', 0),
(5565, 75, NULL, 14, NULL, NULL, 0, 0, NULL, 'بَلِ الْاِنْسَانُ عَلٰى نَفْسِه۪ بَص۪يرَةٌۙ', 0),
(5566, 75, NULL, 15, NULL, NULL, 0, 0, NULL, 'وَلَوْ اَلْقٰى مَعَاذ۪يرَهُۜ', 0),
(5567, 75, NULL, 16, NULL, NULL, 0, 0, NULL, 'لَا تُحَرِّكْ بِه۪ لِسَانَكَ لِتَعْجَلَ بِه۪ۜ', 0),
(5568, 75, NULL, 17, NULL, NULL, 0, 0, NULL, 'اِنَّ عَلَيْنَا جَمْعَهُ وَقُرْاٰنَهُۚ', 0),
(5569, 75, NULL, 18, NULL, NULL, 0, 0, NULL, 'فَاِذَا قَرَأْنَاهُ فَاتَّبِعْ قُرْاٰنَهُۚ', 0),
