(5571, 75, NULL, 20, 577, 17, 4, 29, NULL, 'كَلَّا بَلْ تُحِبُّونَ الْعَاجِلَةَۙ', 0),
(5572, 75, NULL, 21, NULL, NULL, 0, 0, NULL, 'وَتَذَرُونَ الْاٰخِرَةَۜ', 0),
