(5577, 75, NULL, 26, NULL, NULL, 0, 0, NULL, 'كَلَّٓا اِذَا بَلَغَتِ التَّرَاقِيَۙ', 0),
(5578, 75, NULL, 27, NULL, NULL, 0, 0, NULL, 'وَق۪يلَ مَنْ۔ رَاقٍۙ', 0),
