(5587, 75, NULL, 36, NULL, NULL, 0, 0, NULL, 'اَيَحْسَبُ الْاِنْسَانُ اَنْ يُتْرَكَ سُدًىۜ', 0),
(5588, 75, NULL, 37, NULL, NULL, 0, 0, NULL, 'اَلَمْ يَكُ نُطْفَةً مِنْ مَنِيٍّ يُمْنٰىۙ', 0),
(5589, 75, NULL, 38, NULL, NULL, 0, 0, NULL, 'ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوّٰىۙ', 0),
(5590, 75, NULL, 39, NULL, NULL, 0, 0, NULL, 'فَجَعَلَ مِنْهُ الزَّوْجَيْنِ الذَّكَرَ وَالْاُنْثٰىۜ', 0),
(5591, 75, NULL, 40, NULL, NULL, 0, 0, NULL, 'اَلَيْسَ ذٰلِكَ بِقَادِرٍ عَلٰٓى اَنْ يُحْيِيَ الْمَوْتٰى', 0),
(5592, 76, 76, 1, NULL, NULL, 0, 0, NULL, 'هَلْ اَتٰى عَلَى الْاِنْسَانِ ح۪ينٌ مِنَ الدَّهْرِ لَمْ يَكُنْ شَيْـًٔا مَذْكُورًا', 0),
(5593, 76, NULL, 2, NULL, NULL, 0, 0, NULL, 'اِنَّا خَلَقْنَا الْاِنْسَانَ مِنْ نُطْفَةٍ اَمْشَاجٍۗ نَبْتَل۪يهِ فَجَعَلْنَاهُ سَم۪يعًا بَص۪يرًا', 0),
(5594, 76, NULL, 3, NULL, NULL, 0, 0, NULL, 'اِنَّا هَدَيْنَاهُ السَّب۪يلَ اِمَّا شَاكِرًا وَاِمَّا كَفُورًا', 0),
(5595, 76, NULL, 4, NULL, NULL, 0, 0, NULL, 'اِنَّٓا اَعْتَدْنَا لِلْكَافِر۪ينَ سَلَاسِلَا۬ وَاَغْلَالًا وَسَع۪يرًا', 0),
(5596, 76, NULL, 5, NULL, NULL, 0, 0, NULL, 'اِنَّ الْاَبْرَارَ يَشْرَبُونَ مِنْ كَأْسٍ كَانَ مِزَاجُهَا كَافُورًاۚ', 0),
(5597, 76, NULL, 6, 578, 18, 4, 29, NULL, 'عَيْنًا يَشْرَبُ بِهَا عِبَادُ اللّٰهِ يُفَجِّرُونَهَا تَفْج۪يرًا', 0),
(5598, 76, NULL, 7, NULL, NULL, 0, 0, NULL, 'يُوفُونَ بِالنَّذْرِ وَيَخَافُونَ يَوْمًا كَانَ شَرُّهُ مُسْتَط۪يرًا', 0),
(5599, 76, NULL, 8, NULL, NULL, 0, 0, NULL, 'وَيُطْعِمُونَ الطَّعَامَ عَلٰى حُبِّه۪ مِسْك۪ينًا وَيَت۪يمًا وَاَس۪يرًا', 0),
(5600, 76, NULL, 9, NULL, NULL, 0, 0, NULL, 'اِنَّمَا نُطْعِمُكُمْ لِوَجْهِ اللّٰهِ لَا نُر۪يدُ مِنْكُمْ جَزَٓاءً وَلَا شُكُورًا', 0),
(5601, 76, NULL, 10, NULL, NULL, 0, 0, NULL, 'اِنَّا نَخَافُ مِنْ رَبِّنَا يَوْمًا عَبُوسًا قَمْطَر۪يرًا', 0),
(5602, 76, NULL, 11, NULL, NULL, 0, 0, NULL, 'فَوَقٰيهُمُ اللّٰهُ شَرَّ ذٰلِكَ الْيَوْمِ وَلَقّٰيهُمْ نَضْرَةً وَسُرُورًاۚ', 0),
(5603, 76, NULL, 12, NULL, NULL, 0, 0, NULL, 'وَجَزٰيهُمْ بِمَا صَبَرُوا جَنَّةً وَحَر۪يرًاۙ', 0),
(5604, 76, NULL, 13, NULL, NULL, 0, 0, NULL, 'مُتَّكِـ۪ٔينَ ف۪يهَا عَلَى الْاَرَٓائِكِۚ لَا يَرَوْنَ ف۪يهَا شَمْسًا وَلَا زَمْهَر۪يرًاۚ', 0),
(5605, 76, NULL, 14, NULL, NULL, 0, 0, NULL, 'وَدَانِيَةً عَلَيْهِمْ ظِلَالُهَا وَذُلِّلَتْ قُطُوفُهَا تَذْل۪يلًا', 0),
(5606, 76, NULL, 15, NULL, NULL, 0, 0, NULL, 'وَيُطَافُ عَلَيْهِمْ بِاٰنِيَةٍ مِنْ فِضَّةٍ وَاَكْوَابٍ كَانَتْ قَوَار۪يرَاۙ', 0),
(5607, 76, NULL, 16, NULL, NULL, 0, 0, NULL, 'قَوَار۪يرَ مِنْ فِضَّةٍ قَدَّرُوهَا تَقْد۪يرًا', 0),
(5608, 76, NULL, 17, NULL, NULL, 0, 0, NULL, 'وَيُسْقَوْنَ ف۪يهَا كَأْسًا كَانَ مِزَاجُهَا زَنْجَب۪يلًاۚ', 0),
(5609, 76, NULL, 18, NULL, NULL, 0, 0, NULL, 'عَيْنًا ف۪يهَا تُسَمّٰى سَلْسَب۪يلًا', 0),
(5610, 76, NULL, 19, NULL, NULL, 0, 0, NULL, 'وَيَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُخَلَّدُونَۚ اِذَا رَاَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤً۬ا مَنْثُورًا', 0),
(5611, 76, NULL, 20, NULL, NULL, 0, 0, NULL, 'وَاِذَا رَاَيْتَ ثَمَّ رَاَيْتَ نَع۪يمًا وَمُلْكًا كَب۪يرًا', 0),
(5612, 76, NULL, 21, NULL, NULL, 0, 0, NULL, 'عَالِيَهُمْ ثِيَابُ سُنْدُسٍ خُضْرٌ وَاِسْتَبْرَقٌۘ وَحُلُّٓوا اَسَاوِرَ مِنْ فِضَّةٍۚ وَسَقٰيهُمْ رَبُّهُمْ شَرَابًا طَهُورًا', 0),
(5613, 76, NULL, 22, NULL, NULL, 0, 0, NULL, 'اِنَّ هٰذَا كَانَ لَكُمْ جَزَٓاءً وَكَانَ سَعْيُكُمْ مَشْكُورًا۟', 0),
(5614, 76, NULL, 23, NULL, NULL, 0, 0, NULL, 'اِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ الْقُرْاٰنَ تَنْز۪يلًاۚ', 0),
(5615, 76, NULL, 24, NULL, NULL, 0, 0, NULL, 'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ اٰثِمًا اَوْ كَفُورًاۚ', 0),
(5616, 76, NULL, 25, NULL, NULL, 0, 0, NULL, 'وَاذْكُرِ اسْمَ رَبِّكَ بُكْرَةً وَاَص۪يلًاۚ', 0),
(5617, 76, NULL, 26, 579, 19, 4, 29, NULL, 'وَمِنَ الَّيْلِ فَاسْجُدْ لَهُ وَسَبِّحْهُ لَيْلًا طَو۪يلًا', 0),
